SET NAMES utf8mb4;
SET FOREIGN_KEY_CHECKS = 0;

-- ----------------------------
-- Table structure for baiviet
-- ----------------------------
DROP TABLE IF EXISTS `baiviet`;
CREATE TABLE `baiviet`  (
  `ProID` int(11) NOT NULL AUTO_INCREMENT,
  `AnhDaiDien` text CHARACTER SET latin1 COLLATE latin1_swedish_ci NULL,
  `TieuDe` varchar(500) CHARACTER SET utf8 COLLATE utf8_vietnamese_ci NULL DEFAULT NULL,
  `ChuyenMucChaID` int(11) NULL DEFAULT NULL,
  `ChuyenMucConID` int(11) NULL DEFAULT NULL,
  `Youtube` text CHARACTER SET latin1 COLLATE latin1_swedish_ci NULL,
  `Nhan` varchar(50) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL,
  `NgayDang` date NULL DEFAULT NULL,
  `NoiDung` varchar(20000) CHARACTER SET utf8 COLLATE utf8_vietnamese_ci NULL DEFAULT NULL,
  `NDTomTat` varchar(1000) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL,
  `TinhTrang` tinyint(1) NULL DEFAULT NULL,
  `LuotXem` int(255) NULL DEFAULT NULL,
  `TagID` int(11) NULL DEFAULT NULL,
  PRIMARY KEY (`ProID`) USING BTREE,
  INDEX `ChuyenMucChaID`(`ChuyenMucChaID`, `ChuyenMucConID`) USING BTREE,
  INDEX `ChuyenMucConID`(`ChuyenMucConID`) USING BTREE,
  CONSTRAINT `baiviet_ibfk_1` FOREIGN KEY (`ChuyenMucChaID`) REFERENCES `chuyenmuccha` (`ID`) ON DELETE RESTRICT ON UPDATE RESTRICT,
  CONSTRAINT `baiviet_ibfk_2` FOREIGN KEY (`ChuyenMucConID`) REFERENCES `chuyenmuccon` (`ID`) ON DELETE RESTRICT ON UPDATE RESTRICT
) ENGINE = InnoDB AUTO_INCREMENT = 89 CHARACTER SET = latin1 COLLATE = latin1_swedish_ci ROW_FORMAT = Compact;

