-- Records of baiviet
-- ----------------------------
INSERT INTO `baiviet` VALUES (1, '/public/images/62542390_1299146586916333_2721497308326789120_n.jpg', 'Taylor Swift chính thức trở lại với album mang tên \'Lover\'', 5, 10, NULL, 'Taylor Swift', '2019-06-14', '<p><a href=\"http://thethaovanhoa.vn/tags/Taylor+Swift\" target=\"_blank\">Taylor Swift</a>&nbsp;bất ngờ th&ocirc;ng b&aacute;o album ph&ograve;ng thu thứ 7 của m&igrave;nh mang t&ecirc;n&nbsp;<em>Lover</em>&nbsp;sẽ được ch&iacute;nh thức ph&aacute;t h&agrave;nh v&agrave;o ng&agrave;y 23 th&aacute;ng 8 tới đ&acirc;y. Album bao gồm 18 ca kh&uacute;c v&agrave; c&oacute; tới 4 phi&ecirc;n bản kh&aacute;c nhau.</p>\r\n\r\n<p><img alt=\"Chú thích ảnh\" src=\"https://cdnmedia.thethaovanhoa.vn/Upload/H4YRO1ZUXG5fDnj99Lhg/files/2019/03/62542390_1299146586916333_2721497308326789120_n.jpg\" /></p>\r\n\r\n<p><em>Ảnh b&igrave;a của album được Taylor chia sẻ</em></p>\r\n\r\n<p>C&ocirc; chia sẻ: &ldquo;C&oacute; rất nhiều điều m&agrave; t&ocirc;i đ&atilde; h&agrave;o hứng bấy l&acirc;u nay v&agrave; t&ocirc;i chỉ muốn chờ đợi một thời điểm th&iacute;ch hợp để chia sẻ với tất cả c&aacute;c bạn!&rdquo;</p>\r\n\r\n<p>&quot;Album n&agrave;y, về giai điệu, n&oacute; rất l&atilde;ng mạn v&agrave; kh&ocirc;ng đơn giản theo một chủ đề n&agrave;o cả, n&oacute; cũng kh&ocirc;ng ho&agrave;n to&agrave;n chỉ c&oacute; những b&agrave;i h&aacute;t về t&igrave;nh y&ecirc;u đ&acirc;u&quot;,&nbsp;<a href=\"http://thethaovanhoa.vn/tags/Taylor+Swift\" target=\"_blank\">Taylor</a>&nbsp;n&oacute;i th&ecirc;m, &quot;T&ocirc;i c&oacute; &yacute; tưởng về một điều g&igrave; đ&oacute; thật l&atilde;ng mạn, nhưng kh&ocirc;ng chỉ l&agrave; những điều vui vẻ. T&ocirc;i nghĩ bạn c&oacute; thể t&igrave;m thấy sự l&atilde;ng mạn trong c&ocirc; đơn, trong nỗi buồn hoặc kể cả khi trải qua một cuộc xung đột, giải quyết mọi thứ trong cuộc sống của bạn. T&ocirc;i chỉ l&agrave; nh&igrave;n v&agrave;o những điều đ&oacute; với một c&aacute;i nh&igrave;n rất l&atilde;ng mạn.&quot;</p>\r\n\r\n<p><img alt=\"Chú thích ảnh\" src=\"https://cdnmedia.thethaovanhoa.vn/Upload/H4YRO1ZUXG5fDnj99Lhg/files/2019/03/maxresdefault.jpg\" /></p>\r\n\r\n<p>Th&ocirc;ng b&aacute;o quan trọng n&agrave;y được c&ocirc; c&ocirc;ng bố khoảng một th&aacute;ng rưỡi sau khi Taylor ph&aacute;t h&agrave;nh ca kh&uacute;c&nbsp;<em>ME!&nbsp;</em>kết hợp với nam ca sĩ Brendon Urie. Đ&acirc;y cũng l&agrave; ca kh&uacute;c đầu ti&ecirc;n được tiết lộ nằm trong album ph&ograve;ng thu thứ 7 của c&ocirc; n&agrave;ng.</p>\r\n\r\n<p><em>Lover&nbsp;</em>cũng&nbsp;đ&aacute;nh dấu sự trở lại lần đầu ti&ecirc;n của Taylor&nbsp;Swift kể từ album&nbsp;<em>Reputation</em>&nbsp;ra mắt năm 2017.</p>\r\n\r\n<p><strong>Phan Ngọc Quỳnh Trang</strong><br />\r\nTheo&nbsp;<em>CNN</em></p>\r\n', ' Album mới này của nàng \"rắn chúa\" hứa hẹn sẽ công phá làng nhạc thế giới vào tháng 8 năm nay !', NULL, NULL, 1);
INSERT INTO `baiviet` VALUES (2, '/public/images/bts-15605671846342139323893.jpg', 'Có ai ngờ đã đến ngày BTS phá cả kỷ lục đi tour của Taylor Swift và ban nhạc huyền thoại U2 với con số siêu \"khủng\"!', 5, 11, NULL, 'BTS', '2019-06-16', '<p>Với tour diễn v&ograve;ng quanh thế giới &quot;Love Yourself: Speak Yourself&quot;, BTS đ&atilde; thiết lập v&ocirc; số kỷ lục &quot;v&ocirc; tiền kho&aacute;ng hậu&quot;. Thế nhưng, những số liệu mới nhất lại tiếp tục thể hiện đẳng cấp của 7 ch&agrave;ng trai nh&agrave; Big Hit ở một tầm cao kh&aacute;c khi nh&oacute;m đ&atilde; vượt qua cả những &quot;c&acirc;y cổ thụ&quot; của &quot;l&agrave;ng đi tour&quot; như Taylor Swift, U2,..</p>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/6/15/uuefs-1560595751251172850565.png\" target=\"_blank\"><img alt=\"Có ai ngờ đã đến ngày BTS phá cả kỷ lục đi tour của Taylor Swift và ban nhạc huyền thoại U2 với con số siêu khủng! - Ảnh 1.\" src=\"https://kenh14cdn.com/2019/6/15/uuefs-1560595751251172850565.png\" /></a></p>\r\n\r\n<p>BTS</p>\r\n\r\n<p>Theo đ&oacute;, BTS ph&aacute; vỡ kỷ lục doanh thu ph&ograve;ng v&eacute; cao nhất cho hai đ&ecirc;m concert tại s&acirc;n vận động Rose Bowl của Mỹ với&nbsp;<strong>113.040 v&eacute; b&aacute;n ra</strong>, qua đ&oacute; thu về tổng cộng hơn&nbsp;<strong>16.600.000 đ&ocirc;</strong>&nbsp;la Mỹ (tương đương&nbsp;<strong>hơn 383,3 tỷ VNĐ</strong>) cho tour diễn n&agrave;y chỉ t&iacute;nh ri&ecirc;ng hai đ&ecirc;m tại SVĐ lớn nhất nh&igrave; nước Mỹ.</p>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/6/15/bts-15605671846342139323893.jpg\" target=\"_blank\"><img alt=\"Có ai ngờ đã đến ngày BTS phá cả kỷ lục đi tour của Taylor Swift và ban nhạc huyền thoại U2 với con số siêu khủng! - Ảnh 2.\" src=\"https://kenh14cdn.com/2019/6/15/bts-15605671846342139323893.jpg\" /></a></p>\r\n\r\n<p>BTS c&ugrave;ng Taylor Swift</p>\r\n\r\n<p>Với th&agrave;nh t&iacute;ch n&agrave;y, BTS đ&atilde; xuất sắc vượt qua Taylor Swift (<strong>16.250.000 đ&ocirc; la Mỹ</strong>) v&agrave; ban nhạc huyền thoại U2 (<strong>15.780.000 đ&ocirc; la Mỹ</strong>) để trở th&agrave;nh nghệ sĩ kiếm nhiều tiền nhất từ tổ chức show diễn tại SVĐ Rose Bowl, California.</p>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/6/11/taylor-swift-me-1560187052476466040838.jpg\" target=\"_blank\"><img alt=\"Có ai ngờ đã đến ngày BTS phá cả kỷ lục đi tour của Taylor Swift và ban nhạc huyền thoại U2 với con số siêu khủng! - Ảnh 3.\" src=\"https://kenh14cdn.com/2019/6/11/taylor-swift-me-1560187052476466040838.jpg\" /></a></p>\r\n\r\n<p>Taylor Swift</p>\r\n\r\n<p>Hiện tour diễn vẫn c&ograve;n đang tiếp diễn nhưng BTS đ&atilde; thu về đến tổng cộng&nbsp;<strong>hơn 1840 ngh&igrave;n tỷ đồng cho loạt show diễn chỉ t&iacute;nh ri&ecirc;ng tại Mỹ v&agrave; ch&acirc;u &Acirc;u</strong>! Cụ thể, BTS thu về tổng cộng khoảng 44 triệu đ&ocirc; (hơn 1 ngh&igrave;n tỷ VNĐ) chỉ ri&ecirc;ng cho 6 đ&ecirc;m diễn tại Bắc Mỹ với 299.770 v&eacute; được b&aacute;n ra, cộng th&ecirc;m 35 triệu đ&ocirc; (~816,7 tỷ VNĐ) từ 6 đ&ecirc;m diễn tại Brazil, Anh Quốc v&agrave; Paris. Tổng doanh thu l&agrave; 78,9 triệu đ&ocirc; (khoảng 1,84 ngh&igrave;n tỷ VNĐ) với 606.409 v&eacute; được b&aacute;n ra!</p>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/6/10/bts-24-156016251572819266736.jpg\" target=\"_blank\"><img alt=\"Có ai ngờ đã đến ngày BTS phá cả kỷ lục đi tour của Taylor Swift và ban nhạc huyền thoại U2 với con số siêu khủng! - Ảnh 4.\" src=\"https://kenh14cdn.com/2019/6/10/bts-24-156016251572819266736.jpg\" /></a></p>\r\n\r\n<p>Kh&ocirc;ng hổ danh nh&oacute;m nhạc to&agrave;n cầu BTS!</p>\r\n', 'BTS tiếp tục khẳng định tầm ảnh hưởng của mình với tour diễn hoành tráng vượt qua cả Taylor Swift và U2!', NULL, NULL, NULL);
INSERT INTO `baiviet` VALUES (69, '/public/images/1560612680-498-co-thien-lac-chinh-thuc-hen-ho-voi-thi-hau-vuong-phu-sau-20-nam-lam-ban-than-tuy--n-huy--n-3-1560571132-width600height398.jpg', 'Thân thế bất ngờ của bạn gái \'Dương Quá giàu nhất Hong Kong\'', 4, 8, NULL, 'Dương quá', '2019-06-16', '<p>Mới đ&acirc;y nhất, cả giới giải tr&iacute; x&ocirc;n xao b&agrave;n t&aacute;n về việc Cổ Thi&ecirc;n Lạc v&agrave; Tuy&ecirc;n Huy&ecirc;n ch&iacute;nh thức hẹn h&ograve; với nhau. Trước đ&oacute;, cả hai vốn l&agrave; cặp đ&ocirc;i &ldquo;bạn th&acirc;n ế trường kỳ&rdquo; của showbiz Hong Kong suốt 20 năm. Cổ Thi&ecirc;n Lạc được xưng l&agrave; &ldquo;&ocirc;ng tr&ugrave;m điện ảnh thế hệ mới&rdquo; c&ograve;n Tuy&ecirc;n Huy&ecirc;n lại l&agrave; &ldquo;Thị hậu vượng phu&rdquo; đ&igrave;nh đ&aacute;m của thế giới giải tr&iacute; Hoa Ngữ suốt nhiều năm liền.</p>\r\n\r\n<p>Cổ Thi&ecirc;n Lạc l&agrave; một trong những nam thần đời đầu của l&agrave;ng giải tr&iacute; Hong Kong thập ni&ecirc;n 90. Lu&ocirc;n miệt m&agrave;i với nghiệp diễn xuất, c&aacute;i t&ecirc;n Cổ Thi&ecirc;n Lạc nổi tiếng nhanh tới mức kh&oacute; ai ngờ tới. Năm 2015, anh tham gia 14 bộ phim, l&agrave;m đại diện cho 13 thương hiệu v&agrave; thu về 50 triệu USD (hơn 1.000 tỷ đồng).</p>\r\n\r\n<p>Kh&ocirc;ng những thế, anh c&ograve;n rất m&aacute;t tay trong việc kinh doanh bất động sản. Những c&aacute;i t&ecirc;n lớn như&nbsp;<em>CG Special</em>&nbsp;hay&nbsp;<em>Fat Face Production Limited</em>&nbsp;đều c&oacute; lượng cổ phần lớn của anh trong đ&oacute;. Hiện tại, tổng t&agrave;i sản của ch&agrave;ng &quot;Dương Qu&aacute;&quot; hiện đ&atilde; c&aacute;n mốc hơn 200 triệu USD (xấp xỉ 4.700 tỷ đồng). Đ&acirc;y ch&iacute;nh l&agrave; th&agrave;nh quả xứng đ&aacute;ng cho những năm th&aacute;ng l&agrave;m việc kh&ocirc;ng mệt nghỉ của Cổ Thi&ecirc;n Lạc.</p>\r\n\r\n<p><img alt=\"Thân thế bất ngờ của bạn gái &amp;#39;Dương Quá giàu nhất Hong Kong&amp;#39; - 1\" src=\"https://cdn.24h.com.vn/upload/2-2019/images/2019-06-15/1560612680-576-co-thien-lac-chinh-thuc-hen-ho-voi-thi-hau-vuong-phu-sau-20-nam-lam-ban-than-c----thi--n-l---c-2-1560571132-width600height769.jpeg\" /></p>\r\n\r\n<p>Sau 20 năm l&agrave;m bạn th&acirc;n, Cổ Thi&ecirc;n Lạc v&agrave; Tuy&ecirc;n Huy&ecirc;n ch&iacute;nh thức hẹn h&ograve; với nhau.</p>\r\n\r\n<p><strong>Bạn th&acirc;n 20 năm bỗng h&oacute;a người t&igrave;nh</strong></p>\r\n\r\n<p>N&oacute;i về Tuy&ecirc;n Huy&ecirc;n, Cổ Thi&ecirc;n Lạc chia sẻ:&nbsp;<em>&ldquo;T&iacute;nh Tuy&ecirc;n Huy&ecirc;n rất đ&agrave;n &ocirc;ng, mạnh mẽ, th&iacute;ch tranh luận chứ kh&ocirc;ng hề dịu d&agrave;ng như mẫu bạn g&aacute;i t&ocirc;i cần. Nếu quen nhau chắc chắn mỗi ng&agrave;y c&atilde;i nhau tỷ lần&rdquo;</em>. Anh vẫn lu&ocirc;n b&ecirc;n cạnh c&ocirc; suốt những năm th&aacute;ng tuổi trẻ, an ủi động vi&ecirc;n mỗi khi Tuy&ecirc;n Huy&ecirc;n gặp trắc trở trong t&igrave;nh cảm hay c&oacute; kh&uacute;c mắc trong c&ocirc;ng việc v&agrave; cuộc sống.</p>\r\n\r\n<p>Về phần Tuy&ecirc;n Huy&ecirc;n, c&ocirc; n&oacute;i về &ldquo;bạn th&acirc;n 20 năm&rdquo; của m&igrave;nh rằng:&nbsp;<em>&quot;Cổ Thi&ecirc;n Lạc theo chủ nghĩa ho&agrave;n mỹ, c&aacute;i g&igrave; cũng muốn tốt nhất, đẹp nhất. T&ocirc;i v&agrave; Cổ Thi&ecirc;n Lạc hợp t&aacute;c qua nhiều bộ phim v&agrave; chơi th&acirc;n với nhau nhưng giữa ch&uacute;ng t&ocirc;i kh&ocirc;ng hề xảy ra t&igrave;nh cảm y&ecirc;u đương g&igrave; như tin đồn, v&igrave; anh ấy đ&acirc;u c&oacute; coi t&ocirc;i l&agrave;... con g&aacute;i&quot;.</em></p>\r\n\r\n<p><img alt=\"Thân thế bất ngờ của bạn gái &amp;#39;Dương Quá giàu nhất Hong Kong&amp;#39; - 2\" src=\"https://cdn.24h.com.vn/upload/2-2019/images/2019-06-15/1560612680-687-than-the-bat-ngo-cua-ban-gai-duong-qua-giau-nhat-hong-kong-c----thi--n-l---c-1560590119-width600height399.jpg\" /></p>\r\n\r\n<p>Cổ Thi&ecirc;n Lạc v&agrave; Tuy&ecirc;n Huy&ecirc;n cuối c&ugrave;ng cũng cho người h&acirc;m mộ thỏa l&ograve;ng mong ước.</p>\r\n\r\n<p>Tới thời điểm n&agrave;y, cả Cổ Thi&ecirc;n Lạc v&agrave; Tuy&ecirc;n Huy&ecirc;n đều chưa c&oacute; bất cứ x&aacute;c nhận n&agrave;o về tiến triển tốt đẹp trong mối quan hệ của họ. Thế nhưng, người h&acirc;m mộ kh&ocirc;ng khỏi nức l&ograve;ng mong ng&oacute;ng cả hai sẽ c&ugrave;ng về chung một nh&agrave; khi đ&atilde; l&agrave; bạn th&acirc;n tới 20 năm. D&ugrave; trước đ&oacute;, cả 2 đều đ&oacute;ng chung với nhau rất nhiều bộ phim với vai t&igrave;nh nh&acirc;n nồng ch&aacute;y nhưng chưa một lần giả th&agrave;nh thật.</p>\r\n\r\n<p>Họ đ&atilde; l&agrave; người y&ecirc;u của nhau trong phim&nbsp;<em>Ch&uacute; ch&oacute; th&ocirc;ng minh, Hồ sơ trinh s&aacute;t 4, Cỗ m&aacute;y thời gian, Quỷ m&ecirc; t&acirc;m khiếu</em>. Đặc biệt, bộ phim&nbsp;<em>Hồ sơ trinh s&aacute;t 4</em>&nbsp;lại đem tới giải thưởng&nbsp;<em>Thị Đế - Thị Hậu</em>&nbsp;cho cặp đ&ocirc;i v&agrave;o năm 1999. Đ&acirc;y l&agrave; bộ phim m&agrave; cả 2 lại kh&ocirc;ng phải l&agrave; người t&igrave;nh của nhau.</p>\r\n\r\n<p><strong>Hoa đ&aacute;n bậc nhất showbiz nhưng t&igrave;nh duy&ecirc;n c&ograve;n nhiều lận đận</strong></p>\r\n\r\n<p>Tuy&ecirc;n Huy&ecirc;n c&oacute; đ&ocirc;i mắt sắc sảo, cuốn h&uacute;t v&agrave; đầy t&agrave;i năng. Với phong c&aacute;ch diễn xuất linh hoạt c&ugrave;ng việc x&acirc;y dựng h&igrave;nh tượng nữ cường nh&acirc;n bản lĩnh, cương trực đ&atilde; khiến c&ocirc; trở th&agrave;nh một h&igrave;nh ảnh kh&oacute; c&oacute; thể thay thế trong l&ograve;ng của kh&aacute;n giả. Tuy&ecirc;n Huy&ecirc;n ch&iacute;nh l&agrave; một trong &ldquo;Tứ đại Hoa đ&aacute;n&rdquo; đ&igrave;nh đ&aacute;m của TvB. Thế nhưng, đường t&igrave;nh duy&ecirc;n của c&ocirc; gặp đầy trắc trở với v&ocirc; số scandal.</p>\r\n\r\n<p>C&ocirc; t&igrave;nh vướng v&agrave;o tin đồn &ldquo;phim giả t&igrave;nh thật&rdquo; với La Gia Lương. Khi đ&oacute;, v&igrave; hợp t&aacute;c qu&aacute; ăn &yacute; lại đ&uacute;ng thời điểm nam diễn vi&ecirc;n đang xử l&yacute; chuy&ecirc;n ly h&ocirc;n của bản th&acirc;n, Tuy&ecirc;n Huy&ecirc;n bị g&aacute;n c&aacute;i m&aacute;c &ldquo;tiểu tam&rdquo;. Mọi thứ chỉ vỡ lẽ ra khi cả 2 c&ugrave;ng nhau hợp t&aacute;c mở nh&agrave; h&agrave;ng m&agrave; Tuy&ecirc;n Huy&ecirc;n c&ograve;n trở th&agrave;nh mẹ nu&ocirc;i của con trai Lưu Gia Lương.</p>\r\n\r\n<p><img alt=\"Thân thế bất ngờ của bạn gái &amp;#39;Dương Quá giàu nhất Hong Kong&amp;#39; - 3\" src=\"https://cdn.24h.com.vn/upload/2-2019/images/2019-06-15/1560612680-623-co-thien-lac-chinh-thuc-hen-ho-voi-thi-hau-vuong-phu-sau-20-nam-lam-ban-than-tuy--n-huy--n-6-1560571132-width600height436.jpg\" /></p>\r\n\r\n<p>Trương Ch&iacute; L&acirc;m đang hẹn h&ograve; với Vi&ecirc;n Vịnh Nghi nhưng lại d&iacute;nh tin đồn t&igrave;nh cảm với Tuy&ecirc;n Huy&ecirc;n.</p>\r\n\r\n<p><img alt=\"Thân thế bất ngờ của bạn gái &amp;#39;Dương Quá giàu nhất Hong Kong&amp;#39; - 4\" src=\"https://cdn.24h.com.vn/upload/2-2019/images/2019-06-15/1560612680-498-co-thien-lac-chinh-thuc-hen-ho-voi-thi-hau-vuong-phu-sau-20-nam-lam-ban-than-tuy--n-huy--n-3-1560571132-width600height398.jpg\" /></p>\r\n\r\n<p>Nhiều năm sau, t&igrave;nh cảm chị em của Tuy&ecirc;n Huy&ecirc;n v&agrave; Vi&ecirc;n Vịnh Nghi mới phần n&agrave;o trở lại.</p>\r\n\r\n<p>Một người nữa bị d&iacute;nh tin đồn t&igrave;nh cảm ch&iacute;nh l&agrave; Trương Ch&iacute; L&acirc;m - Người m&agrave; Tuy&ecirc;n Huy&ecirc;n vẫn lu&ocirc;n cố t&igrave;nh giữ khoảng c&aacute;ch m&agrave; Vi&ecirc;n Vịnh Nghi đang hẹn h&ograve; c&ugrave;ng nam diễn vi&ecirc;n n&agrave;y. Thế nhưng, d&ugrave; c&oacute; l&agrave;m g&igrave; cũng kh&ocirc;ng tr&aacute;nh khỏi &ldquo;miệng đời&rdquo;. Những tin đồn như nh&aacute;t dao chia cắt cho t&igrave;nh chị em thắm thiết của Vi&ecirc;n Vịnh Nghi v&agrave; Tuy&ecirc;n Huy&ecirc;n. Sau nhiều năm tuyệt giao, m&atilde;i tới gần đ&acirc;y, Tuy&ecirc;n Huy&ecirc;n v&agrave; Vi&ecirc;n Vịnh Nghi mới giao h&ograve;a trở lại.</p>\r\n\r\n<p><img alt=\"Thân thế bất ngờ của bạn gái &amp;#39;Dương Quá giàu nhất Hong Kong&amp;#39; - 5\" src=\"https://cdn.24h.com.vn/upload/2-2019/images/2019-06-15/1560612680-880-co-thien-lac-chinh-thuc-hen-ho-voi-thi-hau-vuong-phu-sau-20-nam-lam-ban-than-tuy--n-huy--n-2-1560571132-width600height392.jpg\" /></p>\r\n\r\n<p>Trương Vệ Kiện v&agrave; Tuy&ecirc;n Huy&ecirc;n từng l&agrave; cặp đ&ocirc;i &quot;Kim Đồng Ngọc Nữ&quot; nhưng chia tay v&igrave; những đ&agrave;m tiếu dư luận.</p>\r\n\r\n<p>Một người kh&aacute;c nữa trong con đường t&igrave;nh duy&ecirc;n của Tuy&ecirc;n Huy&ecirc;n ch&iacute;nh l&agrave; Trương Vệ Kiện. Thế nhưng, cặp đ&ocirc;i &ldquo;nữ cường nam nhu&rdquo; n&agrave;y lại dẫn tới kết cục tan vỡ. Tr&aacute;i với việc Tuy&ecirc;n Huy&ecirc;n l&ecirc;n như diều gặp gi&oacute;, Trương Vệ Kiện khi đ&oacute; c&ograve;n bị n&oacute;i l&agrave; kẻ &ldquo;ăn b&aacute;m&rdquo; bạn g&aacute;i. Khi đ&oacute;, Tuy&ecirc;n Huy&ecirc;n&nbsp;nổi tiếng lấn lướt hơn, &aacute;p lực qu&aacute; lớn từ dư luận, cuối c&ugrave;ng, Trương Vệ Kiện đ&atilde; l&ecirc;n tiếng chia tay trước khi cả 2 chuẩn bị tổ chức h&ocirc;n lễ.</p>\r\n\r\n<p><strong>&ldquo;C&ocirc; đ&atilde; gi&agrave;, lại c&ograve;n mập. Trong nghề đ&atilde; hết thời rồi!&rdquo;</strong></p>\r\n\r\n<p>Đ&oacute; ch&iacute;nh l&agrave; c&acirc;u n&oacute;i của người đồng nghiệp khiến Tuy&ecirc;n Huy&ecirc;n từng thất thần nh&igrave;n lại bản th&acirc;n m&igrave;nh. C&aacute;ch đ&acirc;y chừng 5 năm, tuyến Gi&aacute;p của c&ocirc; c&oacute; vấn đề kh&aacute; lớn khiến th&acirc;n h&igrave;nh bỗng chốc sưng ph&ugrave; l&ecirc;n. Từ 45kg, cơ thể c&ocirc; nhanh ch&oacute;ng tăng l&ecirc;n tới 60kg. Thời điểm đ&oacute;, mọi c&ocirc;ng việc đều giảm s&uacute;t nghi&ecirc;m trọng, danh tiếng cũng mất, số tiền bản th&acirc;n c&ocirc; kiếm được cũng th&acirc;m hụt đi qu&aacute; nhiều.</p>\r\n\r\n<p><img alt=\"Thân thế bất ngờ của bạn gái &amp;#39;Dương Quá giàu nhất Hong Kong&amp;#39; - 6\" src=\"https://cdn.24h.com.vn/upload/2-2019/images/2019-06-15/1560612681-378-than-the-bat-ngo-cua-ban-gai-duong-qua-giau-nhat-hong-kong-tuy--n-huy--n-1560590119-width600height327.jpg\" /></p>\r\n\r\n<p>Tuy&ecirc;n Huy&ecirc;n từng rơi từ đỉnh cao xuống đ&aacute;y sự nghiệp, rồi lại t&igrave;m được &aacute;nh h&agrave;o quang trong cuộc đời.&nbsp;</p>\r\n\r\n<p>Do bản t&iacute;nh vốn sợ phiền phức n&ecirc;n mọi việc lớn nhỏ đều giao cho quản l&yacute; quyết định. C&oacute; việc cũng được, kh&ocirc;ng c&oacute; việc th&igrave; lại gặp gỡ bạn b&egrave;, uống tr&agrave;, leo n&uacute;i. Thế nhưng, bất gi&aacute;c l&uacute;c đ&oacute; gọi cho quản l&yacute; chỉ nhận lại một c&acirc;u ngắn gọn: &ldquo;Kh&ocirc;ng c&oacute; người n&agrave;o t&igrave;m c&ocirc; cả.&rdquo;</p>\r\n\r\n<p>May thay, khi s&oacute;ng gi&oacute; qua đi, Tuy&ecirc;n Huy&ecirc;n đ&atilde; t&igrave;m lại được &aacute;nh h&agrave;o quang năm n&agrave;o của m&igrave;nh. H&agrave;ng loạt vai diễn ấn tượng tr&ecirc;n m&agrave;n ảnh nhỏ ch&iacute;nh l&agrave; sự minh chứng r&otilde; n&eacute;t nhất. C&ocirc; chia sẻ:&nbsp;<em>&ldquo;Suốt 20 năm về trước, t&ocirc;i kh&ocirc;ng c&oacute; lấy một t&aacute;c phẩm ti&ecirc;u biểu n&agrave;o khiến bản th&acirc;n c&oacute; thể h&agrave;i l&ograve;ng. Thế nhưng, khi đ&oacute;ng xong bộ phim Binh đo&agrave;n ph&aacute;i yếu đ&atilde; c&oacute; người gọi t&ocirc;i bằng t&ecirc;n nh&acirc;n vật trong phim - Chị Mall. Điều đ&oacute; khiến t&ocirc;i v&ocirc; c&ugrave;ng sung sướng v&igrave; đ&atilde; c&oacute; một t&aacute;c phẩm ti&ecirc;u biểu sau suốt 26 năm m&ograve;n mỏi đợi chờ.&rdquo;</em></p>\r\n', 'Chuyện tình cảm bất ngờ được công khai của Cổ Thiên Lạc và Tuyên Huyên làm thỏa lòng của người hâm mộ đã dày công chờ đợi.', NULL, NULL, 2);
INSERT INTO `baiviet` VALUES (70, '/public/images/anh-chup-man-hinh-2019-06-16-luc-101252-156065611917514829677.png', 'Trường Giang bị phản ứng vì sai kiến thức cơ bản khi dẫn chương trình “Nhanh như chớp”', 4, 7, NULL, 'Trường Giang', '2019-06-16', '<p><img alt=\"TrÆ°á»ng Giang bá» pháº£n á»©ng vÃ¬ sai kiáº¿n thá»©c cÆ¡ báº£n khi dáº«n chÆ°Æ¡ng trÃ¬nh âNhanh nhÆ° chá»pâ - áº¢nh 1.\" src=\"https://kenh14cdn.com/2019/6/16/anh-chup-man-hinh-2019-06-16-luc-101252-156065611917514829677.png\" /></p>\r\n', 'Rất nhiều ý kiến của dân mạng đã được gửi tới Trường Giang ngay khi chương trình vừa lên sóng.', NULL, NULL, NULL);
INSERT INTO `baiviet` VALUES (71, '/public/images/h6-15606779209361722156337.jpg', 'Những ông bố của loạt idol Kpop từng gây bão mạng: Không visual đỉnh thì cũng quá giàu, bố Lisa còn nổi tiếng thế giới', 4, 7, NULL, 'Kpop', '0000-00-00', '<p>Ngo&agrave;i t&agrave;i năng, c&aacute;c idol Kpop c&ograve;n nổi tiếng với ngoại h&igrave;nh xuất ch&uacute;ng. Điều n&agrave;y khiến người h&acirc;m mộ thắc mắc liệu bố mẹ của c&aacute;c thần tượng n&agrave;y sẽ ho&agrave;n hảo đến mức n&agrave;o. Dưới đ&acirc;y l&agrave; ngoại danh s&aacute;ch những &ocirc;ng bố kh&ocirc;ng chỉ c&oacute; visual đỉnh m&agrave; c&ograve;n v&ocirc; c&ugrave;ng t&agrave;i năng của c&aacute;c ng&ocirc;i sao.</p>\r\n\r\n<h3><a href=\"http://kenh14.vn/v-bts.html\" target=\"_blank\">V</a>&nbsp;(BTS)</h3>\r\n\r\n<p>V (BTS) được bầu chọn l&agrave; gương mặt đẹp trai nhất thế giới, nhiều lần lọt top mỹ nam hot nhất xứ H&agrave;n. Điều n&agrave;y khiến người h&acirc;m mộ lu&ocirc;n t&ograve; m&ograve; nhan sắc của hai vị phụ huynh của anh ch&agrave;ng.</p>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/6/16/cms1391-15606776428611086101026.jpg\" target=\"_blank\"><img alt=\"Những ông bố của loạt idol Kpop từng gây bão mạng: Không visual đỉnh thì cũng quá giàu, bố Lisa còn nổi tiếng thế giới - Ảnh 1.\" src=\"https://kenh14cdn.com/2019/6/16/cms1391-15606776428611086101026.jpg\" /></a></p>\r\n\r\n<p>Th&agrave;nh vi&ecirc;n BTS khiến nhiều người ngưỡng mộ v&igrave; c&oacute; nhan sắc đỉnh cao</p>\r\n\r\n<p>Trong những tấm ảnh gia đ&igrave;nh hiếm hoi được tiết lộ, c&oacute; thể thấy V may mắn sở hữu những ưu điểm nổi trội của bố mẹ m&igrave;nh, đặc biệt l&agrave; sống mũi, đ&ocirc;i m&ocirc;i của bố v&agrave; đ&ocirc;i mắt của mẹ. D&ugrave; bức ảnh kh&ocirc;ng qu&aacute; r&otilde; n&eacute;t nhưng vẫn kh&ocirc;ng d&igrave;m được visual đỉnh cao của &ocirc;ng.</p>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/6/16/65f1f596e824ed67bd748b07dd222d693df309a5r1-1024-741v2hq-15606775158061213921379.jpg\" target=\"_blank\"><img alt=\"Những ông bố của loạt idol Kpop từng gây bão mạng: Không visual đỉnh thì cũng quá giàu, bố Lisa còn nổi tiếng thế giới - Ảnh 2.\" src=\"https://kenh14cdn.com/2019/6/16/65f1f596e824ed67bd748b07dd222d693df309a5r1-1024-741v2hq-15606775158061213921379.jpg\" /></a></p>\r\n\r\n<p>Gương mặt của V thừa hưởng nhiều n&eacute;t đẹp của bố mẹ</p>\r\n\r\n<h3><a href=\"http://kenh14.vn/jungyeon-twice.html\" target=\"_blank\">Jungyeon</a>&nbsp;(TWICE)</h3>\r\n\r\n<p>Jungyeon được xem l&agrave; một trong những th&agrave;nh vi&ecirc;n c&oacute; gia thế tr&acirc;m anh thế phiệt nhất của TWICE. Bố của nữ ca sĩ l&agrave; đầu bếp chuy&ecirc;n nghiệp phụ tr&aacute;ch ẩm thực truyền thống của H&agrave;n Quốc - Yoo Chang Joon. &Ocirc;ng từng l&agrave; đầu bếp 20 năm của kh&aacute;ch sạn si&ecirc;u sang Seoul Plaza của H&agrave;n Quốc. &Ocirc;ng sở hữu một nh&agrave; h&agrave;ng ri&ecirc;ng c&oacute; địa h&igrave;nh tốt tại thủ đ&ocirc; Seoul. Kh&ocirc;ng chỉ nổi tiếng về t&agrave;i năng, bố của nữ idol c&ograve;n khiến fan bất ngờ với nhan sắc kh&ocirc;ng tầm thường từ thời trẻ của m&igrave;nh.</p>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/6/16/h1-1560677456895561365702.jpg\"><img alt=\"Những ông bố của loạt idol Kpop từng gây bão mạng: Không visual đỉnh thì cũng quá giàu, bố Lisa còn nổi tiếng thế giới - Ảnh 3.\" src=\"https://kenh14cdn.com/2019/6/16/h1-1560677456895561365702.jpg\" /></a></p>\r\n\r\n<p>Jungyeon l&agrave; một trong những th&agrave;nh vi&ecirc;n c&oacute; ngoại h&igrave;nh c&aacute; t&iacute;nh, rất ri&ecirc;ng v&agrave; ng&agrave;y c&agrave;ng &quot;l&ecirc;n đời&quot; của TWICE</p>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/6/16/h1-1560677456898429454733.jpg\" target=\"_blank\"><img alt=\"Những ông bố của loạt idol Kpop từng gây bão mạng: Không visual đỉnh thì cũng quá giàu, bố Lisa còn nổi tiếng thế giới - Ảnh 4.\" src=\"https://kenh14cdn.com/2019/6/16/h1-1560677456898429454733.jpg\" /></a></p>\r\n\r\n<p>Nhan sắc của bố Jungyeon thời trẻ</p>\r\n\r\n<h3><a href=\"http://kenh14.vn/btob-yook-sung-jae.html\" target=\"_blank\">Yook Sungjae</a>&nbsp;(BTOB)</h3>\r\n\r\n<p>Mặc d&ugrave; &iacute;t tiết lộ về gia đ&igrave;nh của m&igrave;nh, nhưng ngay từ khi debut, Yook Sung Jae lu&ocirc;n được đồn đo&aacute;n l&agrave; cậu ấm của gia đ&igrave;nh gi&agrave;u c&oacute;. Trong một lần hiếm hoi tr&ecirc;n chương tr&igrave;nh truyền h&igrave;nh, nam ca sĩ tiết lộ bố l&agrave; CEO của một tập đo&agrave;n IT. Tr&ecirc;n trang c&aacute; nh&acirc;n của m&igrave;nh, th&agrave;nh vi&ecirc;n BTOB từng tiết lộ h&igrave;nh ảnh bố mẹ. Bức ảnh khiến người h&acirc;m mộ kh&ocirc;ng khỏi trầm trồ v&igrave; visual đ&aacute;ng nể của hai vị phụ huynh.</p>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/6/16/h2-15606774569041547892327.jpg\" target=\"_blank\"><img alt=\"Những ông bố của loạt idol Kpop từng gây bão mạng: Không visual đỉnh thì cũng quá giàu, bố Lisa còn nổi tiếng thế giới - Ảnh 5.\" src=\"https://kenh14cdn.com/2019/6/16/h2-15606774569041547892327.jpg\" /></a></p>\r\n\r\n<p>Yook Sungjae rất &iacute;t khi chia sẻ về gia cảnh của m&igrave;nh</p>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/6/16/h2-1560677920909536613559.png\" target=\"_blank\"><img alt=\"Những ông bố của loạt idol Kpop từng gây bão mạng: Không visual đỉnh thì cũng quá giàu, bố Lisa còn nổi tiếng thế giới - Ảnh 6.\" src=\"https://kenh14cdn.com/2019/6/16/h2-1560677920909536613559.png\" /></a></p>\r\n\r\n<p>Nhan sắc của bố mẹ Sungjae khiến nhiều người bất ngờ v&igrave; như người nổi tiếng</p>\r\n\r\n<h3><a href=\"http://kenh14.vn/lisa-blackpink.html\" target=\"_blank\">Lisa</a>&nbsp;(BLACKPINK)</h3>\r\n\r\n<p>Trong một chương tr&igrave;nh thực tế của nh&oacute;m, người h&acirc;m mộ từng được diện kiến mẹ ruột v&agrave; bố nu&ocirc;i của Lisa. Bố nu&ocirc;i của Lisa t&ecirc;n l&agrave; Marco Brueschweiler. &Ocirc;ng l&agrave; một đầu bếp nổi tiếng thế giới người Thụy Sĩ, cố vấn kinh doanh đồ ăn v&agrave; chuy&ecirc;n gia về ẩm thực Th&aacute;i. Thậm ch&iacute; &ocirc;ng c&ograve;n từng được Hiệp hội đầu bếp thế giới trao tặng danh hiệu Si&ecirc;u đầu bếp to&agrave;n cầu (Global Master Chef). Kh&ocirc;ng chỉ khiến fan trầm trồ về địa vị v&agrave; t&agrave;i năng m&agrave; vẻ ngo&agrave;i đĩnh đạc của &ocirc;ng cũng l&agrave;m cho mọi người kh&ocirc;ng khỏi bất ngờ.</p>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/6/16/h3-1560677920914526630293.jpg\"><img alt=\"Những ông bố của loạt idol Kpop từng gây bão mạng: Không visual đỉnh thì cũng quá giàu, bố Lisa còn nổi tiếng thế giới - Ảnh 7.\" src=\"https://kenh14cdn.com/2019/6/16/h3-1560677920914526630293.jpg\" /></a></p>\r\n\r\n<p>Lisa được mệnh danh l&agrave; &quot;b&uacute;p b&ecirc; sống&quot; của BLACKPINK</p>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/6/16/5b48a251b4632894a997ab554c078fb3200abd7bhq-1539158418861941533555-15606780462051653905013.jpg\" target=\"_blank\"><img alt=\"Những ông bố của loạt idol Kpop từng gây bão mạng: Không visual đỉnh thì cũng quá giàu, bố Lisa còn nổi tiếng thế giới - Ảnh 8.\" src=\"https://kenh14cdn.com/2019/6/16/5b48a251b4632894a997ab554c078fb3200abd7bhq-1539158418861941533555-15606780462051653905013.jpg\" /></a></p>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/6/16/h3-156067792091854895378.jpg\" target=\"_blank\"><img alt=\"Những ông bố của loạt idol Kpop từng gây bão mạng: Không visual đỉnh thì cũng quá giàu, bố Lisa còn nổi tiếng thế giới - Ảnh 9.\" src=\"https://kenh14cdn.com/2019/6/16/h3-156067792091854895378.jpg\" /></a></p>\r\n\r\n<p>Bố nu&ocirc;i của Lisa l&agrave; một đầu bếp v&ocirc; c&ugrave;ng t&agrave;i năng</p>\r\n\r\n<h3><a href=\"http://kenh14.vn/jackson-got7.html\" target=\"_blank\">Jackson</a>&nbsp;(GOT7)</h3>\r\n\r\n<p>Trong những lần hiếm hoi nam thần tượng đến từ Hongkong chia sẻ h&igrave;nh ảnh về gia đ&igrave;nh m&igrave;nh, Jackson cho thấy phụ huynh anh c&oacute; nhan sắc kh&ocirc;ng hề tầm thường. Bố mẹ của Jackson được nhận x&eacute;t l&agrave; c&oacute; vẻ ngo&agrave;i trẻ hơn tuổi.</p>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/6/16/h4-15606779209251747908052.png\" target=\"_blank\"><img alt=\"Những ông bố của loạt idol Kpop từng gây bão mạng: Không visual đỉnh thì cũng quá giàu, bố Lisa còn nổi tiếng thế giới - Ảnh 10.\" src=\"https://kenh14cdn.com/2019/6/16/h4-15606779209251747908052.png\" /></a></p>\r\n\r\n<p>Bố mẹ Jackson c&oacute; vẻ ngo&agrave;i trẻ trung</p>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/6/16/h4-15606779209221494975842.jpg\" target=\"_blank\"><img alt=\"Những ông bố của loạt idol Kpop từng gây bão mạng: Không visual đỉnh thì cũng quá giàu, bố Lisa còn nổi tiếng thế giới - Ảnh 11.\" src=\"https://kenh14cdn.com/2019/6/16/h4-15606779209221494975842.jpg\" /></a></p>\r\n\r\n<p>Jackson thừa hưởng nhiều n&eacute;t đẹp từ bố mẹ</p>\r\n\r\n<h3><a href=\"http://kenh14.vn/jeon-somi.html\" target=\"_blank\">Jeon Somi</a></h3>\r\n\r\n<p>Bố của Jeon Somi t&ecirc;n l&agrave; Matthew Douma người Canada, c&oacute; chuy&ecirc;n m&ocirc;n cao trong nhiều m&ocirc;n v&otilde; thuật đặc biệt l&agrave; Taekwondo. Tuy nhi&ecirc;n, v&igrave; c&oacute; vẻ ngo&agrave;i đẹp trai n&ecirc;n &ocirc;ng đ&atilde; chuyển sang l&agrave;m người mẫu v&agrave; học trang điểm. &Iacute;t ai biết rằng bố của nữ thần tượng đ&atilde; xuất hiện trong phim &quot;Hậu duệ mặt trời&quot;. D&ugrave; chỉ đ&oacute;ng vai nhỏ nhưng &ocirc;ng nhận được sự ch&uacute; &yacute; của đ&ocirc;ng đảo cộng đồng mạng v&igrave; visual của m&igrave;nh.</p>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/6/16/00221165-156067823299566397087.jpg\" target=\"_blank\"><img alt=\"Những ông bố của loạt idol Kpop từng gây bão mạng: Không visual đỉnh thì cũng quá giàu, bố Lisa còn nổi tiếng thế giới - Ảnh 12.\" src=\"https://kenh14cdn.com/2019/6/16/00221165-156067823299566397087.jpg\" /></a></p>\r\n\r\n<p>Somi mang nhiều n&eacute;t đẹp của người bố</p>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/6/16/cj7ft4iukae1cgc-1560678329501848055350.jpg\" target=\"_blank\"><img alt=\"Những ông bố của loạt idol Kpop từng gây bão mạng: Không visual đỉnh thì cũng quá giàu, bố Lisa còn nổi tiếng thế giới - Ảnh 13.\" src=\"https://kenh14cdn.com/2019/6/16/cj7ft4iukae1cgc-1560678329501848055350.jpg\" /></a></p>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/6/16/h5-15606779209322064065785.png\"><img alt=\"Những ông bố của loạt idol Kpop từng gây bão mạng: Không visual đỉnh thì cũng quá giàu, bố Lisa còn nổi tiếng thế giới - Ảnh 14.\" src=\"https://kenh14cdn.com/2019/6/16/h5-15606779209322064065785.png\" /></a></p>\r\n\r\n<p>Bố Somi từng g&acirc;y sốt v&igrave; vẻ ngo&agrave;i đẹp trai khi tham gia &quot;Hậu duệ mặt trời&quot;</p>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/6/16/jeon3-1505707744615-15606794236291503289885.jpg\" target=\"_blank\"><img alt=\"Những ông bố của loạt idol Kpop từng gây bão mạng: Không visual đỉnh thì cũng quá giàu, bố Lisa còn nổi tiếng thế giới - Ảnh 15.\" src=\"https://kenh14cdn.com/2019/6/16/jeon3-1505707744615-15606794236291503289885.jpg\" /></a></p>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/6/16/c3whrtwwcae2jvp-650x433-1505710046677-1560679452525543462644.jpg\" target=\"_blank\"><img alt=\"Những ông bố của loạt idol Kpop từng gây bão mạng: Không visual đỉnh thì cũng quá giàu, bố Lisa còn nổi tiếng thế giới - Ảnh 16.\" src=\"https://kenh14cdn.com/2019/6/16/c3whrtwwcae2jvp-650x433-1505710046677-1560679452525543462644.jpg\" /></a></p>\r\n\r\n<p><strong>GIF</strong>.</p>\r\n\r\n<p>Ngoại h&igrave;nh si&ecirc;u đỉnh của bố Somi</p>\r\n\r\n<h3><a href=\"http://kenh14.vn/kim-jaejoong-jyj.html\" target=\"_blank\">Jaejoong</a>&nbsp;(JYJ)</h3>\r\n\r\n<p>Jaejoong (JYJ) được biết đến l&agrave; một trong những tượng đ&agrave;i visual của thế hệ idol thế hệ thứ 2. Mặc d&ugrave; đ&atilde; nhiều năm tr&ocirc;i qua nhưng &iacute;t c&oacute; nam thần tượng n&agrave;o c&oacute; thể vượt qua nhan sắc của th&agrave;nh vi&ecirc;n JYJ. &Iacute;t ai biết rằng n&eacute;t đẹp của nam ca sĩ thừa hưởng chủ yếu từ bố của m&igrave;nh. Vẻ đẹp trai của bố Jaejoong nằm ở một đẳng cấp kh&aacute;c ho&agrave;n to&agrave;n so với những &ocirc;ng bố của c&aacute;c idol được liệt k&ecirc; ở tr&ecirc;n. &Ocirc;ng sở hữu một gương mặt đẹp tự nhi&ecirc;n, những đường n&eacute;t chẳng kh&aacute;c g&igrave; một bức tượng thần. D&ugrave; xuất th&acirc;n từ H&agrave;n Quốc nhưng gương mặt &ocirc;ng lại mang n&eacute;t đẹp của người ch&acirc;u &Acirc;u.</p>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/6/16/h6-15606779209391926267561.png\" target=\"_blank\"><img alt=\"Những ông bố của loạt idol Kpop từng gây bão mạng: Không visual đỉnh thì cũng quá giàu, bố Lisa còn nổi tiếng thế giới - Ảnh 18.\" src=\"https://kenh14cdn.com/2019/6/16/h6-15606779209391926267561.png\" /></a></p>\r\n\r\n<p>Nếu được sinh ra ở thời hiện tại, bố của Jaejoong c&oacute; thể l&agrave; một t&agrave;i tử đ&igrave;nh đ&aacute;m</p>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/6/16/h6-15606779209361722156337.jpg\" target=\"_blank\"><img alt=\"Những ông bố của loạt idol Kpop từng gây bão mạng: Không visual đỉnh thì cũng quá giàu, bố Lisa còn nổi tiếng thế giới - Ảnh 19.\" src=\"https://kenh14cdn.com/2019/6/16/h6-15606779209361722156337.jpg\" /></a></p>\r\n\r\n<p>Jaejoong l&agrave; một trong những idol c&oacute; visual đỉnh cao của thế hệ Gen2</p>\r\n', 'Dàn idol Kpop đình đám như V (BTS), Lisa (BLACPINK) hay Yook Sungjae (BTOB)... đều quá xuất chúng.', NULL, NULL, NULL);
INSERT INTO `baiviet` VALUES (72, '/public/images/007bjsojgy1g3rstdo12kj30u011itdr-1560668002285137678337.jpg', 'Mẫu Hàn chi 21 tỷ suốt 10 năm để phẫu thuật giống Angela Baby: Body đẹp hơn bản gốc, trơ trẽn giật chồng bạn thân', 4, 7, NULL, 'Angela Baby, sao Hoa ngữ, sao Hàn, phẫu thuật thẩm', '0000-00-00', '<p><a href=\"http://kenh14.vn/angela-baby.html\" target=\"_blank\">Angela Baby</a>&nbsp;với vẻ đẹp ấn tượng từ l&acirc;u đ&atilde; trở th&agrave;nh tượng nhan sắc của showbiz Hoa ngữ. Gương mặt của c&ocirc; n&agrave;ng cũng trở th&agrave;nh h&igrave;nh mẫu l&yacute; tưởng d&agrave;nh cho c&aacute;c c&ocirc; g&aacute;i hướng tới. Rất nhiều người chọn c&aacute;c chỉnh sửa nhan sắc giống b&agrave; x&atilde; Huỳnh Hiểu Minh, v&agrave; kh&ocirc;ng chỉ ở Trung Quốc, Baby cũng nức tiếng tại H&agrave;n Quốc. Một người mẫu trẻ t&ecirc;n Ban Nam Gyu đ&atilde; mạnh tay chi tới 1 tỷ won (tương đương khoảng 21 tỷ đồng) trong v&ograve;ng 10 năm qua để c&oacute; vẻ đẹp giống với Angela Baby.</p>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/6/16/8ef4-hymscpq7371783-15606651318852099582637.png\" target=\"_blank\"><img alt=\"Mẫu Hàn chi 21 tỷ suốt 10 năm để phẫu thuật giống Angela Baby: Body đẹp hơn bản gốc, trơ trẽn giật chồng bạn thân - Ảnh 1.\" src=\"https://kenh14cdn.com/2019/6/16/8ef4-hymscpq7371783-15606651318852099582637.png\" /></a></p>\r\n\r\n<p>H&igrave;nh ảnh của Ban Nam Gyu - ulzzang v&agrave; cũng l&agrave; người mẫu c&oacute; tiếng tăm của H&agrave;n Quốc sau khi phẫu thuật thẩm mỹ giống Angela Baby</p>\r\n\r\n<p>Trước đ&acirc;y, Nam Gyu sở hữu nhan sắc kh&aacute; b&igrave;nh thường. Người đẹp thực sự nổi tiếng sau khi tiết lộ m&igrave;nh chi tới 1 tỷ won để chỉnh sửa lại nhan sắc. C&oacute; thể thấy, sau khi thẩm mỹ, nhan sắc trước v&agrave; sau của c&ocirc; n&agrave;ng như 2 người ho&agrave;n to&agrave;n kh&aacute;c lạ.</p>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/6/16/bd2090880232472d1d8b7f21100163de197d21b7size38w604h466-1560665317882861177548.jpeg\" target=\"_blank\"><img alt=\"Mẫu Hàn chi 21 tỷ suốt 10 năm để phẫu thuật giống Angela Baby: Body đẹp hơn bản gốc, trơ trẽn giật chồng bạn thân - Ảnh 2.\" src=\"https://kenh14cdn.com/2019/6/16/bd2090880232472d1d8b7f21100163de197d21b7size38w604h466-1560665317882861177548.jpeg\" /></a></p>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/6/16/6418-hymscpq7374135-1560665328556468956208.jpg\" target=\"_blank\"><img alt=\"Mẫu Hàn chi 21 tỷ suốt 10 năm để phẫu thuật giống Angela Baby: Body đẹp hơn bản gốc, trơ trẽn giật chồng bạn thân - Ảnh 3.\" src=\"https://kenh14cdn.com/2019/6/16/6418-hymscpq7374135-1560665328556468956208.jpg\" /></a></p>\r\n\r\n<p>Nhan sắc trước đ&acirc;y của người đẹp họ Ban</p>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/6/16/ffef-hymscpq7372940-15606653622221958992420.jpg\"><img alt=\"Mẫu H&amp;#224;n chi 21 tỷ suốt 10 năm để phẫu thuật giống Angela Baby: Body đẹp hơn bản gốc, trơ trẽn giật chồng bạn th&amp;#226;n - Ảnh 4.\" src=\"https://kenh14cdn.com/thumb_w/660/2019/6/16/ffef-hymscpq7372940-15606653622221958992420.jpg\" /></a></p>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/6/16/d0bb-hymscpq7371962-15606653622201691699000.jpg\"><img alt=\"Mẫu H&amp;#224;n chi 21 tỷ suốt 10 năm để phẫu thuật giống Angela Baby: Body đẹp hơn bản gốc, trơ trẽn giật chồng bạn th&amp;#226;n - Ảnh 4.\" src=\"https://kenh14cdn.com/thumb_w/660/2019/6/16/d0bb-hymscpq7371962-15606653622201691699000.jpg\" /></a></p>\r\n\r\n<p>V&agrave; h&igrave;nh ảnh hiện tại</p>\r\n\r\n<p>Kh&ocirc;ng kh&oacute; c&oacute; thể nhận ra gương mặt kh&aacute;c biệt của Ban Nam Gyu. Nhiều người nhận x&eacute;t, c&ocirc; n&agrave;ng sở hữu vẻ đẹp giống b&agrave; x&atilde; Huỳnh Hiểu Minh tới 70-80%. Tuy nhi&ecirc;n, b&ecirc;n cạnh đ&oacute;, Ban Nam Gyu c&ograve;n g&acirc;y sốt với body chuẩn từng milimet, thậm ch&iacute; c&ograve;n xuất sắc hơn cả &quot;bản gốc&quot; l&agrave; Angela Baby.</p>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/6/16/007bjsojgy1g3rssuupn1j30u011i10q-1560668019294400692077.jpg\" target=\"_blank\"><img alt=\"Mẫu Hàn chi 21 tỷ suốt 10 năm để phẫu thuật giống Angela Baby: Body đẹp hơn bản gốc, trơ trẽn giật chồng bạn thân - Ảnh 5.\" src=\"https://kenh14cdn.com/2019/6/16/007bjsojgy1g3rssuupn1j30u011i10q-1560668019294400692077.jpg\" /></a></p>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/6/16/007bjsojgy1g3rst0dgx1j30u00u0450-1560668019290442303650.jpg\" target=\"_blank\"><img alt=\"Mẫu Hàn chi 21 tỷ suốt 10 năm để phẫu thuật giống Angela Baby: Body đẹp hơn bản gốc, trơ trẽn giật chồng bạn thân - Ảnh 6.\" src=\"https://kenh14cdn.com/2019/6/16/007bjsojgy1g3rst0dgx1j30u00u0450-1560668019290442303650.jpg\" /></a></p>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/6/16/373516c66124a1802267bb493b880a36fcbe82b2size91w640h792-15606661700541201095820.jpeg\" target=\"_blank\"><img alt=\"Mẫu Hàn chi 21 tỷ suốt 10 năm để phẫu thuật giống Angela Baby: Body đẹp hơn bản gốc, trơ trẽn giật chồng bạn thân - Ảnh 7.\" src=\"https://kenh14cdn.com/2019/6/16/373516c66124a1802267bb493b880a36fcbe82b2size91w640h792-15606661700541201095820.jpeg\" /></a></p>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/6/16/868baf26c66f48f2f07d42bfb4d26c799298a6f2size72w537h768-15606661700641698086528.jpeg\" target=\"_blank\"><img alt=\"Mẫu Hàn chi 21 tỷ suốt 10 năm để phẫu thuật giống Angela Baby: Body đẹp hơn bản gốc, trơ trẽn giật chồng bạn thân - Ảnh 8.\" src=\"https://kenh14cdn.com/2019/6/16/868baf26c66f48f2f07d42bfb4d26c799298a6f2size72w537h768-15606661700641698086528.jpeg\" /></a></p>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/6/16/ea4b04e0c41e8c7936efee9203c23390937e88fcsize35w637h354-1-15606661700681522993436.jpeg\" target=\"_blank\"><img alt=\"Mẫu Hàn chi 21 tỷ suốt 10 năm để phẫu thuật giống Angela Baby: Body đẹp hơn bản gốc, trơ trẽn giật chồng bạn thân - Ảnh 9.\" src=\"https://kenh14cdn.com/2019/6/16/ea4b04e0c41e8c7936efee9203c23390937e88fcsize35w637h354-1-15606661700681522993436.jpeg\" /></a></p>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/6/16/a34016ec156dd9d54c3f4b0f3875bf9a11ec3d52size45w640h501-15606661084712002360167.jpeg\"><img alt=\"Mẫu Hàn chi 21 tỷ suốt 10 năm để phẫu thuật giống Angela Baby: Body đẹp hơn bản gốc, trơ trẽn giật chồng bạn thân - Ảnh 10.\" src=\"https://kenh14cdn.com/2019/6/16/a34016ec156dd9d54c3f4b0f3875bf9a11ec3d52size45w640h501-15606661084712002360167.jpeg\" /></a></p>\r\n\r\n<p>Body si&ecirc;u chuẩn của Ban Nam Gyu &quot;ăn đứt&quot; Angela Baby</p>\r\n\r\n<p>Nhờ nhan sắc v&agrave; body si&ecirc;u chuẩn c&ugrave;ng với việc &quot;g&aacute;n&quot; h&igrave;nh ảnh bản th&acirc;n với Angela Baby, Ban Nam Gyu nhận được sự quan t&acirc;m của netizen xứ Trung. C&ocirc; n&agrave;ng thậm ch&iacute; c&ograve;n tham gia 1 số chương tr&igrave;nh của Trung Quốc. Sau đ&oacute;, Ban Nam Gyu rơi v&agrave;o lưới t&igrave;nh của 1 thiếu gia gi&agrave;u c&oacute; họ Lee. Cả hai từng chia sẻ những khoảnh khắc ngọt ng&agrave;o b&ecirc;n nhau.</p>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/6/16/b4fa1a07eba8c48ce6049a8d726a29964377e493size47w609h509-15606665395671530541480.jpeg\" target=\"_blank\"><img alt=\"Mẫu Hàn chi 21 tỷ suốt 10 năm để phẫu thuật giống Angela Baby: Body đẹp hơn bản gốc, trơ trẽn giật chồng bạn thân - Ảnh 11.\" src=\"https://kenh14cdn.com/2019/6/16/b4fa1a07eba8c48ce6049a8d726a29964377e493size47w609h509-15606665395671530541480.jpeg\" /></a></p>\r\n\r\n<p>Nhan sắc ch&agrave;ng thiếu gia gi&agrave;u c&oacute; từng hẹn h&ograve; với Ban Nam Gyu bị nhận x&eacute;t qu&aacute; b&igrave;nh thường</p>\r\n\r\n<p>Vậy nhưng niềm vui ngắn chẳng t&agrave;y gang, 1 thời gian sau, thiếu gia Lee bị bắt v&agrave;o t&ugrave; v&igrave; tội lừa đảo. Nam Gyu l&uacute;c đ&oacute; c&ugrave;ng lặng lẽ rời khỏi người bạn trai n&agrave;y, chỉ c&oacute; điều, Lee bắt đầu tố ngược, đ&ograve;i Nam Gyu phải ho&agrave;n trả anh 1 tỷ won (21 tỷ đồng), thậm ch&iacute; c&ograve;n định khởi kiện c&ocirc;.</p>\r\n\r\n<p>Phản ứng của Nam Gyu l&uacute;c n&agrave;y ch&iacute;nh l&agrave; kh&ocirc;ng quan t&acirc;m. C&ocirc; cho rằng Lee rối loạn t&acirc;m thần, c&oacute; những tuy&ecirc;n bố v&ocirc; l&yacute;.&nbsp;</p>\r\n\r\n<p>Nam Gyu tấn c&ocirc;ng sang thị trường Trung Quốc v&agrave; c&oacute; cho m&igrave;nh v&ocirc; số lời mời quảng c&aacute;o, sự nghiệp cũng phất l&ecirc;n. C&ocirc; tiếp tục hẹn h&ograve; với 1 thiếu gia người Trung t&ecirc;n Lam B&aacute;ch L&acirc;m, tuy nhi&ecirc;n ngay lập tức bị tố giật bồ bạn th&acirc;n 1 c&aacute;ch trơ trẽn.</p>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/6/16/007bjsojgy1g3rstdo12kj30u011itdr-1560668002285137678337.jpg\" target=\"_blank\"><img alt=\"Mẫu Hàn chi 21 tỷ suốt 10 năm để phẫu thuật giống Angela Baby: Body đẹp hơn bản gốc, trơ trẽn giật chồng bạn thân - Ảnh 12.\" src=\"https://kenh14cdn.com/2019/6/16/007bjsojgy1g3rstdo12kj30u011itdr-1560668002285137678337.jpg\" /></a></p>\r\n\r\n<p>Cụ thể, 1 c&ocirc; g&aacute;i t&ecirc;n Mandy l&agrave; bạn th&acirc;n của Nam Gyu, giới thiệu c&ocirc; tới Trung Quốc. Mandy đ&atilde; tố Nam Gyu giật chồng m&igrave;nh bằng những tấm ảnh chụp m&agrave;n h&igrave;nh. Trong đoạn hội thoại, Nam Gyu hỏi Mandy c&oacute; biết 1 người t&ecirc;n Sky Lam B&aacute;ch L&acirc;m l&agrave; ai kh&ocirc;ng, Mandy khẳng định Sky l&agrave; chồng cũng l&agrave; cha của c&aacute;c con c&ocirc;, vậy nhưng Nam Gyu n&oacute;i rằng m&igrave;nh v&agrave; Sky đ&atilde; y&ecirc;u nhau được 2 tuần c&ugrave;ng lời nhắn: &quot;B&acirc;y giờ anh ấy l&agrave; bạn trai t&ocirc;i, xin cậu đừng giận&quot;, &quot;T&ocirc;i quen rất nhiều thiếu gia gi&agrave;u c&oacute;, nhưng đ&acirc;y mới l&agrave; ch&acirc;n &aacute;i của t&ocirc;i&quot;.</p>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/6/16/007bjsojgy1g3rstejlhcj30u011idm3-1560668002279438081200.jpg\" target=\"_blank\"><img alt=\"Mẫu Hàn chi 21 tỷ suốt 10 năm để phẫu thuật giống Angela Baby: Body đẹp hơn bản gốc, trơ trẽn giật chồng bạn thân - Ảnh 13.\" src=\"https://kenh14cdn.com/2019/6/16/007bjsojgy1g3rstejlhcj30u011idm3-1560668002279438081200.jpg\" /></a></p>\r\n\r\n<p>Ban Nam Gyu - tiểu tam trơ trẽn giật chồng của bạn</p>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/6/16/f710-hymscpq7371751-1560668048295173930529.png\"><img alt=\"Mẫu H&amp;#224;n chi 21 tỷ suốt 10 năm để phẫu thuật giống Angela Baby: Body đẹp hơn bản gốc, trơ trẽn giật chồng bạn th&amp;#226;n - Ảnh 14.\" src=\"https://kenh14cdn.com/thumb_w/660/2019/6/16/f710-hymscpq7371751-1560668048295173930529.png\" /></a></p>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/6/16/f5bb-hymscpq7371771-15606680482861904771963.png\"><img alt=\"Mẫu H&amp;#224;n chi 21 tỷ suốt 10 năm để phẫu thuật giống Angela Baby: Body đẹp hơn bản gốc, trơ trẽn giật chồng bạn th&amp;#226;n - Ảnh 14.\" src=\"https://kenh14cdn.com/thumb_w/660/2019/6/16/f5bb-hymscpq7371771-15606680482861904771963.png\" /></a></p>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/6/16/d1c9-hymscpq7371993-1560668048282500753757.jpg\"><img alt=\"Mẫu H&amp;#224;n chi 21 tỷ suốt 10 năm để phẫu thuật giống Angela Baby: Body đẹp hơn bản gốc, trơ trẽn giật chồng bạn th&amp;#226;n - Ảnh 14.\" src=\"https://kenh14cdn.com/thumb_w/660/2019/6/16/d1c9-hymscpq7371993-1560668048282500753757.jpg\" /></a></p>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/6/16/c809-hymscpq7372008-1560668048277993057843.jpg\"><img alt=\"Mẫu H&amp;#224;n chi 21 tỷ suốt 10 năm để phẫu thuật giống Angela Baby: Body đẹp hơn bản gốc, trơ trẽn giật chồng bạn th&amp;#226;n - Ảnh 14.\" src=\"https://kenh14cdn.com/thumb_w/660/2019/6/16/c809-hymscpq7372008-1560668048277993057843.jpg\" /></a></p>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/6/16/51d5-hymscpq7371740-15606680483002132952042.png\"><img alt=\"Mẫu H&amp;#224;n chi 21 tỷ suốt 10 năm để phẫu thuật giống Angela Baby: Body đẹp hơn bản gốc, trơ trẽn giật chồng bạn th&amp;#226;n - Ảnh 14.\" src=\"https://kenh14cdn.com/thumb_w/660/2019/6/16/51d5-hymscpq7371740-15606680483002132952042.png\" /></a></p>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/6/16/4061-hymscpq7371760-156066804829291572865.png\"><img alt=\"Mẫu H&amp;#224;n chi 21 tỷ suốt 10 năm để phẫu thuật giống Angela Baby: Body đẹp hơn bản gốc, trơ trẽn giật chồng bạn th&amp;#226;n - Ảnh 14.\" src=\"https://kenh14cdn.com/thumb_w/660/2019/6/16/4061-hymscpq7371760-156066804829291572865.png\" /></a></p>\r\n\r\n<p>Một v&agrave;i h&igrave;nh ảnh kh&aacute;c của Ban Nam Gyu</p>\r\n', 'Ban Nam Gyu - người mẫu xứ Hàn sở hữu body cực chuẩn nổi lên như 1 hiện tượng vì chi tiền khủng phẫu thuật thẩm mỹ giống Angela Baby lại có rất nhiều phốt khuất sau bóng tối.', NULL, NULL, NULL);
INSERT INTO `baiviet` VALUES (73, '/public/images/anh-chup-man-hinh-2019-06-16-luc-90359-ch-1560693856328537026132.png', 'Thầy mới của Ronaldo ở Juventus chính thức lộ diện: Vừa vô địch châu Âu cùng Chelsea, nghiện thuốc lá cực nặng', 1, 2, NULL, 'ronaldo, sarri, juventus, chelsea', '2019-06-16', '<p>Tối 16/6 (giờ Việt Nam), bến đỗ của HLV Maurizio Sarri ch&iacute;nh thức được x&aacute;c định. Website ch&iacute;nh thức v&agrave; c&aacute;c trang mạng x&atilde; hội của Chelsea đồng loạt th&ocirc;ng b&aacute;o tin chia tay vị chiến lược gia người Italy.</p>\r\n\r\n<p>C&ugrave;ng thời điểm đ&oacute;, website v&agrave; c&aacute;c trang mạng x&atilde; hội của Juventus cũng đăng tải lời ch&agrave;o mừng với HLV Sarri.</p>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/6/16/anh-chup-man-hinh-2019-06-16-luc-85848-ch-1560693544696666263439.png\" target=\"_blank\"><img alt=\"Thầy mới của Ronaldo ở Juventus chính thức lộ diện: Vừa vô địch châu Âu cùng Chelsea, nghiện thuốc lá cực nặng - Ảnh 1.\" src=\"https://kenh14cdn.com/2019/6/16/anh-chup-man-hinh-2019-06-16-luc-85848-ch-1560693544696666263439.png\" /></a></p>\r\n\r\n<p>CLB Chelsea đăng tải đoạn video tri &acirc;n HLV Sarri.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Gi&aacute;m đốc điều h&agrave;nh CLB Chelsea, b&agrave; Marina Granovskaia cho hay: &quot;Trong c&aacute;c cuộc trao đổi sau trận chung kết Europa League, Maurizio đ&atilde; b&agrave;y tỏ r&otilde; r&agrave;ng mong muốn trở lại qu&ecirc; hương l&agrave; điều rất quan trọng với &ocirc;ng.&nbsp;&Ocirc;ng cũng cảm nhận tầm quan trọng của việc gần gũi gia đ&igrave;nh v&agrave; chăm s&oacute;c cha mẹ gi&agrave;, đ&acirc;y cũng l&agrave; l&yacute; do Sarri cảm thấy &ocirc;ng cần trở về để gần gũi gia đ&igrave;nh hơn trong thời điểm n&agrave;y&quot;.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Maurizio chia tay Chelsea với sự cảm k&iacute;ch của c&aacute;c cổ động vi&ecirc;n v&igrave; những cống hiến của &ocirc;ng v&agrave; c&aacute;c trợ l&yacute; trong suốt m&ugrave;a giải qua, tr&ecirc;n tư c&aacute;ch HLV trưởng, v&igrave; danh hiệu v&ocirc; địch Europa League, đưa ch&uacute;ng ta đến chung kết c&uacute;p quốc nội v&agrave; vị tr&iacute; thứ ba tr&ecirc;n BXH Premier League. Xin ch&uacute;c mừng &ocirc;ng tr&ecirc;n cương vị HLV một đội b&oacute;ng lớn ở Serie A v&agrave; ch&uacute;c &ocirc;ng may mắn trong tương lai&quot;, b&agrave; Marina ph&aacute;t biểu.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/6/16/anh-chup-man-hinh-2019-06-16-luc-90359-ch-1560693856328537026132.png\" target=\"_blank\"><img alt=\"Thầy mới của Ronaldo ở Juventus chính thức lộ diện: Vừa vô địch châu Âu cùng Chelsea, nghiện thuốc lá cực nặng - Ảnh 2.\" src=\"https://kenh14cdn.com/2019/6/16/anh-chup-man-hinh-2019-06-16-luc-90359-ch-1560693856328537026132.png\" /></a></p>\r\n\r\n<p>Bức ảnh ch&agrave;o mừng Sarri tới Juventus.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Chỉ sau đ&uacute;ng 1 m&ugrave;a sang Anh dẫn dắt Chelsea, &ocirc;ng Sarri đ&atilde; trở lại qu&ecirc; nh&agrave; Italy để dẫn dắt một đội b&oacute;ng lớn. Nh&agrave; cầm qu&acirc;n sinh năm 1959 sẽ k&yacute; bản hợp đồng 3 năm với CLB 8 lần li&ecirc;n tiếp v&ocirc; địch Italy. Tại Juve, Sarri c&oacute; cơ hội l&agrave;m việc với Cristiano Ronaldo c&ugrave;ng nhiều ng&ocirc;i sao kh&aacute;c.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/6/16/1234-15606941053471373970670.jpg\" target=\"_blank\"><img alt=\"Thầy mới của Ronaldo ở Juventus chính thức lộ diện: Vừa vô địch châu Âu cùng Chelsea, nghiện thuốc lá cực nặng - Ảnh 3.\" src=\"https://kenh14cdn.com/2019/6/16/1234-15606941053471373970670.jpg\" /></a></p>\r\n\r\n<p>V&ocirc; địch Europa League l&agrave; th&agrave;nh t&iacute;ch tốt nhất sự nghiệp của &ocirc;ng Sarri.</p>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/6/16/6422130322812523619124882885380684677906432n-15606943642701893010234.jpg\" target=\"_blank\"><img alt=\"Thầy mới của Ronaldo ở Juventus chính thức lộ diện: Vừa vô địch châu Âu cùng Chelsea, nghiện thuốc lá cực nặng - Ảnh 4.\" src=\"https://kenh14cdn.com/2019/6/16/6422130322812523619124882885380684677906432n-15606943642701893010234.jpg\" /></a></p>\r\n\r\n<p>HLV Sarri nghiện thuốc l&aacute; rất nặng. CĐV lập tức biến logo Juve th&agrave;nh điếu thuốc ch&aacute;y dở.</p>\r\n', 'Trang chủ hai đội bóng Juventus và Chelsea gần như thông báo cùng lúc về tương lai của HLV Maurizio Sarri.', NULL, NULL, NULL);
INSERT INTO `baiviet` VALUES (74, '/public/images/anh-chup-man-hinh-2019-06-16-luc-113543-sa-1560660274127442772475.png', 'Thần thái vợ chồng Beckham áp đảo toàn bộ khách mời đến dự đám cưới xa hoa của đội trưởng Real Madrid', 1, 2, NULL, 'ramos ,Beckham, Đám cưới, ronaldo', '2019-06-16', '<p><a href=\"https://kenh14cdn.com/2019/6/16/anh-chup-man-hinh-2019-06-16-luc-113543-sa-1560660274127442772475.png\" target=\"_blank\"><img alt=\"Thần thái vợ chồng Beckham áp đảo toàn bộ khách mời đến dự đám cưới xa hoa của đội trưởng Real Madrid - Ảnh 1.\" src=\"https://kenh14cdn.com/2019/6/16/anh-chup-man-hinh-2019-06-16-luc-113543-sa-1560660274127442772475.png\" /></a></p>\r\n\r\n<p>S&aacute;ng sớm 16/6 (giờ Việt Nam), Sergio Ramos đ&atilde; l&ecirc;n xe hoa với b&agrave; x&atilde; Pilar Rubio. Lễ cưới được tổ chức tại th&agrave;nh phố Sevilla (T&acirc;y Ban Nha), qu&ecirc; nh&agrave; của Ramos.</p>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/6/16/nintchdbpict000497912919-e1560622175509-15606660011591878672782.jpg\" target=\"_blank\"><img alt=\"Thần thái vợ chồng Beckham áp đảo toàn bộ khách mời đến dự đám cưới xa hoa của đội trưởng Real Madrid - Ảnh 2.\" src=\"https://kenh14cdn.com/2019/6/16/nintchdbpict000497912919-e1560622175509-15606660011591878672782.jpg\" /></a></p>\r\n\r\n<p>Đ&aacute;m cưới của Ramos diễn ra cực ho&agrave;nh tr&aacute;ng với 500 kh&aacute;ch mời tham dự. Đội trưởng CLB Real Madrid chi hẳn 1 triệu euro thu&ecirc; ban nhạc AC/DC tới biểu diễn. Vợ chồng Ramos sẽ kh&ocirc;ng nhận bất cứ m&oacute;n qu&agrave; hoặc khoản tiền mừng cưới n&agrave;o. Thay v&agrave;o đ&oacute;, kh&aacute;ch mời sẽ chuyển khoản với số tiền bất kỳ v&agrave;o t&agrave;i khoản ng&acirc;n h&agrave;ng của Quỹ Từ Thiện UNICEF, do đội trưởng Real Madrid phụ tr&aacute;ch để gi&uacute;p đỡ cho trẻ em ngh&egrave;o tr&ecirc;n to&agrave;n thế giới.</p>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/6/16/nintchdbpict000497896308-e1560619367798-15606660011562100718661.jpg\" target=\"_blank\"><img alt=\"Thần thái vợ chồng Beckham áp đảo toàn bộ khách mời đến dự đám cưới xa hoa của đội trưởng Real Madrid - Ảnh 3.\" src=\"https://kenh14cdn.com/2019/6/16/nintchdbpict000497896308-e1560619367798-15606660011562100718661.jpg\" /></a></p>\r\n\r\n<p>D&agrave;n kh&aacute;ch mời VIP, bao gồm rất nhiều ng&ocirc;i sao trong giới thể thao đ&atilde; đổ bộ xuống Sevilla để dự đ&aacute;m cưới Ramos. Thu h&uacute;t sự ch&uacute; &yacute; lớn nhất đương nhi&ecirc;n l&agrave; cặp Victoria - Beckham.</p>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/6/16/100-1560666540026896282574.jpg\" target=\"_blank\"><img alt=\"Thần thái vợ chồng Beckham áp đảo toàn bộ khách mời đến dự đám cưới xa hoa của đội trưởng Real Madrid - Ảnh 4.\" src=\"https://kenh14cdn.com/2019/6/16/100-1560666540026896282574.jpg\" /></a></p>\r\n\r\n<p>Thần th&aacute;i vợ chồng Beckham &aacute;p đảo mọi cặp vợ chồng kh&aacute;ch mời.</p>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/6/16/anh-chup-man-hinh-2019-06-16-luc-114336-sa-1560660274137964211110.png\" target=\"_blank\"><img alt=\"Thần thái vợ chồng Beckham áp đảo toàn bộ khách mời đến dự đám cưới xa hoa của đội trưởng Real Madrid - Ảnh 5.\" src=\"https://kenh14cdn.com/2019/6/16/anh-chup-man-hinh-2019-06-16-luc-114336-sa-1560660274137964211110.png\" /></a></p>\r\n\r\n<p>Hai vợ chồng sải bước như ng&ocirc;i sao điện ảnh đi tr&ecirc;n thảm đỏ. Cặp đ&ocirc;i đẹp nhất nước Anh c&oacute; gu thời trang cực kỳ sang chảnh. Cũng phải thừa nhận, kh&ocirc;ng đơn thuần chỉ v&igrave; gu thời trang hay thần th&aacute;i m&agrave; vợ chồng Beckham g&acirc;y ch&uacute; &yacute; đến vậy. Ch&iacute;nh mức độ nổi tiếng to&agrave;n cầu v&agrave; th&agrave;nh c&ocirc;ng tột bậc của cả 2 vợ chồng gi&uacute;p họ lu&ocirc;n được người đời nh&igrave;n v&agrave;o với &aacute;nh mắt ngưỡng mộ.</p>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/6/16/3-1560665493256514350099.jpg\"><img alt=\"Thần thái vợ chồng Beckham áp đảo toàn bộ khách mời đến dự đám cưới xa hoa của đội trưởng Real Madrid - Ảnh 6.\" src=\"https://kenh14cdn.com/2019/6/16/3-1560665493256514350099.jpg\" /></a></p>\r\n\r\n<p>Victoria chỉ cần mỉm cười cũng khiến d&acirc;n t&igrave;nh th&aacute;n phục: &quot;Quan trọng nhất vẫn l&agrave; thần th&aacute;i&quot;.</p>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/6/16/5-15606655789131291693642.jpg\" target=\"_blank\"><img alt=\"Thần thái vợ chồng Beckham áp đảo toàn bộ khách mời đến dự đám cưới xa hoa của đội trưởng Real Madrid - Ảnh 7.\" src=\"https://kenh14cdn.com/2019/6/16/5-15606655789131291693642.jpg\" /></a></p>\r\n\r\n<p>Beckham từng chơi cho Real Madrid từ năm 2003 đến 2007, anh l&agrave;m đồng đội của Ramos trong 2 m&ugrave;a b&oacute;ng.</p>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/6/16/anh-chup-man-hinh-2019-06-16-luc-114404-sa-15606602741431428048740.png\"><img alt=\"Thần thái vợ chồng Beckham áp đảo toàn bộ khách mời đến dự đám cưới xa hoa của đội trưởng Real Madrid - Ảnh 8.\" src=\"https://kenh14cdn.com/2019/6/16/anh-chup-man-hinh-2019-06-16-luc-114404-sa-15606602741431428048740.png\" /></a></p>\r\n\r\n<p>M&agrave;n &quot;catwalk&quot; sang trọng tr&ecirc;n đường phố Sevilla, dưới &aacute;nh nắng v&agrave;ng rực rỡ.</p>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/6/16/gettyimages11561194422048x2048-1560660274145766160402.jpg\" target=\"_blank\"><img alt=\"Thần thái vợ chồng Beckham áp đảo toàn bộ khách mời đến dự đám cưới xa hoa của đội trưởng Real Madrid - Ảnh 9.\" src=\"https://kenh14cdn.com/2019/6/16/gettyimages11561194422048x2048-1560660274145766160402.jpg\" /></a></p>\r\n\r\n<p>Luka Modric, tiền vệ trụ cột của Real Madrid, c&ugrave;ng vợ Vanja Bosnic đương nhi&ecirc;n kh&ocirc;ng thể vắng mặt trong ng&agrave;y trọng đại của đội trưởng đội b&oacute;ng.</p>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/6/16/gettyimages11561207262048x2048-1560660274149846176989.jpg\" target=\"_blank\"><img alt=\"Thần thái vợ chồng Beckham áp đảo toàn bộ khách mời đến dự đám cưới xa hoa của đội trưởng Real Madrid - Ảnh 10.\" src=\"https://kenh14cdn.com/2019/6/16/gettyimages11561207262048x2048-1560660274149846176989.jpg\" /></a></p>\r\n\r\n<p>Tiền vệ ng&ocirc;i sao Sergio Busquets tới từ đại k&igrave;nh địch Barcelona. Mặc d&ugrave; thường xuy&ecirc;n hằm h&egrave; nhau tr&ecirc;n s&acirc;n cỏ nhưng Ramos giữ mối quan hệ tốt đẹp với cầu thủ Barca đồng hương T&acirc;y Ban Nha ngo&agrave;i đời. Anh mời hầu như tất cả đồng hương đang chơi cho Barca tới đ&aacute;m cưới.</p>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/6/16/gettyimages11561218932048x2048-1560660274153598937615.jpg\" target=\"_blank\"><img alt=\"Thần thái vợ chồng Beckham áp đảo toàn bộ khách mời đến dự đám cưới xa hoa của đội trưởng Real Madrid - Ảnh 11.\" src=\"https://kenh14cdn.com/2019/6/16/gettyimages11561218932048x2048-1560660274153598937615.jpg\" /></a></p>\r\n\r\n<p>Huyền thoại Roberto Carlos nắm tay vợ Mariana Luccon tới dự tiệc. Carlos từng chơi cho Real Madrid từ năm 1996 đến 2007, c&oacute; mối quan hệ th&acirc;n thiết với Sergio Ramos.</p>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/6/16/gettyimages11561275802048x2048-1560660274156506730288.jpg\" target=\"_blank\"><img alt=\"Thần thái vợ chồng Beckham áp đảo toàn bộ khách mời đến dự đám cưới xa hoa của đội trưởng Real Madrid - Ảnh 12.\" src=\"https://kenh14cdn.com/2019/6/16/gettyimages11561275802048x2048-1560660274156506730288.jpg\" /></a></p>\r\n\r\n<p>&Ocirc;ng Florentino Perez, chủ tịch CLB Real Madrid một m&igrave;nh tới dự tiệc. Ramos quyết định kh&ocirc;ng mời Cristiano Ronaldo đến đ&aacute;m cưới. Truyền th&ocirc;ng đồn đo&aacute;n mối quan hệ giữa 2 người n&agrave;y đổ vỡ sau khi Ronaldo chuyển tới Juventus.</p>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/6/16/anh-chup-man-hinh-2019-06-16-luc-121025-ch-156066189085768893942.png\" target=\"_blank\"><img alt=\"Thần thái vợ chồng Beckham áp đảo toàn bộ khách mời đến dự đám cưới xa hoa của đội trưởng Real Madrid - Ảnh 13.\" src=\"https://kenh14cdn.com/2019/6/16/anh-chup-man-hinh-2019-06-16-luc-121025-ch-156066189085768893942.png\" /></a></p>\r\n\r\n<p>Santi Cazorla c&ugrave;ng c&ocirc; vợ xinh đẹp Ursula Santirso. Cazorla đầy t&agrave;i năng nhưng đen đủi, anh từng su&yacute;t phải cưa ch&acirc;n v&igrave; bị nhiễm khuẩn. Trải qua 8 cuộc phẫu thuật, Santi hồi phục kỳ diệu, c&oacute; thể chơi b&oacute;ng trở lại v&agrave; b&acirc;y giờ thậm ch&iacute; đạt phong độ rất cao, được gọi trở về ĐT T&acirc;y Ban Nha.</p>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/6/16/8-15606660011381074028655.jpg\" target=\"_blank\"><img alt=\"Thần thái vợ chồng Beckham áp đảo toàn bộ khách mời đến dự đám cưới xa hoa của đội trưởng Real Madrid - Ảnh 14.\" src=\"https://kenh14cdn.com/2019/6/16/8-15606660011381074028655.jpg\" /></a></p>\r\n\r\n<p>Vợ chồng ch&acirc;n s&uacute;t Alvaro Morata - Alice Campello cũng thu h&uacute;t sự ch&uacute; &yacute; lớn v&igrave; nhan sắc ngời ngời. C&ocirc; n&agrave;ng Alice l&agrave; thi&ecirc;n thần nước &Yacute;, vừa đẹp vừa giỏi kinh doanh. Cặp vợ chồng đ&atilde; c&oacute; với nhau 2 cậu con trai sinh đ&ocirc;i.</p>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/6/16/shakira-gerard-pique-15606602741631087419271.jpg\" target=\"_blank\"><img alt=\"Thần thái vợ chồng Beckham áp đảo toàn bộ khách mời đến dự đám cưới xa hoa của đội trưởng Real Madrid - Ảnh 15.\" src=\"https://kenh14cdn.com/2019/6/16/shakira-gerard-pique-15606602741631087419271.jpg\" /></a></p>\r\n\r\n<p>Pique v&agrave; Shakira được mời nhưng kh&ocirc;ng tới dự.</p>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/6/16/anh-chup-man-hinh-2019-06-16-luc-115535-sa-1560660958896851648019.png\" target=\"_blank\"><img alt=\"Thần thái vợ chồng Beckham áp đảo toàn bộ khách mời đến dự đám cưới xa hoa của đội trưởng Real Madrid - Ảnh 16.\" src=\"https://kenh14cdn.com/2019/6/16/anh-chup-man-hinh-2019-06-16-luc-115535-sa-1560660958896851648019.png\" /></a></p>\r\n\r\n<p>C&ocirc; d&acirc;u Pilar Rubio sinh năm 1978, hơn ch&uacute; rể tới 8 tuổi. Cặp đ&ocirc;i đ&atilde; chung sống nhiều năm, c&oacute; với nhau 3 cậu con trai.</p>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/6/16/11-1560666351044771622968.jpg\" target=\"_blank\"><img alt=\"Thần thái vợ chồng Beckham áp đảo toàn bộ khách mời đến dự đám cưới xa hoa của đội trưởng Real Madrid - Ảnh 17.\" src=\"https://kenh14cdn.com/2019/6/16/11-1560666351044771622968.jpg\" /></a></p>\r\n\r\n<p>Gia đ&igrave;nh nhỏ của Sergio Ramos.</p>\r\n\r\n<p>Ảnh: Getty</p>\r\n', 'Ramos đã chính thức đưa cô bạn gái Pilar Rubio về dinh. Hơn 500 khách được mời tới dự đám cưới xa hoa của chàng đội trưởng Real Madrid, nổi bật nhất là vợ chồng Beckham.', NULL, NULL, NULL);
INSERT INTO `baiviet` VALUES (75, '/public/images/gettyimages-1150173353-2048x2048-156064605493255697895.jpg', 'Messi mờ nhạt, Argentina nhận thất bại tủi hổ ở trận ra quân Copa America', 1, 2, NULL, 'messi', '2019-06-16', '<p><img alt=\"Messi mờ nhạt, Argentina nhận thất bại tủi hổ ở trận ra quân Copa America - Ảnh 1.\" src=\"https://kenh14cdn.com/2019/6/16/31-15606457810651732372176.jpg\" /></p>\r\n\r\n<p>Được kỳ vọng sẽ tỏa s&aacute;ng để mang về khởi đầu ấn tượng cho tuyển Argentina tại Copa America 2019 nhưng Lionel Messi lại trải qua 90 ph&uacute;t ở cuộc đọ sức với Colombia trong nỗi thất vọng.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Current Time0:08</p>\r\n\r\n<p>/</p>\r\n\r\n<p>Duration2:46</p>\r\n\r\n<p>Auto</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Highlights Argentina 0-2 Colombia</p>\r\n\r\n<p><a href=\"https://fptplay.vn/\" target=\"_blank\"><img alt=\"Messi mờ nhạt, Argentina nhận thất bại tủi hổ ở trận ra quân Copa America - Ảnh 3.\" src=\"https://kenh14cdn.com/2019/6/15/banner-fpt-play-final-15606028111692049158422.png\" /></a></p>\r\n\r\n<p><img alt=\"Messi mờ nhạt, Argentina nhận thất bại tủi hổ ở trận ra quân Copa America - Ảnh 4.\" src=\"https://kenh14cdn.com/thumb_w/660/2019/6/16/gettyimages-1150175624-2048x2048-15606458652581497635595.jpg\" /></p>\r\n\r\n<p>Bị c&aacute;c cầu thủ Colombia chăm s&oacute;c qu&aacute; kỹ, Leo kh&ocirc;ng c&oacute; nhiều khoảng kh&ocirc;ng để chơi b&oacute;ng.</p>\r\n\r\n<p><img alt=\"Messi mờ nhạt, Argentina nhận thất bại tủi hổ ở trận ra quân Copa America - Ảnh 5.\" src=\"https://kenh14cdn.com/thumb_w/660/2019/6/16/gettyimages-1150175905-2048x2048-15606458972871087232193.jpg\" /></p>\r\n\r\n<p>H&agrave;ng thủ đối phương cũng kh&ocirc;ng ngần ngại chơi rắn để &quot;dằn mặt&quot; ng&ocirc;i sao số một của tuyển Argentina.</p>\r\n\r\n<p><img alt=\"Messi mờ nhạt, Argentina nhận thất bại tủi hổ ở trận ra quân Copa America - Ảnh 6.\" src=\"https://kenh14cdn.com/thumb_w/660/2019/6/16/gettyimages-1150175745-2048x2048-156064587628523504334.jpg\" /></p>\r\n\r\n<p>Trong suốt hiệp 1, ng&ocirc;i sao sinh năm 1987 chỉ để lại dấu ấn với c&uacute; chọc khe để Aguero tho&aacute;t xuống nhưng kh&ocirc;ng thể đ&aacute;nh bại thủ th&agrave;nh Ospina. Trong khi b&ecirc;n phần s&acirc;n đối diện, h&agrave;ng thủ thi đấu lỏng lẻo của Argentina nhiều phen lao đao trước những t&igrave;nh huống uy hiếp của c&aacute;c ch&acirc;n s&uacute;t Colombia. Dẫu vậy, 45 ph&uacute;t đầu kh&ocirc;ng chứng kiến b&agrave;n thắng n&agrave;o được ghi.</p>\r\n\r\n<p><img alt=\"Messi mờ nhạt, Argentina nhận thất bại tủi hổ ở trận ra quân Copa America - Ảnh 7.\" src=\"https://kenh14cdn.com/thumb_w/660/2019/6/16/gettyimages-1156173057-2048x2048-1560645878821208138030.jpg\" /></p>\r\n\r\n<p>Những điều chỉnh của HLV Lionel Scaloni gi&uacute;p Argentina chơi khởi sắc hơn sau giờ nghỉ giữa hiệp. Bản th&acirc;n Messi c&oacute; cơ hội mở tỷ số ở ph&uacute;t 66 nhưng pha băng v&agrave;o đ&aacute;nh đầu khi thủ th&agrave;nh Ospina đ&atilde; bị loại bỏ của Leo lại đi chệch cột.</p>\r\n\r\n<p><img alt=\"Messi mờ nhạt, Argentina nhận thất bại tủi hổ ở trận ra quân Copa America - Ảnh 8.\" src=\"https://kenh14cdn.com/thumb_w/660/2019/6/16/gettyimages-1156171430-2048x2048-156064599054174846177.jpg\" /></p>\r\n\r\n<p>Kh&ocirc;ng thể tận dụng những cơ hội ở đầu hiệp 2, Argentina phải trả gi&aacute;. Lần lượt Roger Martinez v&agrave; Duvan Zapata ghi dấu ấn với những pha dứt điểm ấn tượng gi&uacute;p Colombia ấn định chiến thắng 2-0.</p>\r\n\r\n<p><img alt=\"Messi mờ nhạt, Argentina nhận thất bại tủi hổ ở trận ra quân Copa America - Ảnh 9.\" src=\"https://kenh14cdn.com/thumb_w/660/2019/6/16/gettyimages-1150173353-2048x2048-156064605493255697895.jpg\" /></p>\r\n\r\n<p>Gương mặt kh&ocirc;ng giấu nổi vẻ thất vọng của Messi khi c&ugrave;ng c&aacute;c đồng đội nhận thất bại muối mặt ngay trận ra qu&acirc;n.</p>\r\n\r\n<p><img alt=\"Messi mờ nhạt, Argentina nhận thất bại tủi hổ ở trận ra quân Copa America - Ảnh 10.\" src=\"https://kenh14cdn.com/thumb_w/660/2019/6/16/gettyimages-1156172410-2048x2048-156064596776056252871.jpg\" /></p>\r\n\r\n<p>Nếu kh&ocirc;ng sớm cải thiện phong độ, Albiceleste thậm ch&iacute; đối mặt với nguy cơ phải sớm dừng bước ở v&ograve;ng bảng, giải đấu h&agrave;ng triệu người d&acirc;n xứ tango kỳ vọng Messi sẽ giải th&agrave;nh c&ocirc;ng cơn kh&aacute;t danh hiệu quốc tế.</p>\r\n\r\n<p><img alt=\"Messi mờ nhạt, Argentina nhận thất bại tủi hổ ở trận ra quân Copa America - Ảnh 11.\" src=\"https://kenh14cdn.com/thumb_w/660/2019/6/16/2019-06-16074815-1560646107940662913911.png\" /></p>\r\n\r\n<p>Thống k&ecirc; trận đấu.</p>\r\n\r\n<p><img alt=\"Messi mờ nhạt, Argentina nhận thất bại tủi hổ ở trận ra quân Copa America - Ảnh 12.\" src=\"https://kenh14cdn.com/thumb_w/660/2019/6/16/2019-06-16074905-15606461540207914475.png\" /></p>\r\n\r\n<p>Thất bại bất ngờ 0-2 trước Colombia khiến Argentina tạm xếp b&eacute;t bảng B.</p>\r\n\r\n<p><em>Ảnh: Getty Images.</em></p>\r\n', 'Được kỳ vọng nhưng Lionel Messi lại thi đấu khá mờ nhạt và không thể giúp Argentina tránh khỏi thất bại muối mặt 0-2 trước Colombia ở trận ra quân Copa America 2019.', NULL, NULL, NULL);
INSERT INTO `baiviet` VALUES (76, '/public/images/hwg2-artboard-1-copy-6-15605287040601738418011.jpg', 'Thách các \"thánh ham ăn\" hiểu đúng ý nghĩa \"Thánh Ăn Đại Chiến\" của Here We Go năm nay', 2, 4, NULL, 'ẩm thực', '2019-06-17', '<p>Cuộc thi Here We Go 2019 đ&atilde; ch&iacute;nh thức bắt đầu v&agrave; đang ng&agrave;y một n&oacute;ng l&ecirc;n bởi những thử th&aacute;ch v&ocirc; c&ugrave;ng th&uacute; vị. Thế nhưng, trở lại với một phi&ecirc;n bản ho&agrave;n to&agrave;n mới mẻ, độc đ&aacute;o v&agrave; kh&aacute;c lạ - phi&ecirc;n bản &quot;Th&aacute;nh Ăn Đại Chiến&quot;, chắc hẳn rất nhiều bạn vẫn đang t&ograve; m&ograve; về &yacute; nghĩa thật sự của cuộc thi lần n&agrave;y phải kh&ocirc;ng?</p>\r\n\r\n<h2>C&aacute;i t&ecirc;n &quot;Th&aacute;nh Ăn Đại Chiến&quot; của Here We Go 2019 thật sự l&agrave; g&igrave;?</h2>\r\n\r\n<p>Năm 2019, Here We Go lại tiếp tục h&agrave;nh tr&igrave;nh mới của m&igrave;nh. Nhưng chuyến xe&nbsp;Here We Go lần n&agrave;y đặc biệt hơn rất nhiều, một chuyến xe mang t&ecirc;n: Ẩm thực.&nbsp;Cuộc h&agrave;nh tr&igrave;nh đi&nbsp;dọc chiều d&agrave;i đất nước qua ba miền Bắc - Trung - Nam, qua cả c&aacute;c đất nước l&aacute;ng giềng, vừa t&igrave;m hiểu vừa kh&aacute;m ph&aacute;, lại vừa mang theo một sứ mệnh lớn lao, đ&oacute; ch&iacute;nh l&agrave; quảng b&aacute; ẩm thực Việt đến với nhiều nơi tr&ecirc;n thế giới.</p>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/6/14/img9262-15601780070691988695970-1560527276487411132571.jpg\"><img alt=\"Th&amp;#225;ch c&amp;#225;c &amp;quot;th&amp;#225;nh ham ăn&amp;quot; hiểu đ&amp;#250;ng &amp;#253; nghĩa &amp;quot;Th&amp;#225;nh Ăn Đại Chiến&amp;quot; của Here We Go năm nay - Ảnh 1.\" src=\"https://kenh14cdn.com/thumb_w/660/2019/6/14/img9262-15601780070691988695970-1560527276487411132571.jpg\" /></a></p>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/6/14/img9255-15601780070511844737124-1560527276481305096241.jpg\"><img alt=\"Th&amp;#225;ch c&amp;#225;c &amp;quot;th&amp;#225;nh ham ăn&amp;quot; hiểu đ&amp;#250;ng &amp;#253; nghĩa &amp;quot;Th&amp;#225;nh Ăn Đại Chiến&amp;quot; của Here We Go năm nay - Ảnh 1.\" src=\"https://kenh14cdn.com/thumb_w/660/2019/6/14/img9255-15601780070511844737124-1560527276481305096241.jpg\" /></a></p>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/6/14/img9257-15601780070582064609478-15605272764841316347643.jpg\"><img alt=\"Th&amp;#225;ch c&amp;#225;c &amp;quot;th&amp;#225;nh ham ăn&amp;quot; hiểu đ&amp;#250;ng &amp;#253; nghĩa &amp;quot;Th&amp;#225;nh Ăn Đại Chiến&amp;quot; của Here We Go năm nay - Ảnh 1.\" src=\"https://kenh14cdn.com/thumb_w/660/2019/6/14/img9257-15601780070582064609478-15605272764841316347643.jpg\" /></a></p>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/6/14/img9256-156017800705667976197-15605272764862041463957.jpg\"><img alt=\"Th&amp;#225;ch c&amp;#225;c &amp;quot;th&amp;#225;nh ham ăn&amp;quot; hiểu đ&amp;#250;ng &amp;#253; nghĩa &amp;quot;Th&amp;#225;nh Ăn Đại Chiến&amp;quot; của Here We Go năm nay - Ảnh 1.\" src=\"https://kenh14cdn.com/thumb_w/660/2019/6/14/img9256-156017800705667976197-15605272764862041463957.jpg\" /></a></p>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/6/14/img9258-1560178007061730165476-1560527276484329604577.jpg\"><img alt=\"Th&amp;#225;ch c&amp;#225;c &amp;quot;th&amp;#225;nh ham ăn&amp;quot; hiểu đ&amp;#250;ng &amp;#253; nghĩa &amp;quot;Th&amp;#225;nh Ăn Đại Chiến&amp;quot; của Here We Go năm nay - Ảnh 1.\" src=\"https://kenh14cdn.com/thumb_w/660/2019/6/14/img9258-1560178007061730165476-1560527276484329604577.jpg\" /></a></p>\r\n\r\n<p>(Ảnh: Quang Vinh, The Panda&#39;s Flavor).</p>\r\n\r\n<p>Ch&iacute;nh v&igrave; vậy, Here We Go thật sự cần đến c&aacute;c &quot;th&aacute;nh ăn&quot;, bởi bạn kh&ocirc;ng chỉ cần c&oacute; kiến thức s&acirc;u rộng về ẩm thực, bạn&nbsp;c&ograve;n cần c&oacute; đủ sự s&aacute;ng tạo, c&aacute;c kỹ năng để quảng b&aacute; ẩm thực,&nbsp;l&agrave; người tạo n&ecirc;n những xu hướng mới về ẩm thực.</p>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/6/14/hwg2-artboard-1-copy-5-15605287040551448294875.jpg\" target=\"_blank\"><img alt=\"Thách các thánh ham ăn hiểu đúng ý nghĩa Thánh Ăn Đại Chiến của Here We Go năm nay - Ảnh 2.\" src=\"https://kenh14cdn.com/thumb_w/660/2019/6/14/hwg2-artboard-1-copy-5-15605287040551448294875.jpg\" /></a></p>\r\n\r\n<p>V&agrave; cuộc thi n&agrave;y ch&iacute;nh l&agrave; s&acirc;n chơi của c&aacute;c &quot;th&aacute;nh ăn&quot;, nơi sẽ diễn ra một cuộc chiến thật sự với rất nhiều thử th&aacute;ch để t&igrave;m ra những người xuất sắc nhất,&nbsp;xứng đ&aacute;ng trở th&agrave;nh đại sứ cho ẩm thực Việt. Khả năng ăn uống, vốn hiểu biết về ẩm thực, kỹ năng thu h&uacute;t v&agrave;&nbsp;truyền tải c&aacute;c th&ocirc;ng điệp đến&nbsp;người xem... đều cần thiết đối với cuộc thi lần n&agrave;y.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Mukbang - Here We Go</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>00:01:18</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Mukbang - Here We Go</p>\r\n\r\n<h2>Th&aacute;nh Ăn Đại Chiến - cuộc chiến của những &quot;th&aacute;nh ăn&quot; thật sự</h2>\r\n\r\n<p>Đ&uacute;ng như &yacute; nghĩa của &quot;Th&aacute;nh Ăn Đại Chiến&quot; - một cuộc chiến giữa c&aacute;c &quot;th&aacute;nh ăn&quot; để chọn ra người xuất sắc nhất, th&igrave; những thử th&aacute;ch m&agrave; c&aacute;c th&iacute; sinh phải trải qua cũng kh&ocirc;ng hề dễ d&agrave;ng.</p>\r\n\r\n<p><strong><em>V&ograve;ng 1 (12/6 - 23/6): Thử Th&aacute;ch Cực Đại</em></strong></p>\r\n\r\n<p>V&ograve;ng 1 bao gồm 3 thử th&aacute;ch li&ecirc;n ho&agrave;n:&nbsp;<strong>Th&egrave;m g&igrave; c&oacute; nấy, Ăn c&ugrave;ng thần tượng v&agrave; Mukbang Wannabe</strong>.&nbsp;C&aacute;c thử th&aacute;ch n&agrave;y v&ocirc; c&ugrave;ng vui nhộn nhưng cũng kh&ocirc;ng k&eacute;m phần h&oacute;c b&uacute;a. V&ograve;ng n&agrave;y đ&ograve;i hỏi người&nbsp;chơi phải sử dụng tất cả vốn kiến thức, kỹ năng cũng như kinh nghiệm m&agrave; m&igrave;nh c&oacute; về lĩnh vực ẩm thực, qua đ&oacute; bộc lộ t&agrave;i năng trở th&agrave;nh &quot;th&aacute;nh ăn&quot; của m&igrave;nh.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Current Time0:00</p>\r\n\r\n<p>/</p>\r\n\r\n<p>Duration3:03</p>\r\n\r\n<p>Auto</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ăn c&ugrave;ng thần tượng - một trong những thử th&aacute;ch v&ocirc; c&ugrave;ng th&uacute; vị của v&ograve;ng 1.</p>\r\n\r\n<p><em><strong>V&ograve;ng 2 (10/7 - 14/7): Th&aacute;nh ăn đại chiến</strong></em></p>\r\n\r\n<p>Sau khi đ&atilde; kết th&uacute;c v&ograve;ng 1, những th&iacute; sinh xuất sắc nhất sẽ được lựa chọn để v&agrave;o v&ograve;ng 2, l&ecirc;n đường trải nghiệm v&agrave; thể hiện tất cả những t&agrave;i năng m&agrave; m&igrave;nh c&oacute; để c&oacute; thể trở th&agrave;nh một &quot;th&aacute;nh ăn&quot; thật sự.</p>\r\n\r\n<p>Th&iacute; sinh vừa đi trải nghiệm ẩm thực địa phương của nước bạn, đồng thời đ&oacute;ng vai tr&ograve; đại sứ quảng b&aacute; cho ẩm thực Việt lan tỏa đến bạn b&egrave; quốc tế. Nhiệm vụ của v&ograve;ng n&agrave;y l&agrave; ho&agrave;n&nbsp;th&agrave;nh 1 b&agrave;i viết được tr&igrave;nh b&agrave;y dưới dạng Emagazine c&ugrave;ng 1 video thể hiện được một c&aacute;ch s&aacute;ng tạo v&agrave; s&acirc;u sắc nhất nhiệm vụ được đề ra.</p>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/6/14/hwg2-artboard-1-copy-6-15605287040601738418011.jpg\" target=\"_blank\"><img alt=\"Thách các thánh ham ăn hiểu đúng ý nghĩa Thánh Ăn Đại Chiến của Here We Go năm nay - Ảnh 5.\" src=\"https://kenh14cdn.com/2019/6/14/hwg2-artboard-1-copy-6-15605287040601738418011.jpg\" /></a></p>\r\n\r\n<p>Thể hiện vốn kiến thức s&acirc;u rộng về ẩm thực, kỹ năng du lịch v&agrave; tinh thần x&ecirc; dịch của bản th&acirc;n, vừa đi vừa trải nghiệm, vừa c&oacute; thể quảng b&aacute; ẩm thực nước nh&agrave; v&agrave; mang về những giải thưởng thật lớn với tổng trị gi&aacute; l&ecirc;n tới 600 triệu đồng, c&ograve;n chần chờ g&igrave; m&agrave; kh&ocirc;ng gửi b&agrave;i tham dự cuộc thi ngay th&ocirc;i nhỉ?</p>\r\n\r\n<p><a href=\"https://herewego.kenh14.vn/\" target=\"_blank\"><img alt=\"Thách các thánh ham ăn hiểu đúng ý nghĩa Thánh Ăn Đại Chiến của Here We Go năm nay - Ảnh 6.\" src=\"https://kenh14cdn.com/2019/6/13/hwg-artboard-1-copy-4-15604045364471692381029-1560440720077649845486.jpg\" /></a></p>\r\n\r\n<p>Năm 2019, Here We Go trở lại với phi&ecirc;n bản đặc biệt nhất từ trước đến nay với chủ đề Th&aacute;nh Ăn Đại Chiến - một cuộc chiến ẩm thực b&ugrave;ng nổ, m&atilde;n nh&atilde;n d&agrave;nh cho c&aacute;c t&iacute;n đồ ẩm thực, du lịch.</p>\r\n\r\n<p>Here We Go m&ugrave;a 4 đ&atilde; ch&iacute;nh thức diễn ra từ ng&agrave;y 12/6 đến ng&agrave;y 23/7 với 2 v&ograve;ng thi v&ocirc; c&ugrave;ng gay cấn để t&igrave;m ra đại sứ ẩm thực Việt Nam. C&ugrave;ng theo d&otilde;i những chuyến du lịch ẩm thực ấn tượng theo c&aacute;ch ri&ecirc;ng của c&aacute;c bạn trẻ tại&nbsp;<a href=\"https://herewego.kenh14.vn/\" target=\"_blank\">https://herewego.kenh14.vn</a>&nbsp;nh&eacute;!</p>\r\n', 'Một phiên bản vô cùng độc đáo, lần đầu tiên xuất hiện tại cuộc thi Here We Go: Thánh Ăn Đại Chiến. Thế nhưng, \"Thánh Ăn Đại Chiến\" thật sự là gì?', NULL, NULL, NULL);
INSERT INTO `baiviet` VALUES (77, '/public/images/banhtrang3-1560524366477977915740.png', 'Bà Tân Vlog thử làm bánh tráng trộn Sài Gòn và đây chính là kết quả', 2, 3, NULL, 'Bà Tân Vlog', '2019-06-17', '<p>Ở những v&ugrave;ng qu&ecirc; miền Bắc như gia đ&igrave;nh b&agrave; T&acirc;n, b&aacute;nh tr&aacute;ng trộn c&oacute; lẽ vẫn l&agrave; m&oacute;n ăn rất mới lạ m&agrave; kh&ocirc;ng phải ai cũng từng được ăn. Cuộc đời gần 60 nồi b&aacute;nh chưng rồi nhưng đ&acirc;y mới l&agrave; lần đầu ti&ecirc;n b&agrave; T&acirc;n thử l&agrave;m m&oacute;n n&agrave;y n&ecirc;n b&agrave; rất k&igrave; vọng m&igrave;nh c&oacute; thể l&agrave;m ngon như b&aacute;nh tr&aacute;ng trộn của S&agrave;i G&ograve;n.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Current Time0:00</p>\r\n\r\n<p>/</p>\r\n\r\n<p>Duration13:12</p>\r\n\r\n<p>Auto</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>B&agrave; T&acirc;n Vlog thử l&agrave;m b&aacute;nh tr&aacute;ng trộn S&agrave;i G&ograve;n v&agrave; đ&acirc;y ch&iacute;nh l&agrave; kết quả (Nguồn: B&agrave; T&acirc;n Vlog).</p>\r\n\r\n<p>B&aacute;nh tr&aacute;ng trộn l&agrave; m&oacute;n ăn vặt c&oacute; nguồn gốc từ T&acirc;y Ninh. Ban đầu, người ta tận dụng những mẩu vụn được cắt ra từ những l&ograve; b&aacute;nh tr&aacute;ng m&aacute;y, trộn đều với ch&uacute;t dầu, h&agrave;nh phi, muối ớt v&agrave; bột t&ocirc;m để ăn. Dần dần, khi m&oacute;n ăn trở n&ecirc;n quen thuộc hơn v&agrave; được nhiều người y&ecirc;u th&iacute;ch, b&aacute;nh tr&aacute;ng trộn mới được biến tấu th&ecirc;m c&aacute;c nguy&ecirc;n liệu kh&aacute;c như trứng c&uacute;t, thịt b&ograve; kh&ocirc;... để m&oacute;n ăn thơm ngon, hấp dẫn hơn. Hiện nay, b&aacute;nh tr&aacute;ng trộn được b&aacute;n rong tr&ecirc;n khắp c&aacute;c vỉa h&egrave; ở S&agrave;i G&ograve;n v&agrave; trở th&agrave;nh m&oacute;n qu&agrave; vặt quen thuộc của nhiều người, nhất l&agrave; học sinh, sinh vi&ecirc;n. Ở H&agrave; Nội, b&aacute;nh tr&aacute;ng trộn cũng được b&aacute;n ở một số cửa tiệm v&agrave; cũng được rất nhiều bạn trẻ y&ecirc;u th&iacute;ch.</p>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/6/14/banh1-15605246508261608285684.jpg\"><img alt=\"B&amp;#224; T&amp;#226;n Vlog thử l&amp;#224;m b&amp;#225;nh tr&amp;#225;ng trộn S&amp;#224;i G&amp;#242;n v&amp;#224; đ&amp;#226;y ch&amp;#237;nh l&amp;#224; kết quả - Ảnh 2.\" src=\"https://kenh14cdn.com/thumb_w/660/2019/6/14/banh1-15605246508261608285684.jpg\" /></a></p>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/6/14/banh2-15605246508331753415176.jpg\"><img alt=\"B&amp;#224; T&amp;#226;n Vlog thử l&amp;#224;m b&amp;#225;nh tr&amp;#225;ng trộn S&amp;#224;i G&amp;#242;n v&amp;#224; đ&amp;#226;y ch&amp;#237;nh l&amp;#224; kết quả - Ảnh 2.\" src=\"https://kenh14cdn.com/thumb_w/660/2019/6/14/banh2-15605246508331753415176.jpg\" /></a></p>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/6/14/banh3-15605246508371417810079.jpg\"><img alt=\"B&amp;#224; T&amp;#226;n Vlog thử l&amp;#224;m b&amp;#225;nh tr&amp;#225;ng trộn S&amp;#224;i G&amp;#242;n v&amp;#224; đ&amp;#226;y ch&amp;#237;nh l&amp;#224; kết quả - Ảnh 2.\" src=\"https://kenh14cdn.com/thumb_w/660/2019/6/14/banh3-15605246508371417810079.jpg\" /></a></p>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/6/14/banh4-15605246508401858179574.jpg\"><img alt=\"B&amp;#224; T&amp;#226;n Vlog thử l&amp;#224;m b&amp;#225;nh tr&amp;#225;ng trộn S&amp;#224;i G&amp;#242;n v&amp;#224; đ&amp;#226;y ch&amp;#237;nh l&amp;#224; kết quả - Ảnh 2.\" src=\"https://kenh14cdn.com/thumb_w/660/2019/6/14/banh4-15605246508401858179574.jpg\" /></a></p>\r\n\r\n<p>Để l&agrave;m được m&oacute;n n&agrave;y, b&agrave; T&acirc;n đ&atilde; chuẩn bị nguy&ecirc;n liệu rất đầy đủ,&nbsp;từ b&aacute;nh tr&aacute;ng, xo&agrave;i xanh, 150 quả trứng c&uacute;t lộn, rau răm, b&ograve; kh&ocirc; trộn c&ugrave;ng với g&agrave; kh&ocirc;, h&agrave;nh kh&ocirc; v&agrave; gia vị...</p>\r\n\r\n<p>Sau khi gọt xo&agrave;i xanh, đến c&ocirc;ng đoạn th&aacute;i sợi, b&agrave; T&acirc;n sử dụng một &quot;c&ocirc;ng cụ&quot; rất lạ mắt để nạo xo&agrave;i th&agrave;nh sợi. Tuy rằng chiếc nạo kiểu n&agrave;y c&oacute; vẻ lạ với nhiều người, nhưng lại l&agrave; c&aacute;ch l&agrave;m rất quen thuộc ở nhiều v&ugrave;ng n&ocirc;ng th&ocirc;n được những người d&acirc;n nghĩ ra, tự chế, gắn nạo l&ecirc;n khung gỗ để c&ocirc;ng việc th&aacute;i sợi trở n&ecirc;n dễ d&agrave;ng hơn, nhất l&agrave; khi l&agrave;m với số lượng lớn hay tạo n&ecirc;n những m&oacute;n ăn &quot;si&ecirc;u to khổng lồ&quot; như b&agrave; T&acirc;n.</p>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/6/14/banhtrang2-1560524366470732368181.png\" target=\"_blank\"><img alt=\"Bà Tân Vlog thử làm bánh tráng trộn Sài Gòn và đây chính là kết quả - Ảnh 3.\" src=\"https://kenh14cdn.com/2019/6/14/banhtrang2-1560524366470732368181.png\" /></a></p>\r\n\r\n<p>150 quả trứng c&uacute;t c&oacute; vẻ nhiều nhưng cũng kh&ocirc;ng thể l&agrave;m kh&oacute; được b&agrave; T&acirc;n. Số trứng n&agrave;y nhanh ch&oacute;ng được b&oacute;c sạch v&agrave; bổ đ&ocirc;i.</p>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/6/14/banhtrang3-1560524366477977915740.png\" target=\"_blank\"><img alt=\"Bà Tân Vlog thử làm bánh tráng trộn Sài Gòn và đây chính là kết quả - Ảnh 4.\" src=\"https://kenh14cdn.com/2019/6/14/banhtrang3-1560524366477977915740.png\" /></a></p>\r\n\r\n<p>Sau khi chuẩn bị đầy đủ nguy&ecirc;n liệu l&agrave; đến c&ocirc;ng đoạn trộn b&aacute;nh tr&aacute;ng. Lần lượt, từ b&aacute;nh tr&aacute;ng, xo&agrave;i, sa tế cho đến thịt b&ograve; kh&ocirc;, g&agrave; kh&ocirc;, gia vị, trứng c&uacute;t, rau dăm... được cho v&agrave;o v&agrave; đảo đều l&ecirc;n. Rất nhanh ch&oacute;ng, đĩa b&aacute;nh tr&aacute;ng trộn si&ecirc;u to khổng lồ đ&atilde; được b&agrave; T&acirc;n ho&agrave;n th&agrave;nh.</p>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/6/14/banhtrang4-1560524366479615007708.png\" target=\"_blank\"><img alt=\"Bà Tân Vlog thử làm bánh tráng trộn Sài Gòn và đây chính là kết quả - Ảnh 5.\" src=\"https://kenh14cdn.com/2019/6/14/banhtrang4-1560524366479615007708.png\" /></a></p>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/6/14/banhtrang5-1560524366483580358907.png\" target=\"_blank\"><img alt=\"Bà Tân Vlog thử làm bánh tráng trộn Sài Gòn và đây chính là kết quả - Ảnh 6.\" src=\"https://kenh14cdn.com/2019/6/14/banhtrang5-1560524366483580358907.png\" /></a></p>\r\n\r\n<p><strong>GIF</strong>.</p>\r\n\r\n<p>M&oacute;n ăn c&oacute; phần lạ lẫm với những người d&acirc;n ở qu&ecirc; b&agrave; T&acirc;n, c&oacute; lẽ v&igrave; vậy m&agrave; lại được sự y&ecirc;u th&iacute;ch của những người ch&aacute;u, người chị em được b&agrave; T&acirc;n mời đến thưởng thức h&ocirc;m nay.</p>\r\n\r\n<p><strong>GIF</strong>.</p>\r\n\r\n<p><strong>GIF</strong>.</p>\r\n\r\n<p>B&acirc;y giờ th&igrave; h&atilde;y thử đo&aacute;n xem, m&oacute;n tiếp theo b&agrave; T&acirc;n l&agrave;m sẽ l&agrave; g&igrave; nh&eacute;!</p>\r\n', 'Sau nhiều món ăn bắt \"trend\" của giới trẻ thì hôm nay, bà Tân Vlog đã thử làm một món ăn vặt miền Nam rất quen thuộc: bánh tráng trộn.', NULL, NULL, NULL);
INSERT INTO `baiviet` VALUES (78, '/public/images/22-15605006724301535128452.jpg', 'Thế giới các loại sợi của ẩm thực Việt \"đồ sộ\" và đáng tự hào như thế nào, xem đây là biết', 2, 3, NULL, 'Các món ăn dạng sợi', '0000-00-00', '<p>Nếu phải kể về c&aacute;c m&oacute;n sợi của Việt Nam th&igrave; e rằng kể đến ng&agrave;y mai cũng chưa chắc hết. Từ nguy&ecirc;n liệu, h&igrave;nh d&aacute;ng, m&agrave;u sắc, hương vị v&agrave; kết cấu, Việt Nam c&oacute; kh&ocirc;ng biết bao nhi&ecirc;u những m&oacute;n sợi kh&aacute;c nhau. Thậm ch&iacute;, c&ugrave;ng l&agrave; một loại nhưng ở l&agrave;ng x&atilde; kh&aacute;c lại c&oacute; vị kh&aacute;c, ở v&ugrave;ng kh&aacute;c lại mang đặc trưng kh&aacute;c.</p>\r\n\r\n<p>N&oacute;i kh&ocirc;ng ngoa, Việt Nam c&oacute; lẽ l&agrave; một trong số những quốc gia c&oacute; nhiều m&oacute;n ăn dạng sợi nhất, ai thấy kh&ocirc;ng tin th&igrave; h&atilde;y cho họ xem ngay danh s&aacute;ch sau đ&acirc;y nh&eacute;!</p>\r\n\r\n<h3>Phở</h3>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/6/14/1-1560499650714948175218.jpg\"><img alt=\"Thế giới c&amp;#225;c loại sợi của ẩm thực Việt &amp;quot;đồ sộ&amp;quot; v&amp;#224; đ&amp;#225;ng tự h&amp;#224;o như thế n&amp;#224;o, xem đ&amp;#226;y l&amp;#224; biết - Ảnh 1.\" src=\"https://kenh14cdn.com/thumb_w/660/2019/6/14/1-1560499650714948175218.jpg\" /></a></p>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/6/14/2-1560499650715555738751.jpg\"><img alt=\"Thế giới c&amp;#225;c loại sợi của ẩm thực Việt &amp;quot;đồ sộ&amp;quot; v&amp;#224; đ&amp;#225;ng tự h&amp;#224;o như thế n&amp;#224;o, xem đ&amp;#226;y l&amp;#224; biết - Ảnh 1.\" src=\"https://kenh14cdn.com/thumb_w/660/2019/6/14/2-1560499650715555738751.jpg\" /></a></p>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/6/14/3-15604996507181435317537.jpg\"><img alt=\"Thế giới c&amp;#225;c loại sợi của ẩm thực Việt &amp;quot;đồ sộ&amp;quot; v&amp;#224; đ&amp;#225;ng tự h&amp;#224;o như thế n&amp;#224;o, xem đ&amp;#226;y l&amp;#224; biết - Ảnh 1.\" src=\"https://kenh14cdn.com/thumb_w/660/2019/6/14/3-15604996507181435317537.jpg\" /></a></p>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/6/14/4-15604996507201010941074.jpg\"><img alt=\"Thế giới c&amp;#225;c loại sợi của ẩm thực Việt &amp;quot;đồ sộ&amp;quot; v&amp;#224; đ&amp;#225;ng tự h&amp;#224;o như thế n&amp;#224;o, xem đ&amp;#226;y l&amp;#224; biết - Ảnh 1.\" src=\"https://kenh14cdn.com/thumb_w/660/2019/6/14/4-15604996507201010941074.jpg\" /></a></p>\r\n\r\n<p>M&oacute;n sợi nổi tiếng nhất phải nhắc đến chắc chắn l&agrave; phở rồi. H&igrave;nh ảnh b&aacute;nh phở trắng, mềm mại, dai dai l&agrave;m từ bột gạo đ&atilde; tạo n&ecirc;n thương hiệu m&oacute;n ăn Việt độc nhất v&ocirc; nhị m&agrave; to&agrave;n thế giới biết đến. Phở c&oacute; mặt xuy&ecirc;n suốt chiều d&agrave;i dải đất h&igrave;nh chữ S với nhiều phi&ecirc;n bản v&ugrave;ng miền, từ h&agrave;ng vỉa h&egrave; đến nh&agrave; h&agrave;ng lớn. Phở nằm trong danh s&aacute;ch nhất-định-phải-thử đối với bất k&igrave; du kh&aacute;ch n&agrave;o đến Việt Nam lần đầu.</p>\r\n\r\n<p>Thậm ch&iacute;, chỉ mỗi m&oacute;n phở cũng sinh ra biết bao phi&ecirc;n bản độc đ&aacute;o từ phở b&ograve;, phở g&agrave; đến phở x&agrave;o, phở cuốn&hellip;</p>\r\n\r\n<h3>Miến</h3>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/6/14/5-1560499863723420209904.jpg\"><img alt=\"Thế giới c&amp;#225;c loại sợi của ẩm thực Việt &amp;quot;đồ sộ&amp;quot; v&amp;#224; đ&amp;#225;ng tự h&amp;#224;o như thế n&amp;#224;o, xem đ&amp;#226;y l&amp;#224; biết - Ảnh 2.\" src=\"https://kenh14cdn.com/thumb_w/660/2019/6/14/5-1560499863723420209904.jpg\" /></a></p>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/6/14/6-15604998637162058729705.jpg\"><img alt=\"Thế giới c&amp;#225;c loại sợi của ẩm thực Việt &amp;quot;đồ sộ&amp;quot; v&amp;#224; đ&amp;#225;ng tự h&amp;#224;o như thế n&amp;#224;o, xem đ&amp;#226;y l&amp;#224; biết - Ảnh 2.\" src=\"https://kenh14cdn.com/thumb_w/660/2019/6/14/6-15604998637162058729705.jpg\" /></a></p>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/6/14/7-15604998637181371844550.jpg\"><img alt=\"Thế giới c&amp;#225;c loại sợi của ẩm thực Việt &amp;quot;đồ sộ&amp;quot; v&amp;#224; đ&amp;#225;ng tự h&amp;#224;o như thế n&amp;#224;o, xem đ&amp;#226;y l&amp;#224; biết - Ảnh 2.\" src=\"https://kenh14cdn.com/thumb_w/660/2019/6/14/7-15604998637181371844550.jpg\" /></a></p>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/6/14/8-1560499863720742166016.jpg\"><img alt=\"Thế giới c&amp;#225;c loại sợi của ẩm thực Việt &amp;quot;đồ sộ&amp;quot; v&amp;#224; đ&amp;#225;ng tự h&amp;#224;o như thế n&amp;#224;o, xem đ&amp;#226;y l&amp;#224; biết - Ảnh 2.\" src=\"https://kenh14cdn.com/thumb_w/660/2019/6/14/8-1560499863720742166016.jpg\" /></a></p>\r\n\r\n<p>Miến l&agrave; thực phẩm dạng sợi kh&ocirc;, được l&agrave;m từ bột gạo, bột dong, bột đậu xanh hoặc bột sắn. Việt Nam ta c&oacute; nhiều chủng loại miến nhưng hẳn nhiều người từng nghe tới m&oacute;n miến dong trứ danh. Sợi miến l&agrave;m từ bột dong thường dai, trong v&agrave; đặc biệt l&agrave; &iacute;t trương, nở khi ăn. Miến c&oacute; t&iacute;nh ứng dụng cao v&agrave; được người Việt Nam tận dụng để chế tạo ra nhiều phi&ecirc;n bản kh&aacute;c nhau như miến ngan, miến cua, miến lươn, miến x&agrave;o&hellip; đ&ocirc;i khi cũng được l&agrave;m nh&acirc;n của c&aacute;c loại nem r&aacute;n.</p>\r\n\r\n<h3>Hủ tiếu</h3>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/6/14/13-15605002892011899119048.jpg\"><img alt=\"Thế giới c&amp;#225;c loại sợi của ẩm thực Việt &amp;quot;đồ sộ&amp;quot; v&amp;#224; đ&amp;#225;ng tự h&amp;#224;o như thế n&amp;#224;o, xem đ&amp;#226;y l&amp;#224; biết - Ảnh 3.\" src=\"https://kenh14cdn.com/thumb_w/660/2019/6/14/13-15605002892011899119048.jpg\" /></a></p>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/6/14/14-15605002892081289831554.jpg\"><img alt=\"Thế giới c&amp;#225;c loại sợi của ẩm thực Việt &amp;quot;đồ sộ&amp;quot; v&amp;#224; đ&amp;#225;ng tự h&amp;#224;o như thế n&amp;#224;o, xem đ&amp;#226;y l&amp;#224; biết - Ảnh 3.\" src=\"https://kenh14cdn.com/thumb_w/660/2019/6/14/14-15605002892081289831554.jpg\" /></a></p>\r\n\r\n<p>Hủ tiếu tuy kh&ocirc;ng c&oacute; xuất xứ từ Việt Nam (nhiều nguồn cho rằng c&oacute; nguồn gốc từ người Trung Quốc), nhưng c&oacute; một điều kh&ocirc;ng thể tranh c&atilde;i: hủ tiếu sau khi du nhập về Việt Nam c&oacute; những phi&ecirc;n bản mang n&eacute;t đặc trưng kh&ocirc;ng lẫn v&agrave;o đ&acirc;u được. Nhất l&agrave; hủ tiếu ở c&aacute;c v&ugrave;ng miền T&acirc;y s&ocirc;ng nước như hủ tiếu Mỹ Tho, Sa Đ&eacute;c&hellip;</p>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/6/14/15-15605003159191487792705.jpg\" target=\"_blank\"><img alt=\"Thế giới các loại sợi của ẩm thực Việt đồ sộ và đáng tự hào như thế nào, xem đây là biết - Ảnh 4.\" src=\"https://kenh14cdn.com/2019/6/14/15-15605003159191487792705.jpg\" /></a></p>\r\n\r\n<p>M&oacute;n hủ tiếu miền T&acirc;y s&ocirc;ng nước l&agrave;m m&ecirc; mẩn đầu bếp Gordon Ramsay.</p>\r\n\r\n<p>Bằng chứng l&agrave; m&oacute;n hủ tiếu tr&ecirc;n chợ nổi C&aacute;i Răng đ&atilde; từng được đầu bếp nổi tiếng Gordon Ramsay đ&aacute;nh gi&aacute; cao đến mức đưa l&ecirc;n l&agrave;m &quot;đề b&agrave;i&quot; trong chương tr&igrave;nh Master Chef. Hương vị hủ tiếu Việt Nam đặc trưng đến mức nhiều th&iacute; sinh đ&atilde; gặp kh&oacute; khăn trong việc t&aacute;i tạo lại, nhiều người c&ograve;n th&uacute; nhận rằng họ kh&ocirc;ng biết phải bắt đầu từ đ&acirc;u.</p>\r\n\r\n<h3>B&uacute;n</h3>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/6/14/10-1560500009917602215778.jpg\"><img alt=\"Thế giới c&amp;#225;c loại sợi của ẩm thực Việt &amp;quot;đồ sộ&amp;quot; v&amp;#224; đ&amp;#225;ng tự h&amp;#224;o như thế n&amp;#224;o, xem đ&amp;#226;y l&amp;#224; biết - Ảnh 5.\" src=\"https://kenh14cdn.com/thumb_w/660/2019/6/14/10-1560500009917602215778.jpg\" /></a></p>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/6/14/11-15605000099131099922462.jpg\"><img alt=\"Thế giới c&amp;#225;c loại sợi của ẩm thực Việt &amp;quot;đồ sộ&amp;quot; v&amp;#224; đ&amp;#225;ng tự h&amp;#224;o như thế n&amp;#224;o, xem đ&amp;#226;y l&amp;#224; biết - Ảnh 5.\" src=\"https://kenh14cdn.com/thumb_w/660/2019/6/14/11-15605000099131099922462.jpg\" /></a></p>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/6/14/12-15605000162212137609188.jpg\"><img alt=\"Thế giới c&amp;#225;c loại sợi của ẩm thực Việt &amp;quot;đồ sộ&amp;quot; v&amp;#224; đ&amp;#225;ng tự h&amp;#224;o như thế n&amp;#224;o, xem đ&amp;#226;y l&amp;#224; biết - Ảnh 5.\" src=\"https://kenh14cdn.com/thumb_w/660/2019/6/14/12-15605000162212137609188.jpg\" /></a></p>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/6/14/9-15605000099152110656631.jpg\"><img alt=\"Thế giới c&amp;#225;c loại sợi của ẩm thực Việt &amp;quot;đồ sộ&amp;quot; v&amp;#224; đ&amp;#225;ng tự h&amp;#224;o như thế n&amp;#224;o, xem đ&amp;#226;y l&amp;#224; biết - Ảnh 5.\" src=\"https://kenh14cdn.com/thumb_w/660/2019/6/14/9-15605000099152110656631.jpg\" /></a></p>\r\n\r\n<p>&Agrave;, b&uacute;n, một từ th&acirc;n thương đối với mọi người con đất Việt. Chẳng k&eacute;m với phở, b&uacute;n nổi tiếng v&agrave; c&oacute; mặt ở khắp nơi tr&ecirc;n đất nước, thậm ch&iacute; c&ograve;n &quot;x&ocirc;m&quot; hơn với nhiều phi&ecirc;n bản độc đ&aacute;o v&agrave; nổi tiếng. Về cơ bản, người ta thường quen thuộc với m&oacute;n b&uacute;n c&oacute; sợi tr&ograve;n, mềm, được d&ugrave;ng trong c&aacute;c m&oacute;n như b&uacute;n ri&ecirc;u, b&uacute;n c&aacute;, b&uacute;n ốc&hellip; b&ecirc;n cạnh đ&oacute;, c&ograve;n c&oacute; c&aacute;c phi&ecirc;n bản b&uacute;n trộn phổ biến như b&uacute;n thịt x&agrave;o, b&uacute;n thịt nướng&hellip; hoặc c&aacute;c m&oacute;n b&uacute;n kh&ocirc; như b&uacute;n đậu mắm t&ocirc;m.</p>\r\n\r\n<p>C&aacute;c loại b&uacute;n kh&aacute;c nhau theo từng v&ugrave;ng, nổi tiếng c&oacute; b&uacute;n Mạch Tr&agrave;ng hơn 2000 năm tuổi từng được d&ugrave;ng để tiến vua An Dương Vương, hoặc m&oacute;n b&uacute;n b&ograve; Huế với sợi to hơn b&uacute;n b&igrave;nh thường.</p>\r\n\r\n<h3>M&igrave;</h3>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/6/14/16-15605003926121557847893.jpg\"><img alt=\"Thế giới c&amp;#225;c loại sợi của ẩm thực Việt &amp;quot;đồ sộ&amp;quot; v&amp;#224; đ&amp;#225;ng tự h&amp;#224;o như thế n&amp;#224;o, xem đ&amp;#226;y l&amp;#224; biết - Ảnh 6.\" src=\"https://kenh14cdn.com/thumb_w/660/2019/6/14/16-15605003926121557847893.jpg\" /></a></p>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/6/14/17-156050039261556816501.jpg\"><img alt=\"Thế giới c&amp;#225;c loại sợi của ẩm thực Việt &amp;quot;đồ sộ&amp;quot; v&amp;#224; đ&amp;#225;ng tự h&amp;#224;o như thế n&amp;#224;o, xem đ&amp;#226;y l&amp;#224; biết - Ảnh 6.\" src=\"https://kenh14cdn.com/thumb_w/660/2019/6/14/17-156050039261556816501.jpg\" /></a></p>\r\n\r\n<p>M&igrave; Quảng (tr&aacute;i) v&agrave; cao lầu (phải).</p>\r\n\r\n<p>M&igrave; kh&ocirc;ng phải l&agrave; m&oacute;n duy nhất ở Việt Nam mới c&oacute;, nhất l&agrave; c&aacute;c m&oacute;n m&igrave; trứng, m&igrave; ăn liền&hellip; tuy nhi&ecirc;n Việt Nam cũng c&oacute; những phi&ecirc;n bản m&igrave; độc đ&aacute;o, v&iacute; dụ như m&igrave; Quảng v&agrave; cao lầu. M&igrave; Quảng được l&agrave;m bằng bột gạo xay mịn v&agrave; tr&aacute;ng th&agrave;nh từng miếng b&aacute;nh mỏng, trong khi cao lầu được cho l&agrave; c&oacute; cấu tạo gần giống với m&igrave; udon của Nhật Bản. C&oacute; lẽ ch&iacute;nh v&igrave; thế m&agrave; nhiều người thường xếp cao lầu như một loại m&igrave;, tuy nhi&ecirc;n người d&acirc;n ở Hội An (nơi xuất xứ của cao lầu) thường gọi những sợi n&agrave;y l&agrave; &quot;b&aacute;nh&quot; chứ kh&ocirc;ng gọi l&agrave; m&igrave;. B&aacute;nh cao lầu c&oacute; điểm độc đ&aacute;o l&agrave; được ng&acirc;m chung với tro. B&aacute;nh cao lầu truyền thống xưa c&ograve;n nhất định phải được ng&acirc;m với tro đốt từ c&acirc;y ở C&ugrave; Lao Ch&agrave;m mới gọi l&agrave; &quot;đ&uacute;ng vị&quot;.</p>\r\n\r\n<h3>B&aacute;nh canh</h3>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/6/14/18-15605005559777099921.png\"><img alt=\"Thế giới c&amp;#225;c loại sợi của ẩm thực Việt &amp;quot;đồ sộ&amp;quot; v&amp;#224; đ&amp;#225;ng tự h&amp;#224;o như thế n&amp;#224;o, xem đ&amp;#226;y l&amp;#224; biết - Ảnh 7.\" src=\"https://kenh14cdn.com/thumb_w/660/2019/6/14/18-15605005559777099921.png\" /></a></p>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/6/14/19-1560500555975664606139.jpg\"><img alt=\"Thế giới c&amp;#225;c loại sợi của ẩm thực Việt &amp;quot;đồ sộ&amp;quot; v&amp;#224; đ&amp;#225;ng tự h&amp;#224;o như thế n&amp;#224;o, xem đ&amp;#226;y l&amp;#224; biết - Ảnh 7.\" src=\"https://kenh14cdn.com/thumb_w/660/2019/6/14/19-1560500555975664606139.jpg\" /></a></p>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/6/14/20-1560500555979993352007.jpg\"><img alt=\"Thế giới c&amp;#225;c loại sợi của ẩm thực Việt &amp;quot;đồ sộ&amp;quot; v&amp;#224; đ&amp;#225;ng tự h&amp;#224;o như thế n&amp;#224;o, xem đ&amp;#226;y l&amp;#224; biết - Ảnh 7.\" src=\"https://kenh14cdn.com/thumb_w/660/2019/6/14/20-1560500555979993352007.jpg\" /></a></p>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/6/14/21-1560500555981396541884.jpg\"><img alt=\"Thế giới c&amp;#225;c loại sợi của ẩm thực Việt &amp;quot;đồ sộ&amp;quot; v&amp;#224; đ&amp;#225;ng tự h&amp;#224;o như thế n&amp;#224;o, xem đ&amp;#226;y l&amp;#224; biết - Ảnh 7.\" src=\"https://kenh14cdn.com/thumb_w/660/2019/6/14/21-1560500555981396541884.jpg\" /></a></p>\r\n\r\n<p>Thật kh&oacute; để định nghĩa b&aacute;nh canh, khi m&agrave; sợi b&aacute;nh c&oacute; thể được l&agrave;m từ đủ loại nguy&ecirc;n liệu như bột gạo, bột năng, bột m&igrave;, bột sắn hoặc sự pha trộn giữa c&aacute;c loại bột tr&ecirc;n theo nhiều tỷ lệ kh&aacute;c nhau. C&oacute; nhiều kiểu ăn b&aacute;nh canh như b&aacute;nh canh gi&ograve; heo, b&aacute;nh canh c&aacute; l&oacute;c, b&aacute;nh canh t&ocirc;m thịt&hellip; Một số nơi ăn b&aacute;nh canh chung với nui, c&aacute;c tỉnh miền Nam lại c&oacute; m&oacute;n b&aacute;nh canh cốt dừa rất đặc biệt.</p>\r\n\r\n<h3>B&aacute;nh đa</h3>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/6/14/22-15605006724301535128452.jpg\"><img alt=\"Thế giới c&amp;#225;c loại sợi của ẩm thực Việt &amp;quot;đồ sộ&amp;quot; v&amp;#224; đ&amp;#225;ng tự h&amp;#224;o như thế n&amp;#224;o, xem đ&amp;#226;y l&amp;#224; biết - Ảnh 8.\" src=\"https://kenh14cdn.com/thumb_w/660/2019/6/14/22-15605006724301535128452.jpg\" /></a></p>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/6/14/23-15605006724261763332062.jpg\"><img alt=\"Thế giới c&amp;#225;c loại sợi của ẩm thực Việt &amp;quot;đồ sộ&amp;quot; v&amp;#224; đ&amp;#225;ng tự h&amp;#224;o như thế n&amp;#224;o, xem đ&amp;#226;y l&amp;#224; biết - Ảnh 8.\" src=\"https://kenh14cdn.com/thumb_w/660/2019/6/14/23-15605006724261763332062.jpg\" /></a></p>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/6/14/24-15605006724281589759061.jpg\"><img alt=\"Thế giới c&amp;#225;c loại sợi của ẩm thực Việt &amp;quot;đồ sộ&amp;quot; v&amp;#224; đ&amp;#225;ng tự h&amp;#224;o như thế n&amp;#224;o, xem đ&amp;#226;y l&amp;#224; biết - Ảnh 8.\" src=\"https://kenh14cdn.com/thumb_w/660/2019/6/14/24-15605006724281589759061.jpg\" /></a></p>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/6/14/25-1560500672432151015703.jpg\"><img alt=\"Thế giới c&amp;#225;c loại sợi của ẩm thực Việt &amp;quot;đồ sộ&amp;quot; v&amp;#224; đ&amp;#225;ng tự h&amp;#224;o như thế n&amp;#224;o, xem đ&amp;#226;y l&amp;#224; biết - Ảnh 8.\" src=\"https://kenh14cdn.com/thumb_w/660/2019/6/14/25-1560500672432151015703.jpg\" /></a></p>\r\n\r\n<p>Nhắc đến b&aacute;nh đa, c&oacute; lẽ ch&iacute;nh người Việt cũng sẽ hơi &quot;m&ocirc;ng lung&quot; do c&aacute;i t&ecirc;n n&agrave;y d&ugrave;ng để chỉ cả một loại b&aacute;nh tr&aacute;ng nướng gi&ograve;n, c&oacute; m&egrave; đen v&agrave; một loại b&aacute;nh sợi m&agrave;u n&acirc;u được c&aacute;n mỏng, ăn c&ugrave;ng nước d&ugrave;ng c&oacute; ri&ecirc;u cua ở Hải Ph&ograve;ng. B&aacute;nh đa cua Hải Ph&ograve;ng l&agrave; một m&oacute;n dạng sợi rất nổi tiếng v&agrave; l&agrave; đặc sản được du kh&aacute;ch &quot;săn l&ugrave;ng&quot;.&nbsp;&nbsp;</p>\r\n\r\n<h3>B&aacute;nh tằm</h3>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/6/14/30-15605010240941674014135.jpg\"><img alt=\"Thế giới c&amp;#225;c loại sợi của ẩm thực Việt &amp;quot;đồ sộ&amp;quot; v&amp;#224; đ&amp;#225;ng tự h&amp;#224;o như thế n&amp;#224;o, xem đ&amp;#226;y l&amp;#224; biết - Ảnh 9.\" src=\"https://kenh14cdn.com/thumb_w/660/2019/6/14/30-15605010240941674014135.jpg\" /></a></p>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/6/14/31-156050102409272673934.jpg\"><img alt=\"Thế giới c&amp;#225;c loại sợi của ẩm thực Việt &amp;quot;đồ sộ&amp;quot; v&amp;#224; đ&amp;#225;ng tự h&amp;#224;o như thế n&amp;#224;o, xem đ&amp;#226;y l&amp;#224; biết - Ảnh 9.\" src=\"https://kenh14cdn.com/thumb_w/660/2019/6/14/31-156050102409272673934.jpg\" /></a></p>\r\n\r\n<p>Thoạt nh&igrave;n, b&aacute;nh tằm tr&ocirc;ng c&oacute; vẻ giống với b&aacute;nh canh nhưng thực ra lại rất kh&aacute;c đấy. B&aacute;nh tằm l&agrave;m từ bột gạo tẻ, c&oacute; h&igrave;nh dạng trắng v&agrave; hơi ngắn, giống như con tằm n&ecirc;n được gọi l&agrave; b&aacute;nh tằm. B&aacute;nh tằm l&agrave; m&oacute;n ăn c&oacute; xuất xứ ở miền Nam Việt Nam, thường ăn c&ugrave;ng với b&igrave; lợn, th&iacute;nh gạo, rau củ, nước cốt dừa v&agrave; nước mắm chua ngọt.</p>\r\n\r\n<h3>B&aacute;nh hỏi</h3>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/6/14/26-15605009307691352822270.jpg\"><img alt=\"Thế giới c&amp;#225;c loại sợi của ẩm thực Việt &amp;quot;đồ sộ&amp;quot; v&amp;#224; đ&amp;#225;ng tự h&amp;#224;o như thế n&amp;#224;o, xem đ&amp;#226;y l&amp;#224; biết - Ảnh 10.\" src=\"https://kenh14cdn.com/thumb_w/660/2019/6/14/26-15605009307691352822270.jpg\" /></a></p>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/6/14/27-15605009307651947306567.jpg\"><img alt=\"Thế giới c&amp;#225;c loại sợi của ẩm thực Việt &amp;quot;đồ sộ&amp;quot; v&amp;#224; đ&amp;#225;ng tự h&amp;#224;o như thế n&amp;#224;o, xem đ&amp;#226;y l&amp;#224; biết - Ảnh 10.\" src=\"https://kenh14cdn.com/thumb_w/660/2019/6/14/27-15605009307651947306567.jpg\" /></a></p>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/6/14/28-15605009307731508721830.jpg\"><img alt=\"Thế giới c&amp;#225;c loại sợi của ẩm thực Việt &amp;quot;đồ sộ&amp;quot; v&amp;#224; đ&amp;#225;ng tự h&amp;#224;o như thế n&amp;#224;o, xem đ&amp;#226;y l&amp;#224; biết - Ảnh 10.\" src=\"https://kenh14cdn.com/thumb_w/660/2019/6/14/28-15605009307731508721830.jpg\" /></a></p>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/6/14/29-15605009307781303531872.jpg\"><img alt=\"Thế giới c&amp;#225;c loại sợi của ẩm thực Việt &amp;quot;đồ sộ&amp;quot; v&amp;#224; đ&amp;#225;ng tự h&amp;#224;o như thế n&amp;#224;o, xem đ&amp;#226;y l&amp;#224; biết - Ảnh 10.\" src=\"https://kenh14cdn.com/thumb_w/660/2019/6/14/29-15605009307781303531872.jpg\" /></a></p>\r\n\r\n<p>B&aacute;nh hỏi l&agrave; một m&oacute;n b&aacute;nh v&ocirc; c&ugrave;ng đặc biệt của Việt Nam, với sợi mỏng v&agrave; nhỏ đến mức kh&ocirc;ng thể t&aacute;ch rời m&agrave; phải đan th&agrave;nh miếng. Mỗi sợi b&aacute;nh hỏi c&oacute; khi chỉ nhỏ bằng đường k&iacute;nh một chiếc kim. B&aacute;nh hỏi thường được ăn c&ugrave;ng với thịt heo quay, mỡ h&agrave;nh v&agrave; nước mắm chua ngọt. Đ&acirc;y l&agrave; một m&oacute;n ăn miền Nam phổ biến, thường xuất hiện trong những dịp cưới hỏi.</p>\r\n\r\n<h3>Tạm kết:</h3>\r\n\r\n<p>Kể ra &quot;sương sương&quot; như vậy, nhưng chắc chắn một điều l&agrave; ch&uacute;ng m&igrave;nh vẫn đ&atilde; bỏ s&oacute;t kh&ocirc;ng &iacute;t những c&aacute;i t&ecirc;n ở v&ugrave;ng miền, khu vực kh&aacute;c nhau. Tuy nhi&ecirc;n ch&iacute;nh điều &quot;kh&oacute; kể hết&quot; như vậy mới thể hiện được sự phong ph&uacute;, &quot;đồ sộ&quot; của c&aacute;c m&oacute;n ăn l&agrave;m từ sợi ở Việt Nam. Bạn đọc n&agrave;o thấy ch&uacute;ng m&igrave;nh kể s&oacute;t m&oacute;n n&agrave;o th&igrave; đừng ngại bổ sung nh&eacute;!</p>\r\n', 'Xét riêng về các món ăn dạng sợi, Việt Nam là một trong những nước có nhiều \"chủng loại\" nhất đấy.', NULL, NULL, NULL);
INSERT INTO `baiviet` VALUES (79, '/public/images/photostory-2-copy-7-1560590618590403583561.jpg', 'Cùng đi chén sạch \"bạch tuộc 7 món\" ngay trong lúc vào mùa thu hoạch nhé!', 2, 3, NULL, 'đồ ăn', '2019-06-18', '<p><a href=\"https://kenh14cdn.com/2019/6/15/photostory-2-copy-8-15605906186021751518397.jpg\" target=\"_blank\"><img alt=\"Cùng đi chén sạch bạch tuộc 7 món ngay trong lúc bạch tuộc đang vào mùa thu hoạch nhé! - Ảnh 1.\" src=\"https://kenh14cdn.com/2019/6/15/photostory-2-copy-8-15605906186021751518397.jpg\" /></a></p>\r\n\r\n<hr />\r\n<p><a href=\"https://kenh14cdn.com/2019/6/15/photostory-2-copy-10-156059061861882233245.jpg\" target=\"_blank\"><img alt=\"Cùng đi chén sạch bạch tuộc 7 món ngay trong lúc bạch tuộc đang vào mùa thu hoạch nhé! - Ảnh 3.\" src=\"https://kenh14cdn.com/2019/6/15/photostory-2-copy-10-156059061861882233245.jpg\" /></a></p>\r\n\r\n<hr />\r\n<p><a href=\"https://kenh14cdn.com/2019/6/15/photostory-2-copy-11-1560590618626171410260.jpg\" target=\"_blank\"><img alt=\"Cùng đi chén sạch bạch tuộc 7 món ngay trong lúc bạch tuộc đang vào mùa thu hoạch nhé! - Ảnh 5.\" src=\"https://kenh14cdn.com/2019/6/15/photostory-2-copy-11-1560590618626171410260.jpg\" /></a></p>\r\n\r\n<hr />\r\n<p><a href=\"https://kenh14cdn.com/2019/6/15/photostory-2-copy-7-1560590618590403583561.jpg\" target=\"_blank\"><img alt=\"Cùng đi chén sạch bạch tuộc 7 món ngay trong lúc bạch tuộc đang vào mùa thu hoạch nhé! - Ảnh 7.\" src=\"https://kenh14cdn.com/2019/6/15/photostory-2-copy-7-1560590618590403583561.jpg\" /></a></p>\r\n\r\n<hr />\r\n<p><a href=\"https://kenh14cdn.com/2019/6/15/photostory-2-copy-13-1560590618640417787086.jpg\" target=\"_blank\"><img alt=\"Cùng đi chén sạch bạch tuộc 7 món ngay trong lúc bạch tuộc đang vào mùa thu hoạch nhé! - Ảnh 9.\" src=\"https://kenh14cdn.com/2019/6/15/photostory-2-copy-13-1560590618640417787086.jpg\" /></a></p>\r\n\r\n<hr />\r\n<p><a href=\"https://kenh14cdn.com/2019/6/15/photostory-2-copy-12-1560590618633152172576.jpg\" target=\"_blank\"><img alt=\"Cùng đi chén sạch bạch tuộc 7 món ngay trong lúc bạch tuộc đang vào mùa thu hoạch nhé! - Ảnh 11.\" src=\"https://kenh14cdn.com/2019/6/15/photostory-2-copy-12-1560590618633152172576.jpg\" /></a></p>\r\n\r\n<hr />\r\n<p><a href=\"https://kenh14cdn.com/2019/6/15/photostory-2-copy-9-15605906186101457052500.jpg\" target=\"_blank\"><img alt=\"Cùng đi chén sạch bạch tuộc 7 món ngay trong lúc bạch tuộc đang vào mùa thu hoạch nhé! - Ảnh 13.\" src=\"https://kenh14cdn.com/2019/6/15/photostory-2-copy-9-15605906186101457052500.jpg\" /></a></p>\r\n', 'Với hội cuồng hải sản thì mùa hè luôn luôn là \"mùa kiếm ăn\" vì vô vàn các loại hải sản, ví dụ bạch tuộc, đang trong mùa thu hoạch, vậy thì không thể bỏ lỡ dịp này mà hãy đi tận hưởng danh sách \"bạch tuộc 7 món\" dưới đây', NULL, NULL, 5);
INSERT INTO `baiviet` VALUES (80, '/public/images/photostory-11-copy-27-1560416196945180652080.jpg', 'Những thức uống này nghe thì lạ lẫm với người Hà Nội, chứ ở Sài Gòn mùa hè năm nào cũng bán khắp mọi nẻo đường', 2, 3, NULL, 'Nước uống', '2019-06-16', '<p><img alt=\"Những thức uống này nghe thì lạ lẫm với người Hà Nội, chứ ở Sài Gòn mùa hè năm nào cũng bán khắp mọi nẻo đường - Ảnh 1.\" src=\"https://kenh14cdn.com/thumb_w/660/2019/6/13/photostory-11-copy-23-15604161969691689333056.jpg\" /></p>\r\n\r\n<hr />\r\n<p><img alt=\"Những thức uống này nghe thì lạ lẫm với người Hà Nội, chứ ở Sài Gòn mùa hè năm nào cũng bán khắp mọi nẻo đường - Ảnh 3.\" src=\"https://kenh14cdn.com/thumb_w/660/2019/6/13/photostory-11-copy-24-1560416196952634327167.jpg\" /></p>\r\n\r\n<hr />\r\n<p><img alt=\"Những thức uống này nghe thì lạ lẫm với người Hà Nội, chứ ở Sài Gòn mùa hè năm nào cũng bán khắp mọi nẻo đường - Ảnh 5.\" src=\"https://kenh14cdn.com/thumb_w/660/2019/6/13/photostory-11-copy-25-15604161969591301901548.jpg\" /></p>\r\n\r\n<hr />\r\n<p><img alt=\"Những thức uống này nghe thì lạ lẫm với người Hà Nội, chứ ở Sài Gòn mùa hè năm nào cũng bán khắp mọi nẻo đường - Ảnh 7.\" src=\"https://kenh14cdn.com/thumb_w/660/2019/6/13/photostory-11-copy-26-15604161969642032940505.jpg\" /></p>\r\n\r\n<hr />\r\n<p><img alt=\"Những thức uống này nghe thì lạ lẫm với người Hà Nội, chứ ở Sài Gòn mùa hè năm nào cũng bán khắp mọi nẻo đường - Ảnh 9.\" src=\"https://kenh14cdn.com/thumb_w/660/2019/6/13/photostory-11-copy-27-1560416196945180652080.jpg\" /></p>\r\n\r\n<hr />\r\n<p><img alt=\"Những thức uống này nghe thì lạ lẫm với người Hà Nội, chứ ở Sài Gòn mùa hè năm nào cũng bán khắp mọi nẻo đường - Ảnh 11.\" src=\"https://kenh14cdn.com/thumb_w/660/2019/6/13/photostory-11-copy-28-1560416196949920967344.jpg\" /></p>\r\n\r\n<hr />\r\n<p><img alt=\"Những thức uống này nghe thì lạ lẫm với người Hà Nội, chứ ở Sài Gòn mùa hè năm nào cũng bán khắp mọi nẻo đường - Ảnh 13.\" src=\"https://kenh14cdn.com/thumb_w/660/2019/6/13/photostory-11-copy-29-1560416196972994177538.jpg\" /></p>\r\n', 'Ngoại trừ các hàng trà sữa thì những thức uống dân dã giải nhiệt những ngày nóng bức ở Sài Gòn xem ra có phần hơi khác so với thủ đô đấy nhé.', NULL, NULL, NULL);
INSERT INTO `baiviet` VALUES (81, '/public/images/1-copy-12-15604157047841382910452.png', '4 công thức mix đồ từ các hotgirl Instagram giúp bạn vẫn long lanh trong nắng nóng mùa hè', 4, 7, NULL, 'Instagram', '2019-06-17', '<p><img alt=\"4 công thức mix đồ từ các hotgirl Instagram giúp bạn vẫn long lanh trong nắng nóng mùa hè - Ảnh 1.\" src=\"https://kenh14cdn.com/thumb_w/660/2019/6/13/1-copy-5-1560415704752596977355.png\" /></p>\r\n\r\n<hr />\r\n<p><img alt=\"4 công thức mix đồ từ các hotgirl Instagram giúp bạn vẫn long lanh trong nắng nóng mùa hè - Ảnh 3.\" src=\"https://kenh14cdn.com/thumb_w/660/2019/6/13/1-copy-30-15604157047881023570278.png\" /></p>\r\n\r\n<hr />\r\n<p><img alt=\"4 công thức mix đồ từ các hotgirl Instagram giúp bạn vẫn long lanh trong nắng nóng mùa hè - Ảnh 5.\" src=\"https://kenh14cdn.com/thumb_w/660/2019/6/13/1-copy-8-15604157047691691856823.png\" /></p>\r\n\r\n<hr />\r\n<p><img alt=\"4 công thức mix đồ từ các hotgirl Instagram giúp bạn vẫn long lanh trong nắng nóng mùa hè - Ảnh 7.\" src=\"https://kenh14cdn.com/thumb_w/660/2019/6/13/1-copy-11-15604157047812114589488.png\" /></p>\r\n\r\n<hr />\r\n<p><img alt=\"4 công thức mix đồ từ các hotgirl Instagram giúp bạn vẫn long lanh trong nắng nóng mùa hè - Ảnh 9.\" src=\"https://kenh14cdn.com/thumb_w/660/2019/6/13/1-copy-12-15604157047841382910452.png\" /></p>\r\n', 'Để không trở thành \"nạn nhân\" của mùa hè, bạn nhất định nên học hỏi 4 công thức này.', NULL, NULL, NULL);
INSERT INTO `baiviet` VALUES (82, '/public/images/pasted image 0.png', 'Hàn Quốc: Thầy đánh trò từng là phương pháp giáo dục hợp lý, đổi luật vì vụ bạo hành nghiêm trọng nhưng vẫn gây tranh cãi', 3, 5, NULL, 'Pháp luật', '2019-06-15', '<p>Ngo&agrave;i học sinh m&acirc;u thuẫn với nhau th&igrave; bạo lực học đường c&ograve;n bao gồm lu&ocirc;n cả trường hợp gi&aacute;o vi&ecirc;n đ&aacute;nh học sinh. Hầu hết c&aacute;c nước phương T&acirc;y đều đặt nặng vấn nạn n&agrave;y v&agrave; c&oacute; thể kh&eacute;p v&agrave;o tội h&igrave;nh sự, nhưng một số quốc gia khu vực ch&acirc;u &Aacute; vẫn thường xuy&ecirc;n xảy ra những vụ gi&aacute;o vi&ecirc;n bạo h&agrave;nh học sinh d&ugrave; ch&iacute;nh phủ những nước n&agrave;y đ&atilde; v&agrave; đang nỗ lực hết sức để ngăn chặn. Tại H&agrave;n Quốc, việc gi&aacute;o vi&ecirc;n đ&aacute;nh học sinh trước đ&acirc;y c&ograve;n được xem l&agrave; phương ph&aacute;p gi&aacute;o dục hợp l&yacute;.</p>\r\n\r\n<p><a href=\"https://lh5.googleusercontent.com/c-WxCR3ZCb3lHahvI6Ujelw2AhzjFKy90ONxOb6GuCy_32gPMUixNMRhCAQQEvd54f8eLm0ocIhuUacaIuOOtDy4rZjGPyzFlOw2CEiEx7jabWushC36qgNv0lTuwjR16BFPDkza\" target=\"_blank\"><img alt=\"Hàn Quốc: Thầy đánh trò từng là phương pháp giáo dục hợp lý, đổi luật vì vụ bạo hành nghiêm trọng nhưng vẫn gây tranh cãi - Ảnh 1.\" src=\"https://lh5.googleusercontent.com/c-WxCR3ZCb3lHahvI6Ujelw2AhzjFKy90ONxOb6GuCy_32gPMUixNMRhCAQQEvd54f8eLm0ocIhuUacaIuOOtDy4rZjGPyzFlOw2CEiEx7jabWushC36qgNv0lTuwjR16BFPDkza\" /></a></p>\r\n\r\n<p><a href=\"https://lh5.googleusercontent.com/nG4iUajhKr3yYUDRq9eVuRcQ7MjQg7luLmLpTaUqFXBfmCLDnGdT4NYuAGbyhE5tH7WABogTKp28B4vFuJKEsxzcYLyijo3PXFXq-0nt0iUC_GsFvyEmH-1DzLl_eHOEG16NwUWs\" target=\"_blank\"><img alt=\"Hàn Quốc: Thầy đánh trò từng là phương pháp giáo dục hợp lý, đổi luật vì vụ bạo hành nghiêm trọng nhưng vẫn gây tranh cãi - Ảnh 2.\" src=\"https://lh5.googleusercontent.com/nG4iUajhKr3yYUDRq9eVuRcQ7MjQg7luLmLpTaUqFXBfmCLDnGdT4NYuAGbyhE5tH7WABogTKp28B4vFuJKEsxzcYLyijo3PXFXq-0nt0iUC_GsFvyEmH-1DzLl_eHOEG16NwUWs\" /></a></p>\r\n\r\n<p>Bắt đầu từ thế kỷ thứ 10, những ng&ocirc;i trường l&agrave;ng ở H&agrave;n Quốc chịu ảnh hưởng nặng nề bởi văn h&oacute;a Trung Quốc. Khi đ&oacute;, thầy gi&aacute;o d&ugrave;ng roi đ&aacute;nh học sinh được xem l&agrave; phương ph&aacute;p dạy dỗ v&agrave; răn đe th&ocirc;ng thường. Nhất l&agrave; đối với những lớp c&oacute; sĩ số đ&ocirc;ng th&igrave; phương ph&aacute;p n&agrave;y ph&aacute;t huy hiệu quả cực kỳ cao, gi&uacute;p gi&aacute;o vi&ecirc;n quản l&yacute; học sinh tốt hơn v&agrave; g&oacute;p phần tăng năng suất giảng dạy.</p>\r\n\r\n<p>Tại H&agrave;n Quốc, trừng phạt thể x&aacute;c được chia l&agrave;m 2 loại l&agrave; trực tiếp v&agrave; gi&aacute;n tiếp. Trực tiếp l&agrave; việc gi&aacute;o vi&ecirc;n d&ugrave;ng roi đ&aacute;nh v&agrave;o người học sinh v&agrave; gi&aacute;n tiếp l&agrave; &aacute;p dụng những h&igrave;nh phạt như h&iacute;t đất, chạy v&ograve;ng quanh s&acirc;n trường&hellip; Vậy nhưng, đa số gi&aacute;o vi&ecirc;n chọn &aacute;p dụng phương ph&aacute;p trực tiếp. Năm 1999, gi&aacute;o vi&ecirc;n trường phổ th&ocirc;ng Kukje đề xuất &yacute; kiến nghi&ecirc;m cấm h&agrave;nh vi trừng phạt thể x&aacute;c tại học đường nhưng vấp phải nhiều sự phản đối. Một trong những l&yacute; do được đưa ra l&agrave; th&ocirc;ng qua việc kỷ luật học sinh, gi&aacute;o vi&ecirc;n c&oacute; thể giữ được uy quyền v&agrave; danh dự của m&igrave;nh, từ đ&oacute; dễ d&agrave;ng hơn trong việc dạy dỗ.</p>\r\n\r\n<p><a href=\"https://lh6.googleusercontent.com/9p4Yi2BXNkvM6uHhRN6EBrguV-z8GOTUBz_OJc9YRi_x4qpOX87bS1j6HOqqHRmcEDN-DvmXE8sZlnBdgDwzWf7TM6ndiQ2lOUwfJGd6Bo2g18n0CpQXCFKZ41x_P3mwD0jMC8BC\" target=\"_blank\"><img alt=\"Hàn Quốc: Thầy đánh trò từng là phương pháp giáo dục hợp lý, đổi luật vì vụ bạo hành nghiêm trọng nhưng vẫn gây tranh cãi - Ảnh 3.\" src=\"https://lh6.googleusercontent.com/9p4Yi2BXNkvM6uHhRN6EBrguV-z8GOTUBz_OJc9YRi_x4qpOX87bS1j6HOqqHRmcEDN-DvmXE8sZlnBdgDwzWf7TM6ndiQ2lOUwfJGd6Bo2g18n0CpQXCFKZ41x_P3mwD0jMC8BC\" /></a></p>\r\n\r\n<p><a href=\"https://lh6.googleusercontent.com/xgYwwG5BQuo5OtndbrVozdiv7W81RzY0DwhKW-f2V3mjxDtkNZlrCm7qpXJB-RxT3I8O82ypxPYSBoc_dsYwCh8rcceqxj8iIjGdiRt-5DeYJ_an-4PL_yXITHyc3jQRZTijmh9z\" target=\"_blank\"><img alt=\"Hàn Quốc: Thầy đánh trò từng là phương pháp giáo dục hợp lý, đổi luật vì vụ bạo hành nghiêm trọng nhưng vẫn gây tranh cãi - Ảnh 4.\" src=\"https://lh6.googleusercontent.com/xgYwwG5BQuo5OtndbrVozdiv7W81RzY0DwhKW-f2V3mjxDtkNZlrCm7qpXJB-RxT3I8O82ypxPYSBoc_dsYwCh8rcceqxj8iIjGdiRt-5DeYJ_an-4PL_yXITHyc3jQRZTijmh9z\" /></a></p>\r\n\r\n<p>Trừng phạt thể x&aacute;c gi&aacute;n tiếp thường thấy tr&ecirc;n phim học đường H&agrave;n Quốc.</p>\r\n\r\n<p>Năm 2011, kết quả cuộc khảo s&aacute;t được đăng tr&ecirc;n trang b&aacute;o của Viện h&igrave;nh sự H&agrave;n Quốc, cho thấy c&oacute; đến 94,6% học sinh thừa nhận từng bị gi&aacute;o vi&ecirc;n đ&aacute;nh. M&ugrave;a h&egrave; năm đ&oacute;, vụ việc thầy gi&aacute;o họ Oh đ&aacute;nh học sinh lớp 6 dữ dội ngay tại lớp học nổ ra v&agrave; trở th&agrave;nh đ&ograve;n gi&aacute;ng trực tiếp v&agrave;o hệ thống gi&aacute;o dục H&agrave;n Quốc. Đ&aacute;ng n&oacute;i hơn l&agrave; phản ứng của những đứa trẻ xung quanh v&ocirc; c&ugrave;ng b&igrave;nh thản như thể l&agrave; chuyện &quot;thường ng&agrave;y ở huyện&quot;.</p>\r\n\r\n<p><a href=\"https://lh3.googleusercontent.com/hFXpHWVHSna6q7B81CPx9D4oLi5rS8g_IGlNChC1NLdf87GgNFjoY-Q7YOnk5E2FfRYgUujRbSW9WlvuhW-zsA50Pj9zlB6_YjPrfyttfVlSGGPGwgWDpX4EujvsUPEUmShKWGYP\" target=\"_blank\"><img alt=\"Hàn Quốc: Thầy đánh trò từng là phương pháp giáo dục hợp lý, đổi luật vì vụ bạo hành nghiêm trọng nhưng vẫn gây tranh cãi - Ảnh 5.\" src=\"https://lh3.googleusercontent.com/hFXpHWVHSna6q7B81CPx9D4oLi5rS8g_IGlNChC1NLdf87GgNFjoY-Q7YOnk5E2FfRYgUujRbSW9WlvuhW-zsA50Pj9zlB6_YjPrfyttfVlSGGPGwgWDpX4EujvsUPEUmShKWGYP\" /></a></p>\r\n\r\n<p>Khi đ&oacute;,&nbsp;H&agrave;n Quốc&nbsp;nổ ra tranh c&atilde;i dữ dội, rất nhiều người ủng hộ lệnh cấm thực hiện h&agrave;nh vi trừng phạt thể x&aacute;c ở trường học. Trong khi đ&oacute;, Li&ecirc;n đo&agrave;n gi&aacute;o vi&ecirc;n nước n&agrave;y lại phản biện rằng gi&aacute;o vi&ecirc;n phải sử dụng phương ph&aacute;p răn đe để kỷ luật học sinh. Những người n&agrave;y c&ograve;n đổ lỗi cho việc thiếu hụt nh&acirc;n sự, gi&aacute;o vi&ecirc;n th&ocirc;ng thường phải chủ nhiệm 1 lớp tr&ecirc;n 60 học sinh n&ecirc;n rất kh&oacute; kiểm so&aacute;t nếu như kh&ocirc;ng trừng phạt thể x&aacute;c. Họ khẳng định c&ocirc;ng t&aacute;c giảng dạy sẽ diễn ra ho&agrave;n to&agrave;n tốt đẹp nếu như ch&iacute;nh phủ chịu chi tiền để thu&ecirc; đủ gi&aacute;o vi&ecirc;n.</p>\r\n\r\n<p>Th&aacute;ng 11/2011, ph&ograve;ng gi&aacute;o dục Seoul đ&atilde; ban h&agrave;nh lệnh cấm h&agrave;nh vi trừng phạt thể x&aacute;c, theo sau đ&oacute; l&agrave; c&aacute;c khu vực l&acirc;n cận như tỉnh Bắc Seolla cũng nghi&ecirc;m t&uacute;c chấp h&agrave;nh. Một trong những người đồng t&igrave;nh với ch&iacute;nh s&aacute;ch n&agrave;y l&agrave; thầy gi&aacute;o Dong Hoon Chan, th&agrave;nh vi&ecirc;n Hội li&ecirc;n hiệp gi&aacute;o vi&ecirc;n H&agrave;n Quốc, cho biết việc kỷ luật học sinh bằng c&aacute;ch bắt ch&uacute;ng v&agrave;o ph&ograve;ng tự vấn hoặc 1 buổi leo n&uacute;i c&ugrave;ng gi&aacute;o vi&ecirc;n cũng c&oacute; thể gi&uacute;p cải thiện mối quan hệ thầy tr&ograve;, đồng thời tăng khả năng tương t&aacute;c giữa 2 b&ecirc;n. Anh c&ograve;n đề nghị Bộ gi&aacute;o dục cần thảo luận đưa ra nhiều phương ph&aacute;p quản l&yacute; học sinh d&agrave;nh cho gi&aacute;o vi&ecirc;n, thay v&igrave; sử dụng bạo lực như trước đ&acirc;y.</p>\r\n\r\n<p>Một v&agrave;i học sinh phản đối lệnh cấm v&agrave; cho rằng n&oacute; kh&ocirc;ng kh&aacute;c g&igrave; tr&ograve; đ&ugrave;a. Em Park Bum Jun chia sẻ nhiều bạn học của m&igrave;nh đ&atilde; lợi dụng điều luật n&agrave;y để đ&aacute;nh lại gi&aacute;o vi&ecirc;n.</p>\r\n\r\n<p>&quot;<em>Sau khi điều luật được ban h&agrave;nh, nhiều học sinh v&ocirc; tư thực hiện h&agrave;nh vi bạo lực với gi&aacute;o vi&ecirc;n v&igrave; tin rằng ch&uacute;ng sẽ kh&ocirc;ng bao giờ bị kh&eacute;p tội. T&ocirc;i biết điều luật được đưa ra l&agrave; bảo vệ quyền con người của học sinh nhưng nhiều gi&aacute;o vi&ecirc;n giờ đ&acirc;y cũng chịu nhiều tổn thương</em>&quot; - Park n&oacute;i.</p>\r\n\r\n<p><a href=\"https://lh4.googleusercontent.com/EfXJsxWDBcshzmRWNjxtvTOxWoKyLzcBemjUcgNXmIlU5PvxZpTB5kMkuJvjo75jTCXpKsLnM2sq1VHqZE1yxrXZ0JB__e8wuef0JflrcTtj-Dgda6pTKxTBxcta7xUndPq16YUj\" target=\"_blank\"><img alt=\"Hàn Quốc: Thầy đánh trò từng là phương pháp giáo dục hợp lý, đổi luật vì vụ bạo hành nghiêm trọng nhưng vẫn gây tranh cãi - Ảnh 6.\" src=\"https://lh4.googleusercontent.com/EfXJsxWDBcshzmRWNjxtvTOxWoKyLzcBemjUcgNXmIlU5PvxZpTB5kMkuJvjo75jTCXpKsLnM2sq1VHqZE1yxrXZ0JB__e8wuef0JflrcTtj-Dgda6pTKxTBxcta7xUndPq16YUj\" /></a></p>\r\n\r\n<p>Một học sinh kh&aacute;c l&agrave; Lee Chun Joo cũng đồng t&igrave;nh v&agrave; cho rằng đất nước m&igrave;nh lu&ocirc;n cần một biện ph&aacute;p trừng trị để thay đổi c&aacute;ch h&agrave;nh xử của một v&agrave;i học sinh kh&oacute; dạy. Người n&agrave;y cũng tin rằng gi&aacute;o vi&ecirc;n đ&aacute;nh học sinh l&agrave; v&igrave; muốn tốt cho c&aacute;c em, tất cả đều v&igrave; tương lai của c&aacute;c em. Điều n&agrave;y đ&atilde; tồn tại trong văn h&oacute;a H&agrave;n Quốc từ rất l&acirc;u.</p>\r\n\r\n<p>Năm 2015, một cuộc khảo s&aacute;t được thực hiện tr&ecirc;n 21 ngh&igrave;n học sinh th&agrave;nh phố Seoul cho ra kết quả 20% đứa trẻ thừa nhận từng bị gi&aacute;o vi&ecirc;n bạo h&agrave;nh. Đ&acirc;y l&agrave; một con số đ&aacute;ng quan ngại, th&ocirc;i th&uacute;c ch&iacute;nh phủ H&agrave;n Quốc phải đưa ra nhiều biện ph&aacute;p hơn để giải quyết vấn nạn, đồng thời đảm bảo quyền v&agrave; nghĩa vụ của học sinh lẫn gi&aacute;o vi&ecirc;n.</p>\r\n\r\n<p><em>(Nguồn: Tổng hợp)</em></p>\r\n', 'Theo người Hàn Quốc từng quan niệm, việc giáo viên đánh học sinh là phương pháp kỷ luật cần thiết trong nền giáo dục, giúp thầy răn đe trò và nâng cao hiệu quả giảng dạy.', NULL, NULL, 3);
INSERT INTO `baiviet` VALUES (83, '/public/images/screen-shot-2019-05-13-at-15127-pm-1557730298321284279152.png', '\"Thần tượng hóa\" Khá Bảnh và Văn Kính Dương: Từ bao giờ tội phạm coi thường luật pháp lại được tung hô như một huyền thoại?', 3, 5, NULL, 'Pháp luật', '2019-06-15', '<h3>Những con số biết n&oacute;i: 6 &aacute;n tử h&igrave;nh, 3 &aacute;n chung th&acirc;n c&ugrave;ng 20 năm t&ugrave;&nbsp;</h3>\r\n\r\n<p>Phi&ecirc;n t&ograve;a x&eacute;t xử&nbsp;<a href=\"http://kenh14.vn/vu-an-trum-ma-tuy-van-kinh-duong-va-ngoc-miu.html\" target=\"_blank\">Văn K&iacute;nh Dương c&ugrave;ng người t&igrave;nh hot girl Ngọc Miu v&agrave; đồng bọn</a>&nbsp;hiện đang l&agrave; t&acirc;m điểm ch&uacute; &yacute; của dư luận những ng&agrave;y gần đ&acirc;y. Kết th&uacute;c phi&ecirc;n xử chiều 9/5, TAND TP HCM đ&atilde; đề nghị tổng cộng 6 &aacute;n tử h&igrave;nh, 3 &aacute;n chung th&acirc;n v&agrave; 1 &aacute;n t&ugrave; 20 năm d&agrave;nh cho &ocirc;ng tr&ugrave;m n&agrave;y c&ugrave;ng c&aacute;c &quot;ch&acirc;n rết&quot; trong đường d&acirc;y với c&aacute;c tội danh sản xuất, mua b&aacute;n v&agrave; t&agrave;ng trữ tr&aacute;i ph&eacute;p chất ma t&uacute;y.</p>\r\n\r\n<p>L&agrave; đối tượng cầm đầu đường d&acirc;y, Văn K&iacute;nh Dương cũng l&agrave; kẻ bị truy tố nhiều nhất với tổng cộng 5 tội danh. Trong đ&oacute;, 10-14 năm t&ugrave; cho c&aacute;c tội &quot;trốn khỏi nơi giam giữ&quot;, &quot;l&agrave;m giả con dấu t&agrave;i liệu cơ quan, tổ chức&quot; v&agrave; &quot;lừa đảo chiếm đoạt t&agrave;i sản&quot;; tử h&igrave;nh về tội &quot;sản xuất tr&aacute;i ph&eacute;p chất ma t&uacute;y&quot; v&agrave; &quot;mua b&aacute;n tr&aacute;i ph&eacute;p chất ma t&uacute;y&quot;. Tổng h&igrave;nh phạt l&agrave; tử h&igrave;nh.</p>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/5/12/batch597816053497642592204672576014761577676800n-155737358090429091961-1557680361119705579031.jpg\" target=\"_blank\"><img alt=\"Thần tượng hóa Khá Bảnh và Văn Kính Dương: Từ bao giờ tội phạm coi thường luật pháp lại được tung hô như một huyền thoại? - Ảnh 1.\" src=\"https://kenh14cdn.com/2019/5/12/batch597816053497642592204672576014761577676800n-155737358090429091961-1557680361119705579031.jpg\" /></a></p>\r\n\r\n<p>Văn K&iacute;nh Dương vẫn giữ th&aacute;i độ b&igrave;nh thản, thậm ch&iacute; l&agrave; cười d&ugrave; bị đề nghị &aacute;n tử h&igrave;nh.</p>\r\n\r\n<p>Trước khi bị bắt v&igrave; cầm đầu đường d&acirc;y ma t&uacute;y khủng, d&ugrave; đang trong thời gian trốn trại, Dương vẫn tiếp tục l&ocirc;i k&eacute;o c&aacute;c bị c&aacute;o l&agrave; người th&acirc;n, bạn b&egrave; để c&ugrave;ng nhau tạo n&ecirc;n hệ thống sản xuất v&agrave; mua b&aacute;n ma t&uacute;y với quy m&ocirc; cực lớn. Văn K&iacute;nh Dương c&ugrave;ng đồng bọn đ&atilde; lập nhiều xưởng sản xuất ở nhiều địa điểm kh&aacute;c nhau rồi mang đi ti&ecirc;u thụ khắp c&aacute;c tỉnh th&agrave;nh.</p>\r\n\r\n<p>Chỉ trong một thời gian ngắn, đường d&acirc;y của Dương đ&atilde; cho &quot;xuất xưởng&quot; tới 120 kg ma t&uacute;y, thực hiện tr&oacute;t lọt hơn 20 lượt mua b&aacute;n, thu về h&agrave;ng tỷ đồng. Tới khi đường d&acirc;y của Dương bị cảnh s&aacute;t triệt hạ, số ma t&uacute;y c&ograve;n lại chỉ vỏn vẹn 18 kg.</p>\r\n\r\n<p>Những con số ở tr&ecirc;n thực sự biết n&oacute;i, khiến người ta chẳng cần phải chờ đến ng&agrave;y 14/5 để nghe t&ograve;a tuy&ecirc;n &aacute;n cũng dễ d&agrave;ng nhận ra, với Dương, chỉ c&oacute; thể l&agrave; h&igrave;nh phạt cao nhất. D&ugrave; như thế c&oacute; lẽ cũng chưa đủ. Thử hỏi với hơn 1 tạ ma t&uacute;y đ&atilde; b&aacute;n ra, đ&atilde; bao nhi&ecirc;u &quot;c&aacute;i chết trắng&quot; được &ocirc;ng tr&ugrave;m n&agrave;y reo rắc?</p>\r\n\r\n<h3>Một t&ecirc;n tội phạm lọc l&otilde;i chuy&ecirc;n gieo rắc &quot;c&aacute;i chết trắng&quot; với th&aacute;i độ coi thường ph&aacute;p luật được tung h&ocirc; như một huyền thoại?</h3>\r\n\r\n<p>Qu&aacute; khứ bất hảo, tội chồng l&ecirc;n tội, &aacute;n chất l&ecirc;n &aacute;n cho thấy Dương l&agrave; đối tượng l&igrave; lợm, lọc l&otilde;i v&agrave; coi thường ph&aacute;p luật. Th&aacute;i độ thản nhi&ecirc;n của Dương trong suốt qu&aacute; tr&igrave;nh x&eacute;t xử cho thấy y kh&ocirc;ng hề ăn năn, hối lỗi, thể hiện y l&agrave; đối tượng đặc biệt nguy hiểm.</p>\r\n\r\n<p>Ấy vậy m&agrave; chỉ bằng v&agrave;i c&acirc;u n&oacute;i, h&agrave;nh động kiểu &quot;anh h&ugrave;ng nửa m&ugrave;a&quot; m&agrave; thực chất thể hiện sự thiếu th&agrave;nh khẩn h&ograve;ng bao che cho người t&igrave;nh v&agrave; đồng bọn, &ocirc;ng tr&ugrave;m ma t&uacute;y kh&eacute;t tiếng n&agrave;y lại đang được kh&ocirc;ng &iacute;t người tung h&ocirc; như một huyền thoại. Một post hiện đang được chia sẻ mạnh mẽ tr&ecirc;n MXH thậm ch&iacute; c&ograve;n n&oacute;i về Dương như một &quot;thần tượng giới trẻ&quot;, d&aacute;m chấp nhận hậu quả xấu nhất v&agrave; l&agrave; &quot;con người lu&ocirc;n hướng đến điều cao cả, tốt đẹp...?!&quot;</p>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/5/12/screen-shot-2019-05-12-at-222908-15576749609251934962467-155768024811158176490.png\"><img alt=\"Thần tượng hóa Khá Bảnh và Văn Kính Dương: Từ bao giờ tội phạm coi thường luật pháp lại được tung hô như một huyền thoại? - Ảnh 2.\" src=\"https://kenh14cdn.com/2019/5/12/screen-shot-2019-05-12-at-222908-15576749609251934962467-155768024811158176490.png\" /></a></p>\r\n\r\n<p>B&agrave;i đăng của một cư d&acirc;n mạng với những &quot;lời c&oacute; c&aacute;nh&quot; d&agrave;nh cho tr&ugrave;m ma t&uacute;y Văn K&iacute;nh Dương.</p>\r\n\r\n<p>B&ecirc;n cạnh những lời c&oacute; c&aacute;nh d&agrave;nh cho &quot;thần tượng&quot;, chủ nh&acirc;n b&agrave;i viết c&ograve;n tranh thủ thể hiện sự &quot;hiểu biết&quot; bằng việc liệt k&ecirc; khối t&agrave;i sản khủng h&agrave;ng trăm tỷ của Văn K&iacute;nh Dương c&ugrave;ng h&agrave;ng loạt si&ecirc;u xe m&agrave; &ocirc;ng tr&ugrave;m n&agrave;y sở hữu với những c&aacute;i t&ecirc;n đ&igrave;nh đ&aacute;m như Lamborghini, McLaren...&nbsp;Tuy nhi&ecirc;n, một sự thật hiển nhi&ecirc;n dường như lại nằm ngo&agrave;i hiểu biết n&agrave;y, đ&oacute; l&agrave; tất cả những thứ xa hoa kể tr&ecirc;n đều được mua bằng tiền phạm ph&aacute;p từ hoạt động ma t&uacute;y.&nbsp;</p>\r\n\r\n<p>Chưa hết, người n&agrave;y c&ograve;n cho hay, &ocirc;ng tr&ugrave;m họ Văn đ&atilde; ch&iacute;nh thức được ghi danh v&agrave;o &quot;Wiki B&aacute;ch Khoa từ điển thế giới&quot; (!?) v&igrave; &quot;th&agrave;nh t&iacute;ch&quot; khủng với đường d&acirc;y ma t&uacute;y của m&igrave;nh, đồng thời &quot;t&aacute;i hiện lại lịch sử thế giới&quot;, s&aacute;nh ngang với &ocirc;ng tr&ugrave;m ma t&uacute;y kh&eacute;t tiếng người Colombia - Pablo&nbsp;Escobar!</p>\r\n\r\n<p>Ngay sau khi được đăng tải, b&agrave;i viết với những tuy&ecirc;n bố g&acirc;y sốc đ&atilde; lập tức thu h&uacute;t sự quan t&acirc;m của đ&ocirc;ng đảo cư d&acirc;n mạng v&agrave; nhanh ch&oacute;ng được chia sẻ tới gần 2 ngh&igrave;n lượt. Đ&aacute;ng ch&uacute; &yacute;, kh&ocirc;ng &iacute;t trong số đ&oacute; thể sự đồng t&igrave;nh với quan điểm của chủ nh&acirc;n b&agrave;i viết.</p>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/5/12/screen-shot-2019-05-12-at-230533-15576772407351840572306-15576773599721600052077.png\" target=\"_blank\"><img alt=\"Thần tượng hóa Khá Bảnh và Văn Kính Dương: Từ bao giờ tội phạm coi thường luật pháp lại được tung hô như một huyền thoại? - Ảnh 3.\" src=\"https://kenh14cdn.com/2019/5/12/screen-shot-2019-05-12-at-230533-15576772407351840572306-15576773599721600052077.png\" /></a></p>\r\n\r\n<p>H&agrave;ng loạt cư d&acirc;n mạng chia sẻ b&agrave;i viết với sự ủng hộ, cổ s&uacute;y.</p>\r\n\r\n<h3>Đừng tạo n&ecirc;n những thần tượng bất hảo chỉ v&igrave; say đắm c&aacute;i gọi l&agrave; &quot;t&igrave;nh nghĩa anh em trong giang hồ&quot;</h3>\r\n\r\n<p>Chẳng ri&ecirc;ng g&igrave; những th&agrave;nh vi&ecirc;n t&iacute;ch cực của &quot;fan club&quot; Văn K&iacute;nh Dương, hay d&acirc;n chơi Kh&aacute; Bảnh trước đ&acirc;y, mỗi người ch&uacute;ng ta đều rất dễ động l&ograve;ng trước một h&agrave;nh động nghĩa hiệp của người kh&aacute;c, bất kể họ l&agrave; ai. Rồi cứ thế tự m&igrave;nh h&igrave;nh tượng h&oacute;a, ng&ocirc;n t&igrave;nh h&oacute;a n&oacute; l&ecirc;n m&agrave; qu&ecirc;n đi bản chất thực sự của vấn đề.&nbsp;</p>\r\n\r\n<p>Văn K&iacute;nh Dương &quot;bảo vệ&quot; người t&igrave;nh v&agrave; đồng phạm của m&igrave;nh trước quan t&ograve;a, li&ecirc;n tục bao che chối tội lại được xem l&agrave; h&agrave;nh động đ&aacute;ng mặt đ&agrave;n &ocirc;ng, l&agrave; người &quot;ch&iacute;nh nghĩa&quot; qu&ecirc;n m&igrave;nh v&igrave; t&igrave;nh, v&igrave; bằng hữu. Nụ cười của Văn K&iacute;nh Dương khi t&ograve;a đề nghị Ngọc Miu bản &aacute;n 20 năm t&ugrave; l&agrave; một chi tiết đặc biệt g&acirc;y ch&uacute; &yacute; với nhiều người, nhưng &quot;ng&ocirc;n t&igrave;nh h&oacute;a&quot; n&oacute; rồi biến &ocirc;ng tr&ugrave;m ma t&uacute;y th&agrave;nh h&igrave;nh tượng &quot;so&aacute;i ca&quot; ho&agrave;n hảo đốn tim ph&aacute;i nữ th&igrave; lại l&agrave; c&acirc;u chuyện kh&aacute;c.</p>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/5/13/screen-shot-2019-05-13-at-15127-pm-1557730298321284279152.png\"><img alt=\"Thần tượng hóa Khá Bảnh và Văn Kính Dương: Từ bao giờ tội phạm coi thường luật pháp lại được tung hô như một huyền thoại? - Ảnh 4.\" src=\"https://kenh14cdn.com/2019/5/13/screen-shot-2019-05-13-at-15127-pm-1557730298321284279152.png\" /></a></p>\r\n\r\n<p>Kh&aacute; Bảnh - thanh ni&ecirc;n với qu&aacute; khứ bất hảo, ra t&ugrave; v&agrave;o tội từng được tung h&ocirc; l&agrave; &quot;thần tượng giới trẻ&quot;.</p>\r\n\r\n<p>Pablo Escobar - kẻ m&agrave; Dương được đem ra so s&aacute;nh, cho tới tận b&acirc;y giờ vẫn được nhiều người nhắc đến với việc su&yacute;t trở th&agrave;nh tổng thống, từng được coi l&agrave; người gi&agrave;u nhất thế giới với số t&agrave;i sản kếch x&ugrave; l&ecirc;n tới tr&ecirc;n 50 tỷ USD. Người ta cứ mải m&ecirc; nhắc tới sự h&agrave;o ph&oacute;ng của hắn qua những đồng tiền bẩn hắn &quot;bố th&iacute;&quot; cho d&acirc;n ngh&egrave;o, m&agrave; qu&ecirc;n đi rằng hắn c&ograve;n l&agrave; một t&ecirc;n khủng bố, l&agrave; một trong những kẻ giết người t&agrave;n bạo nhất trong lịch sử nh&acirc;n loại.</p>\r\n\r\n<p>Theo thống k&ecirc;, cho tới khi bị cảnh s&aacute;t bắn chết tr&ecirc;n n&oacute;c nh&agrave; ngay giữa thủ phủ Medell&iacute;n - qu&ecirc; nh&agrave; của &ocirc;ng tr&ugrave;m n&agrave;y, Escobar đ&atilde; ra lệnh giết hơn 2800 sĩ quan cảnh s&aacute;t, đồng thời bị buộc chịu tr&aacute;ch nhiệm cho c&aacute;i chết của hơn 1400 người d&acirc;n v&ocirc; tội kh&aacute;c thiệt mạng do ảnh hưởng của c&aacute;c cuộc giao tranh giữa c&aacute;c băng đảng, m&agrave; hắn l&agrave; người hưởng lợi. Đ&acirc;y mới l&agrave; ch&acirc;n dung thực sự về &quot;Don Pablo&quot; được lịch sử nh&igrave;n nhận.</p>\r\n\r\n<p>Thế n&ecirc;n, nếu c&oacute; điểm chung n&agrave;o đ&oacute; giữa Văn K&iacute;nh Dương v&agrave; Pablo Escobar, th&igrave; đ&oacute; chỉ c&oacute; thể l&agrave; c&aacute;i c&aacute;ch m&agrave; ch&uacute;ng l&agrave;m gi&agrave;u bằng thứ &quot;h&agrave;ng trắng&quot; chết ch&oacute;c, tiếp tay l&agrave;m bao nhi&ecirc;u thế hệ thanh ni&ecirc;n sa đ&agrave; v&agrave;o ma t&uacute;y, tội phạm. V&agrave; cả sự hoang tưởng khi tự nghĩ m&igrave;nh l&agrave; doanh nh&acirc;n th&agrave;nh đạt nữa m&agrave; th&ocirc;i.</p>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/5/13/screen-shot-2019-05-13-at-14946-pm-15577301972301221356176.png\"><img alt=\"Thần tượng hóa Khá Bảnh và Văn Kính Dương: Từ bao giờ tội phạm coi thường luật pháp lại được tung hô như một huyền thoại? - Ảnh 5.\" src=\"https://kenh14cdn.com/2019/5/13/screen-shot-2019-05-13-at-14946-pm-15577301972301221356176.png\" /></a></p>\r\n\r\n<p>&quot;T&igrave;nh nghĩa anh em giang hồ&quot; l&agrave; nguồn cơn của rất nhiều vụ &aacute;n đau l&ograve;ng.</p>\r\n\r\n<p>Một nghĩa cử được cho l&agrave; đẹp nhưng thực hiện đằng sau những h&agrave;nh vi xấu, hoặc được d&ugrave;ng để bao biện cho h&agrave;nh vi xấu th&igrave; chẳng c&ograve;n đẹp ch&uacute;t n&agrave;o. Đ&atilde; bao lần v&igrave; những thứ như &quot;t&igrave;nh anh em giang hồ&quot; m&agrave; nảy sinh đ&acirc;m ch&eacute;m, thanh to&aacute;n lẫn nhau, cũng v&igrave; thế m&agrave; g&acirc;y ra &aacute;n mạng đau l&ograve;ng khiến người mất, kẻ vướng v&ograve;ng lao l&yacute;?</p>\r\n\r\n<p>Những h&agrave;nh động kiểu &quot;anh h&ugrave;ng đường c&ugrave;ng&quot; như thế nếu được tung h&ocirc;, cổ s&uacute;y v&agrave; học theo sẽ dần dần tạo ra những Kh&aacute; Bảnh, những Văn K&iacute;nh Dương kh&aacute;c, để lại những hệ lụy xấu m&agrave; cả x&atilde; hội phải g&aacute;nh chịu.&nbsp;</p>\r\n\r\n<p>Ai cũng c&oacute; quyền thể hiện quan điểm c&aacute; nh&acirc;n của m&igrave;nh, nhất l&agrave; ở m&ocirc;i trường mở như MXH. Tuy nhi&ecirc;n, h&atilde;y c&oacute; tr&aacute;ch nhiệm hơn trước mỗi c&uacute; click chuột, để những lượt share, những comment của bạn truyền đi những th&ocirc;ng điệp t&iacute;ch cực gi&uacute;p cuộc sống trở n&ecirc;n tốt đẹp hơn.</p>\r\n', 'Là kẻ cầm đầu đường dây sản xuất, mua bán hàng trăm kg ma túy, đem \"cái chết trắng\" reo rắc khắp các tỉnh thành. Vậy mà chỉ bằng vài hành động kiểu \"anh hùng đường cùng\" nhằm bao che cho đồng bọn, Văn Kính Dương lại được không ít người tung hô như một huyền thoại vì cái gọi là \"nghĩa hiệp\"?', NULL, NULL, 3);
INSERT INTO `baiviet` VALUES (84, '/public/images/photo-1-1555994308260185668585.jpg', 'Thầy giáo bị tố làm nữ sinh lớp 8 mang bầu: Đền 300 triệu xin không đưa ra pháp luật?', 3, 6, NULL, 'Pháp luật', '2019-06-06', '<p>Trao đổi với ph&oacute;ng vi&ecirc;n ng&agrave;y 23/4, &ocirc;ng Nguyễn Thế Dũng, Ph&oacute; Gi&aacute;m đốc Sở GD&amp;ĐT tỉnh L&agrave;o Cai cho biết, s&aacute;ng c&ugrave;ng ng&agrave;y Sở đ&atilde; tổ chức đo&agrave;n c&ocirc;ng t&aacute;c xuống l&agrave;m việc với Ph&ograve;ng Gi&aacute;o dục huyện Bảo Y&ecirc;n v&agrave; x&atilde; Thượng H&agrave; để l&agrave;m r&otilde; th&ocirc;ng tin một thầy gi&aacute;o tại trường THCS tr&ecirc;n địa b&agrave;n bị tố c&aacute;o về h&agrave;nh vi l&agrave;m học sinh lớp 8 mang thai.</p>\r\n\r\n<p>Theo Ph&ograve;ng Gi&aacute;o dục huyện b&aacute;o c&aacute;o l&ecirc;n, hiện nay gi&aacute;o vi&ecirc;n nam bị tố c&aacute;o đ&atilde; bị đ&igrave;nh chỉ c&ocirc;ng t&aacute;c để l&agrave;m r&otilde; vụ việc. C&ograve;n th&ocirc;ng tin cụ thể phải chờ kết quả l&agrave;m việc với cơ sở v&agrave; căn cứ điều tra, gi&aacute;m định từ ph&iacute;a cơ quan c&ocirc;ng an&rdquo;, &ocirc;ng Dũng n&oacute;i.</p>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/4/23/photo-1-1555994308260185668585.jpg\" target=\"_blank\"><img alt=\"Thầy giáo bị tố làm nữ sinh lớp 8 mang bầu: Đền 300 triệu xin không đưa ra pháp luật? - Ảnh 1.\" src=\"https://kenh14cdn.com/2019/4/23/photo-1-1555994308260185668585.jpg\" /></a></p>\r\n\r\n<p>Thầy gi&aacute;o H. - người bị tố l&agrave;m nữ sinh lớp 8 mang bầu. Ảnh gia đ&igrave;nh nạn nh&acirc;n cung cấp</p>\r\n\r\n<p>V&agrave;i ng&agrave;y gần đ&acirc;y, dư luận dấy l&ecirc;n th&ocirc;ng tin gia đ&igrave;nh học sinh H.T.H. (13 tuổi), học sinh lớp 8 của Trường THCS x&atilde; Thượng H&agrave; l&agrave;m đơn tố c&aacute;o thầy gi&aacute;o c&ugrave;ng trường c&oacute; h&agrave;nh vi hiếp d&acirc;m khiến con em m&igrave;nh mang thai. Hiện, thai nhi trong bụng em H. đ&atilde; được 12 tuần tuổi. Ch&iacute;nh quyền địa phương v&agrave; c&aacute;c cơ quan li&ecirc;n quan đều đ&atilde; nắm bắt được th&ocirc;ng tin n&agrave;y.</p>\r\n\r\n<p>Tr&ograve; chuyện với ch&uacute;ng t&ocirc;i, gia đ&igrave;nh nữ sinh N.T.H. (học sinh lớp 8, trường THCS Thượng H&agrave;) cho biết, thầy gi&aacute;o c&oacute; h&agrave;nh vi quan hệ t&igrave;nh dục nhiều lần với ch&aacute;u H. dẫn đến mang thai l&agrave; gi&aacute;o vi&ecirc;n chủ nhiệm.</p>\r\n\r\n<p>Sau khi sự việc vỡ lở, người th&acirc;n của ch&aacute;u H. đ&atilde; bức x&uacute;c chia sẻ sự việc với b&aacute;o ch&iacute;. Theo chị N.T.N. (chị g&aacute;i nữ sinh H.) hiện gia đ&igrave;nh rất hoang mang, lo lắng về vụ việc của ch&aacute;u H..</p>\r\n\r\n<p>Chị N. cho biết, ng&agrave;y 22/4, thấy em g&aacute;i c&oacute; nhiều biểu hiện bất thường n&ecirc;n đ&atilde; mua que thử thai về cho em g&aacute;i thử. Ph&aacute;t hiện em c&oacute; bầu, chị N. tức tốc đưa em H. đến bệnh viện để si&ecirc;u &acirc;m th&igrave; kết quả c&aacute;i thai đ&atilde; 12 tuần tuổi.</p>\r\n\r\n<p>B&agrave;ng ho&agrave;ng trước sự thực n&agrave;y, chị N. c&ugrave;ng gia đ&igrave;nh đưa H. đến cơ quan chức năng tr&igrave;nh b&aacute;o. Biết sự việc, thầy gi&aacute;o A. c&ugrave;ng gia đ&igrave;nh đ&atilde; đến nh&agrave; nữ sinh H. để xin lỗi v&agrave; xin thương lượng. Thầy A. đ&atilde; thừa nhận to&agrave;n bộ h&agrave;nh vi của m&igrave;nh đồng thời muốn được giải quyết vụ việc bằng t&igrave;nh cảm m&agrave; kh&ocirc;ng đưa ra ph&aacute;p luật, nhưng gia đ&igrave;nh chị kh&ocirc;ng đồng &yacute;.</p>\r\n\r\n<p>Theo chị N. trước th&aacute;i độ ki&ecirc;n quyết của gia đ&igrave;nh, người th&acirc;n của thầy A. đ&atilde; đề nghị đưa cho gia đ&igrave;nh số tiền 300 triệu v&agrave; giải quyết vụ việc theo hướng t&igrave;nh cảm m&agrave; kh&ocirc;ng đưa ra ph&aacute;p luật.</p>\r\n\r\n<p>&ldquo;&Yacute; họ l&agrave; đưa cho gia đ&igrave;nh t&ocirc;i số tiền tr&ecirc;n để gi&uacute;p em t&ocirc;i giải quyết c&aacute;i thai. Họ cũng kh&ocirc;ng n&oacute;i r&otilde; khi giải quyết c&aacute;i thai xong thầy gi&aacute;o sẽ như thế n&agrave;o với em t&ocirc;i. Hiện giờ gia đ&igrave;nh t&ocirc;i cũng kh&ocirc;ng biết thế n&agrave;o, chỉ mong ph&aacute;p luật c&ocirc;ng t&acirc;m xử l&yacute; đ&uacute;ng người, đ&uacute;ng tội, trả lại c&ocirc;ng bằng cho em t&ocirc;i&rdquo;, chị N. n&oacute;i.</p>\r\n\r\n<p>Trước đ&acirc;y, gia đ&igrave;nh đ&atilde; ph&aacute;t hiện em H. c&oacute; sử dụng một chiếc điện thoại th&ocirc;ng minh hiệu OPPO. Biết điều n&agrave;y, gia đ&igrave;nh truy hỏi th&igrave; H. cho biết, điện thoại l&agrave; thầy A. cho mượn để &ldquo;thầy hỏi sĩ số lớp&rdquo;.</p>\r\n\r\n<p>Sau đ&oacute;, ch&iacute;nh chị N. đ&atilde; đến gặp thầy A. đề nghị thầy thu lại chiếc điện thoại để em H. tập trung v&agrave;o học h&agrave;nh. Thầy A. sau đ&oacute; c&oacute; lấy lại điện thoại nhưng chỉ được v&agrave;i h&ocirc;m, chị N. lại thấy em g&aacute;i m&igrave;nh sử dụng chiếc điện thoại đ&oacute;.</p>\r\n\r\n<p>Thấy sự bất thường, chị N. truy hỏi th&igrave; H. mới kể lại việc đ&atilde; quan hệ t&igrave;nh dục với thầy A. nhiều lần, c&oacute; lần thực hiện ở ph&ograve;ng trực gi&aacute;o vi&ecirc;n của trường. Sau khi xong việc, thầy A. dặn H. kh&ocirc;ng được kể lại sự việc với ai.</p>\r\n\r\n<p>Được biết trong s&aacute;ng nay (23/4), cơ quan chuy&ecirc;n m&ocirc;n sẽ gi&aacute;m định ADN thai nhi của học sinh lớp 8 t&ecirc;n H.T.H. Trong trường hợp kết quả gi&aacute;m định ADN tr&ugrave;ng khớp, c&ocirc;ng an sẽ khởi tố thầy gi&aacute;o để điều tra, xử l&yacute;.</p>\r\n', 'Gia đình nữ sinh lớp 8 cho biết, sau khi sự việc được phát giác, người thân của thầy giáo đã đề nghị đưa số tiền 300 triệu và giải quyết vụ việc theo hướng tình cảm mà không đưa ra pháp luật.', NULL, NULL, 3);
INSERT INTO `baiviet` VALUES (85, '/public/images/5757530225732078260414521303352850113363968n-15559775220961908350881-1555977933675374681672.png', 'Đồng nghiệp ngã quỵ khi chứng kiến nữ công nhân môi trường bị ô tô điên đâm: \"Số chị ấy khổ lắm, hôm qua đi viện mua thuốc còn chưa kịp uống...\"', 3, 6, NULL, 'Pháp luật', '2019-06-01', '<blockquote>\r\n<p>http://kenh14.vn/dong-nghiep-chung-kien-nu-cong-nhan-moi-truong-bi-o-to-dien-dam-so-chi-ay-kho-lam-hom-qua-di-vien-mua-thuoc-con-chua-kip-uong-20190423081800499.chn</p>\r\n</blockquote>\r\n\r\n<p>23h đ&ecirc;m 22/4, một đ&ecirc;m kinh ho&agrave;ng ở th&agrave;nh phố H&agrave; Nội. Thời điểm ấy, chiếc<a href=\"http://kenh14.vn/nong-o-to-dam-lien-hoan-tren-duong-pho-ha-noi-it-nhat-1-cong-nhan-moi-truong-tu-vong-20190423001616531.chn\" target=\"_blank\">&nbsp;&ocirc; t&ocirc; m&agrave;u đen</a>&nbsp;do người đ&agrave;n &ocirc;ng điều khiển lao nhanh từ phố Quy Hồ (quận Đống Đa, H&agrave; Nội), đ&acirc;m tr&uacute;ng một số người d&acirc;n ở khu vực Ng&atilde; Tư Sở.</p>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/4/23/5757530225732078260414521303352850113363968n-15559775220961908350881-1555977933675374681672.png\" target=\"_blank\"><img alt=\"Đồng nghiệp ngã quỵ khi chứng kiến nữ công nhân môi trường bị ô tô điên đâm: Số chị ấy khổ lắm, hôm qua đi viện mua thuốc còn chưa kịp uống... - Ảnh 2.\" src=\"https://kenh14cdn.com/2019/4/23/5757530225732078260414521303352850113363968n-15559775220961908350881-1555977933675374681672.png\" /></a></p>\r\n\r\n<p>Hiện trường vụ việc.</p>\r\n\r\n<p>May mắn thay, một người phụ nữ b&aacute;n qu&aacute;n phở kịp n&eacute; tr&aacute;nh trước mũi tử thần, một người đ&agrave;n &ocirc;ng đi xe đạp điện bị t&ocirc;ng vẫn kh&ocirc;ng hết hoảng hồn.</p>\r\n\r\n<p>Chiếc xe &quot;đi&ecirc;n&quot; n&oacute;i tr&ecirc;n chưa dừng lại, lao nhanh về ph&iacute;a đường L&aacute;ng. Đ&uacute;ng thời điểm n&agrave;y chị L&ecirc; Thị Thu H&agrave; (SN 1977, tr&uacute; tại phố X&atilde; Đ&agrave;n, quận Đống Đa) đang l&agrave;m việc c&ugrave;ng một số đồng nghiệp trước khu vực 220 đường L&aacute;ng.</p>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/4/23/583748142239245179663836827210245943066624n-15559815802661996908660.jpg\" target=\"_blank\"><img alt=\"Đồng nghiệp ngã quỵ khi chứng kiến nữ công nhân môi trường bị ô tô điên đâm: Số chị ấy khổ lắm, hôm qua đi viện mua thuốc còn chưa kịp uống... - Ảnh 3.\" src=\"https://kenh14cdn.com/thumb_w/660/2019/4/23/583748142239245179663836827210245943066624n-15559815802661996908660.jpg\" /></a></p>\r\n\r\n<p>Thi thể chị H&agrave; nằm tại hiện trường.</p>\r\n\r\n<p>Chiếc xe ph&oacute;ng với tốc độ kinh ho&agrave;ng, t&ocirc;ng tr&uacute;ng chị H&agrave;, k&eacute;o l&ecirc; chị v&agrave; chiếc xe r&aacute;c gần 200 m&eacute;t.&nbsp;Khi thi thể chị nằm xuống đường, chiếc xe c&ograve;n đ&acirc;m th&ecirc;m 1 &ocirc; t&ocirc; với 1 chiếc xe m&aacute;y, cho đến khi bị người d&acirc;n truy đuổi bắt giữ.</p>\r\n\r\n<p>Chứng kiến vụ việc, ai nấy đều b&agrave;ng ho&agrave;ng, sợ h&atilde;i, nhiều người c&ograve;n b&agrave;y tỏ, nếu kh&ocirc;ng bị d&acirc;n truy đuổi kh&ocirc;ng biết chiếc xe c&ograve;n đ&acirc;m bao người.</p>\r\n\r\n<p>H&agrave;ng trăm người b&agrave;n t&aacute;n tại hiện trường, rằng t&agrave;i xế bị ng&aacute;o đ&aacute;, t&agrave;i xế say xỉn n&ecirc;n mới như vậy.</p>\r\n\r\n<p>Đ&ecirc;m qua l&agrave; một đ&ecirc;m &aacute;m ảnh của những&nbsp;<a href=\"http://kenh14.vn/nong-o-to-dam-lien-hoan-tren-duong-pho-ha-noi-it-nhat-1-cong-nhan-moi-truong-tu-vong-20190423001616531.chn\" target=\"_blank\">c&ocirc;ng nh&acirc;n m&ocirc;i trường</a>&nbsp;l&agrave;m việc tại khu Ng&atilde; Tư Sở. Thời điểm chị H&agrave; bị chiếc xe đ&acirc;m, c&oacute; một số đồng nghiệp l&agrave;m c&ugrave;ng, nhưng khi nh&igrave;n thấy như vậy cũng chỉ kịp h&eacute;t l&ecirc;n rồi chạy l&ecirc;n vỉa h&egrave;.</p>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/4/23/5844482020247042076588993340380096857899008n-15559816337781347101528-15559817525961542769611.png\"><img alt=\"Đồng nghiệp ng&amp;#227; quỵ khi chứng kiến nữ c&amp;#244;ng nh&amp;#226;n m&amp;#244;i trường bị &amp;#244; t&amp;#244; đi&amp;#234;n đ&amp;#226;m: &amp;quot;Số chị ấy khổ lắm, h&amp;#244;m qua đi viện mua thuốc c&amp;#242;n chưa kịp uống...&amp;quot; - Ảnh 4.\" src=\"https://kenh14cdn.com/thumb_w/660/2019/4/23/5844482020247042076588993340380096857899008n-15559816337781347101528-15559817525961542769611.png\" /></a></p>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/4/23/584434265373891500011791086319154689998848n-1555981633772558709697-1555981695918606981721.png\"><img alt=\"Đồng nghiệp ng&amp;#227; quỵ khi chứng kiến nữ c&amp;#244;ng nh&amp;#226;n m&amp;#244;i trường bị &amp;#244; t&amp;#244; đi&amp;#234;n đ&amp;#226;m: &amp;quot;Số chị ấy khổ lắm, h&amp;#244;m qua đi viện mua thuốc c&amp;#242;n chưa kịp uống...&amp;quot; - Ảnh 4.\" src=\"https://kenh14cdn.com/thumb_w/660/2019/4/23/584434265373891500011791086319154689998848n-1555981633772558709697-1555981695918606981721.png\" /></a></p>\r\n\r\n<p>Đồng nghiệp đau đớn cạnh thi thể chị H&agrave;, mọi người an ủi cậu con trai của chị.</p>\r\n\r\n<p>Chị H&agrave; kh&ocirc;ng may gặp nạn, thi thể kh&ocirc;ng c&ograve;n nguy&ecirc;n vẹn. Chỉ trong t&iacute;ch tắc, nh&igrave;n đồng nghiệp của m&igrave;nh trước đ&oacute; th&ocirc;i c&ograve;n n&oacute;i chuyện với nhau nay đ&atilde; ra đi vĩnh viễn, đồng nghiệp của chị kh&oacute;c ngất, ng&atilde; quỵ xuống.</p>\r\n\r\n<p>Mảnh vải được đắp l&ecirc;n người chị H&agrave;, một nấm hương tạm được đặt ra, người chứng kiến đều rơi nước mắt. Tiếng đồng nghiệp chị kh&oacute;c r&eacute; l&ecirc;n &quot;chị ơi sao ra đi như vậy, chị ơi em biết l&agrave;m sao đ&acirc;y...&quot; x&eacute; l&ograve;ng bao người chứng kiến.</p>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/4/23/583810785552498183325215077376467495550976n-15559817772631042692614-1555981816544770158034.png\" target=\"_blank\"><img alt=\"Đồng nghiệp ngã quỵ khi chứng kiến nữ công nhân môi trường bị ô tô điên đâm: Số chị ấy khổ lắm, hôm qua đi viện mua thuốc còn chưa kịp uống... - Ảnh 5.\" src=\"https://kenh14cdn.com/2019/4/23/583810785552498183325215077376467495550976n-15559817772631042692614-1555981816544770158034.png\" /></a></p>\r\n\r\n<p>Chiếc gi&agrave;y v&agrave; chiếc mũ của chị H&agrave; nằm lặng lẽ tại hiện trường.</p>\r\n\r\n<p>Gần 1 giờ đồng hồ sau, người con trai cả của chị được đưa đến hiện trường, cậu b&eacute; kh&oacute;c nức nở, ng&atilde; xuống ngay cạnh thi thể mẹ.</p>\r\n\r\n<p>Ngồi kh&oacute;c ngay cạnh thi thể chị H&agrave;, một nữ đồng nghiệp cho biết, chị đ&atilde; gắn b&oacute; c&ocirc;ng việc n&agrave;y gần 8 năm nay. B&igrave;nh thường chị l&agrave; một người chịu kh&oacute;, si&ecirc;ng năng v&agrave; rất h&ograve;a đồng vui vẻ với người kh&aacute;c.</p>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/4/23/584425396747490062747757180877962462363648n-15559777217951007695387-15559778128932117426797.png\" target=\"_blank\"><img alt=\"Đồng nghiệp ngã quỵ khi chứng kiến nữ công nhân môi trường bị ô tô điên đâm: Số chị ấy khổ lắm, hôm qua đi viện mua thuốc còn chưa kịp uống... - Ảnh 6.\" src=\"https://kenh14cdn.com/2019/4/23/584425396747490062747757180877962462363648n-15559777217951007695387-15559778128932117426797.png\" /></a></p>\r\n\r\n<p>Gia đ&igrave;nh kh&ocirc;ng hạnh ph&uacute;c, chị H&agrave; phải nu&ocirc;i đứa con trai đầu, người con trai &uacute;t sống c&ugrave;ng bố.</p>\r\n\r\n<p>Hằng ng&agrave;y chị phải l&agrave;m việc cật lực để c&oacute; tiền nu&ocirc;i con trai đầu ăn học, gửi th&ecirc;m tiền để nu&ocirc;i con trai thứ hai. Chị H&agrave; phải l&agrave;m việc cả ng&agrave;y, cả đ&ecirc;m kh&ocirc;ng nghỉ.</p>\r\n\r\n<p>&quot;Thương chị ấy lắm, chị ấy l&agrave; người tốt, đi l&agrave;m với ch&uacute;ng t&ocirc;i đều rất vui vẻ chịu kh&oacute;. Gia cảnh kh&oacute; khăn chị phải l&agrave;m cả ng&agrave;y cả đ&ecirc;m, mới c&oacute; ch&uacute;t tiền lo cho hai con&quot;, người phụ nữ chia sẻ.</p>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/4/23/5857756123490946886595002223254013632053248n-155598194276459783696-15559819856872011950487.png\" target=\"_blank\"><img alt=\"Đồng nghiệp ngã quỵ khi chứng kiến nữ công nhân môi trường bị ô tô điên đâm: Số chị ấy khổ lắm, hôm qua đi viện mua thuốc còn chưa kịp uống... - Ảnh 7.\" src=\"https://kenh14cdn.com/2019/4/23/5857756123490946886595002223254013632053248n-155598194276459783696-15559819856872011950487.png\" /></a></p>\r\n\r\n<p>Con mất mẹ thật rồi, mẹ ơi...</p>\r\n\r\n<p>Ngồi &ocirc;m người con trai chị H&agrave; v&agrave;o l&ograve;ng kh&oacute;c nức nở, một đồng nghiệp kh&aacute;c của chị H&agrave; cho biết, chị c&oacute; hai người con trai, con trai đầu nay đang học lớp 9, đứa con sau đang học lớp 6 ở c&ugrave;ng bố. Người con trai đầu ở c&ugrave;ng chị rất thương mẹ, năm nay ch&aacute;u b&eacute; vừa đăng k&yacute; trường học cấp 3.</p>\r\n\r\n<p>&quot;Nh&igrave;n chị ấy bị đ&acirc;m như vậy ch&uacute;ng t&ocirc;i đau đớn lắm, b&acirc;y giờ l&agrave;m sao ch&uacute;ng t&ocirc;i c&oacute; thể cầm c&acirc;y chổi để l&agrave;m việc nữa đ&acirc;y.</p>\r\n\r\n<p>H&ocirc;m qua chị ấy c&ograve;n t&acirc;m sự với t&ocirc;i, rằng con trai xin tiền nộp tiền học m&agrave; chị chưa c&oacute; tiền. Qua chị ấy cũng vừa đi viện mua đống thuốc về, c&ograve;n chưa kịp uống.&nbsp;H&ocirc;m qua chị ấy c&ograve;n rủ t&ocirc;i đi ch&ugrave;a Hương, t&ocirc;i c&ograve;n ngăn chị ấy lại.</p>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/4/23/580797736279708676653476896127420353478656n-15559818426461556036183-1555981880682737826575.png\" target=\"_blank\"><img alt=\"Đồng nghiệp ngã quỵ khi chứng kiến nữ công nhân môi trường bị ô tô điên đâm: Số chị ấy khổ lắm, hôm qua đi viện mua thuốc còn chưa kịp uống... - Ảnh 8.\" src=\"https://kenh14cdn.com/2019/4/23/580797736279708676653476896127420353478656n-15559818426461556036183-1555981880682737826575.png\" /></a></p>\r\n\r\n<p>Ch&uacute;ng t&ocirc;i buồn lắm, chị ấy sống khổ sở l&acirc;u nay rồi, giờ chết vẫn kh&ocirc;ng hết khổ. Đứa con trai đầu thương mẹ lắm, biết nh&agrave; kh&oacute; khăn n&ecirc;n học rất chăm chỉ, năm ngo&aacute;i c&ograve;n được nhận qu&agrave; của c&ocirc;ng ty nơi mẹ l&agrave;m.</p>\r\n\r\n<p>Nghe tin mẹ mất, chạy ra đ&acirc;y th&igrave; ng&atilde; xuống, ch&aacute;u n&oacute; vừa đăng k&yacute; trường cấp 3, chuẩn bị thi chuyển cấp m&agrave; giờ thế n&agrave;y. Kh&ocirc;ng biết tương lai ch&aacute;u rồi sẽ như thế n&agrave;o nữa&quot;, người phụ nữ n&oacute;i trong nước mắt.</p>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/4/23/photo2019-04-2300-26-41-2-1555977654086955047567-15559778935661442648838.png\" target=\"_blank\"><img alt=\"Đồng nghiệp ngã quỵ khi chứng kiến nữ công nhân môi trường bị ô tô điên đâm: Số chị ấy khổ lắm, hôm qua đi viện mua thuốc còn chưa kịp uống... - Ảnh 9.\" src=\"https://kenh14cdn.com/2019/4/23/photo2019-04-2300-26-41-2-1555977654086955047567-15559778935661442648838.png\" /></a></p>\r\n\r\n<p>T&agrave;i xế g&acirc;y tai nạn bị d&acirc;n v&acirc;y bắt.</p>\r\n\r\n<p>Đến 00h30 thi thể chị H&agrave; được đưa về nh&agrave; x&aacute;c bệnh viện Bạch Mai, nhiều đồng nghiệp đ&atilde; c&ugrave;ng theo chiếc xe cấp cứu đi c&ugrave;ng chị v&agrave; con trai.</p>\r\n\r\n<p>V&agrave;o đ&ecirc;m qua ngay sau khi l&aacute;i xe g&acirc;y&nbsp;<a href=\"http://kenh14.vn/vu-o-to-dien-dam-lien-hoan-mot-nu-cong-nhan-moi-truong-tu-vong-tai-xe-co-bieu-hien-say-xin-2019042307085739.chn\" target=\"_blank\">tai nạn</a>, nam t&agrave;i xế n&agrave;y đ&atilde; bị người d&acirc;n đuổi bắt rồi bị cảnh s&aacute;t &aacute;p giải về trụ sở C&ocirc;ng an quận Đống Đa để tạm giữ.</p>\r\n\r\n<p>Trong s&aacute;ng 23/4 c&ocirc;ng an quận Đống Đa đ&atilde; tiến h&agrave;nh lệnh giữ người trong trường hợp khẩn cấp đối với t&agrave;i xế n&oacute;i tr&ecirc;n.</p>\r\n\r\n<p>Đến thời điểm hiện tại, cảnh s&aacute;t cũng chưa l&agrave;m việc được với t&agrave;i xế n&agrave;y, v&igrave; c&oacute; những biểu hiện say xỉn.</p>\r\n', 'Chứng kiến cảnh chị Hà bị chiếc xe ô tô đâm trúng, kéo lê hàng trăm mét, rất đông đồng nghiệp của chị gục ngã, khóc ngất tại hiện trường. Theo lời chia sẻ của các đồng nghiệp, chị Hà là người chịu khó, hòa đồng và làm việc rất chăm chỉ.', NULL, NULL, NULL);
INSERT INTO `baiviet` VALUES (88, '/public/images/image-2019-06-17-074332-15607322479831300835354.jpg', 'Độc Quyền: Mỹ Tâm nựng má Mai Tài Phến trên máy bay và loạt bằng chứng hẹn hò chàng diễn viên kém 10 tuổi!', 4, 7, NULL, 'Mỹ tâm', '2019-06-18', '<p>Sau khi hợp t&aacute;c chung trong dự &aacute;n MV &quot;Đừng hỏi em&quot; v&agrave; mới đ&acirc;y nhất l&agrave; phim &quot;Chị trợ l&yacute; của anh&quot;,&nbsp;<a href=\"http://kenh14.vn/my-tam-hen-ho-mai-tai-phen.html\" target=\"_blank\">Mỹ T&acirc;m v&agrave; Mai T&agrave;i Phến</a>&nbsp;li&ecirc;n tục bị kh&aacute;n giả thắc mắc về mối quan hệ t&igrave;nh cảm hiện tại. Cả hai xuất hiện qu&aacute; t&igrave;nh cảm ở mọi nơi, mọi l&uacute;c, ăn mặc đồng điệu, kh&ocirc;ng ngại d&agrave;nh những lời lẽ c&oacute; c&aacute;nh,... l&agrave;m dấy l&ecirc;n tin đồn &quot;phim giả t&igrave;nh thật&quot;. D&acirc;n mạng thậm ch&iacute; c&ograve;n ph&aacute;t hiện ra cả hai c&ugrave;ng đi du lịch, đắp chung chăn tr&ecirc;n m&aacute;y bay,... những t&igrave;nh tiết n&agrave;y c&agrave;ng l&agrave;m lời đồn c&oacute; cơ sở khẳng định. N&oacute;i về vấn đề n&agrave;y Mỹ T&acirc;m khẳng định hiện tại c&ocirc; qu&yacute; Mai T&agrave;i Phến nhưng nếu l&agrave; duy&ecirc;n th&igrave; chẳng biết được!</p>\r\n\r\n<p>Thế nhưng việc hẹn h&ograve; của cặp đ&ocirc;i lại gần như được khẳng định chắc nịch sau nhiều ng&agrave;y ch&uacute;ng t&ocirc;i cất c&ocirc;ng theo dấu theo h&agrave;nh tr&igrave;nh của cặp đ&ocirc;i n&agrave;y.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>http://kenh14.vn/doc-quyen-my-tam-nung-ma-mai-tai-phen-tren-may-bay-va-loat-bang-chung-hen-ho-chang-dien-vien-kem-10-tuoi-20190616095138249.chn</p>\r\n\r\n<p>Auto</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>H&agrave;nh tr&igrave;nh theo dấu cặp đ&ocirc;i Mỹ T&acirc;m - Mai T&agrave;i Phến</p>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/6/17/map-artboard-1-copy-15607351847661290370282.jpg\" target=\"_blank\"><img alt=\"Độc Quyền: Mỹ Tâm nựng má Mai Tài Phến trên máy bay và loạt bằng chứng hẹn hò chàng diễn viên kém 10 tuổi! - Ảnh 2.\" src=\"https://kenh14cdn.com/2019/6/17/map-artboard-1-copy-15607351847661290370282.jpg\" /></a></p>\r\n\r\n<p>H&agrave;nh tr&igrave;nh theo dấu đi t&igrave;m bằng chứng hẹn h&ograve; của cặp đ&ocirc;i Mỹ T&acirc;m - Mai T&agrave;i Phến.</p>\r\n\r\n<p>Bắt đầu từ&nbsp;<strong><a href=\"http://kenh14.vn/doc-quyen-my-tam-nung-ma-mai-tai-phen-tren-may-bay-va-loat-bang-chung-hen-ho-chang-dien-vien-kem-10-tuoi-20190616095138249.chn\" target=\"_blank\">đ&ecirc;m 13/6, Mỹ T&acirc;m v&agrave; Mai T&agrave;i Phến c&ugrave;ng nhau khởi h&agrave;nh từ TP.HCM đến s&acirc;n bay C&aacute;t Bi&nbsp;</a>(Hải Ph&ograve;ng)</strong>. Cặp đ&ocirc;i qua đ&ecirc;m tại đ&acirc;y cho đến s&aacute;ng h&ocirc;m sau (14/6), Mỹ T&acirc;m g&oacute;p mặt trong một sự kiện ở th&agrave;nh phố hoa phượng đỏ. Mai T&agrave;i Phến kh&ocirc;ng xuất hiện trong sự kiện nhưng nam diễn vi&ecirc;n đợi Mỹ T&acirc;m sau đ&oacute; cả hai c&ugrave;ng nhau di chuyển bằng &ocirc; t&ocirc; đến thủ đ&ocirc; H&agrave; Nội để tiếp tục h&agrave;nh tr&igrave;nh đi Đ&agrave; Nẵng.</p>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/6/17/image-2019-06-17-074332-15607322479831300835354.jpg\" target=\"_blank\"><img alt=\"Độc Quyền: Mỹ Tâm nựng má Mai Tài Phến trên máy bay và loạt bằng chứng hẹn hò chàng diễn viên kém 10 tuổi! - Ảnh 3.\" src=\"https://kenh14cdn.com/2019/6/17/image-2019-06-17-074332-15607322479831300835354.jpg\" /></a></p>\r\n\r\n<p>Mỹ T&acirc;m xuất hiện tại sự kiện ở TP. Hải Ph&ograve;ng.</p>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/6/16/image-2019-06-16-092902-1560652245721127121045.jpg\" target=\"_blank\"><img alt=\"Độc Quyền: Mỹ Tâm nựng má Mai Tài Phến trên máy bay và loạt bằng chứng hẹn hò chàng diễn viên kém 10 tuổi! - Ảnh 4.\" src=\"https://kenh14cdn.com/2019/6/16/image-2019-06-16-092902-1560652245721127121045.jpg\" /></a></p>\r\n\r\n<p>Ngay sau khi kết th&uacute;c sự kiện, nữ ca sĩ di chuyển ngay ra xe đi H&agrave; Nội để bay đi Đ&agrave; Nẵng.</p>\r\n\r\n<p>Khoảng&nbsp;<strong>13h chiều ng&agrave;y 14/6, Mỹ T&acirc;m v&agrave; Mai T&agrave;i Phến đ&aacute;p xuống s&acirc;n bay TP. Đ&agrave; Nẵng</strong>. Ở đ&acirc;y, cả hai được đưa đ&oacute;n bằng xe ri&ecirc;ng. Mỹ T&acirc;m c&oacute; buổi biểu diễn trong lễ khai mạc Festival du lịch biển Tam Kỳ 2019 (Quảng Nam). L&agrave; một người con của mảnh đất miền Trung, &quot;hoạ mi t&oacute;c n&acirc;u&quot; được ch&agrave;o đ&oacute;n nồng nhiệt. Xe của nữ ca sĩ di chuyển kh&oacute; khăn giữa d&ograve;ng người v&acirc;y k&iacute;n. Kh&ocirc;ng thấy Mai T&agrave;i Phến xuất hiện trong đ&ecirc;m diễn n&agrave;y của Mỹ T&acirc;m. Kết th&uacute;c những tiết mục của m&igrave;nh, Mỹ T&acirc;m ngay lập tức l&ecirc;n xe về TP.Đ&agrave; Nẵng.</p>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/6/16/642272454101302829124278838020179418939392n-1560698082661892634771.jpg\" target=\"_blank\"><img alt=\"Độc Quyền: Mỹ Tâm nựng má Mai Tài Phến trên máy bay và loạt bằng chứng hẹn hò chàng diễn viên kém 10 tuổi! - Ảnh 5.\" src=\"https://kenh14cdn.com/2019/6/16/642272454101302829124278838020179418939392n-1560698082661892634771.jpg\" /></a></p>\r\n\r\n<p>Mỹ T&acirc;m chật vật giữa d&ograve;ng người tại Tam Kỳ.</p>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/6/16/anh-chup-man-hinh-2019-06-16-luc-215854-1560697161372203683644.png\" target=\"_blank\"><img alt=\"Độc Quyền: Mỹ Tâm nựng má Mai Tài Phến trên máy bay và loạt bằng chứng hẹn hò chàng diễn viên kém 10 tuổi! - Ảnh 6.\" src=\"https://kenh14cdn.com/2019/6/16/anh-chup-man-hinh-2019-06-16-luc-215854-1560697161372203683644.png\" /></a></p>\r\n\r\n<p>Mỹ T&acirc;m h&aacute;t khai mạc Festival du lịch biển Tam Kỳ 2019 (Quảng Nam).</p>\r\n\r\n<p><strong>14h30 ng&agrave;y 15/6, sau hơn 1 ng&agrave;y theo ch&acirc;n cặp đ&ocirc;i, ch&uacute;ng t&ocirc;i cũng bắt gặp Mỹ T&acirc;m v&agrave; Mai T&agrave;i Phến c&ugrave;ng nhau xuất hiện tại s&acirc;n bay TP. Đ&agrave; Nẵng để khởi h&agrave;nh đi H&agrave; Nội</strong>. Cả hai đeo khẩu trang k&iacute;n m&iacute;t để tr&aacute;nh sự ch&uacute; &yacute;, nhưng dễ d&agrave;ng nhận ra họ mang gi&agrave;y đ&ocirc;i, chỉ kh&aacute;c m&agrave;u. Cặp đ&ocirc;i l&agrave;m thủ tục checkin rất nhanh để di chuyển ngay v&agrave;o ph&ograve;ng chờ. Tại đ&acirc;y, Mỹ T&acirc;m v&agrave; Mai T&agrave;i Phến ngồi s&aacute;t b&ecirc;n nhau. Tr&ecirc;n m&aacute;y bay, Mỹ T&acirc;m c&oacute; khoảnh khắc nựng m&aacute; Mai T&agrave;i Phến. Trước khi đ&aacute;p xuống s&acirc;n bay, nam diễn vi&ecirc;n đặt tay t&igrave;nh tứ l&ecirc;n đ&ugrave;i của nữ ca sĩ &quot;Hoạ mi t&oacute;c n&acirc;u&quot;.</p>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/6/16/646570754615454280113043932526598007291904n-1560652819882330598886.jpg\" target=\"_blank\"><img alt=\"Độc Quyền: Mỹ Tâm nựng má Mai Tài Phến trên máy bay và loạt bằng chứng hẹn hò chàng diễn viên kém 10 tuổi! - Ảnh 7.\" src=\"https://kenh14cdn.com/2019/6/16/646570754615454280113043932526598007291904n-1560652819882330598886.jpg\" /></a></p>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/6/16/643286874354981939363398340567828721565696n-1560652819877686945701.jpg\" target=\"_blank\"><img alt=\"Độc Quyền: Mỹ Tâm nựng má Mai Tài Phến trên máy bay và loạt bằng chứng hẹn hò chàng diễn viên kém 10 tuổi! - Ảnh 8.\" src=\"https://kenh14cdn.com/2019/6/16/643286874354981939363398340567828721565696n-1560652819877686945701.jpg\" /></a></p>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/6/16/6427412328395622060780687429822246987759616n-1560652819873728190417-15606532104031309854450.png\" target=\"_blank\"><img alt=\"Độc Quyền: Mỹ Tâm nựng má Mai Tài Phến trên máy bay và loạt bằng chứng hẹn hò chàng diễn viên kém 10 tuổi! - Ảnh 9.\" src=\"https://kenh14cdn.com/2019/6/16/6427412328395622060780687429822246987759616n-1560652819873728190417-15606532104031309854450.png\" /></a></p>\r\n\r\n<p>Ch&uacute;ng t&ocirc;i bắt gặp Mỹ T&acirc;m - Mai T&agrave;i Phến xuất hiện c&ugrave;ng nhau ở s&acirc;n bay Đ&agrave; Nẵng. Cặp đ&ocirc;i mang gi&agrave;y đ&ocirc;i, bịt khẩu trang k&iacute;n m&iacute;t.</p>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/6/17/anh-chup-man-hinh-2019-06-16-luc-221540-15607317459851901783583.png\" target=\"_blank\"><img alt=\"Độc Quyền: Mỹ Tâm nựng má Mai Tài Phến trên máy bay và loạt bằng chứng hẹn hò chàng diễn viên kém 10 tuổi! - Ảnh 10.\" src=\"https://kenh14cdn.com/2019/6/17/anh-chup-man-hinh-2019-06-16-luc-221540-15607317459851901783583.png\" /></a></p>\r\n\r\n<p>L&ecirc;n m&aacute;y bay, cả hai th&aacute;o khẩu trang, ngồi cạnh nhau.</p>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/6/17/anh-chup-man-hinh-2019-06-16-luc-220010-15607317767351272845627.png\"><img alt=\"Độc Quyền: Mỹ Tâm nựng má Mai Tài Phến trên máy bay và loạt bằng chứng hẹn hò chàng diễn viên kém 10 tuổi! - Ảnh 11.\" src=\"https://kenh14cdn.com/2019/6/17/anh-chup-man-hinh-2019-06-16-luc-220010-15607317767351272845627.png\" /></a></p>\r\n\r\n<p>Mỹ T&acirc;m bất ngờ&quot;nựng m&aacute;&quot; Mai T&agrave;i Phến. Khoảnh khắc n&agrave;y chắc chắn sẽ khiến bao người h&acirc;m mộ nhảy dựng l&ecirc;n v&igrave; qu&aacute; ngọt ng&agrave;o.</p>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/6/17/anh-chup-man-hinh-2019-06-16-luc-221957-1560731810226556045989.png\"><img alt=\"Độc Quyền: Mỹ T&amp;#226;m nựng m&amp;#225; Mai T&amp;#224;i Phến tr&amp;#234;n m&amp;#225;y bay v&amp;#224; loạt bằng chứng hẹn h&amp;#242; ch&amp;#224;ng diễn vi&amp;#234;n k&amp;#233;m 10 tuổi! - Ảnh 12.\" src=\"https://kenh14cdn.com/thumb_w/660/2019/6/17/anh-chup-man-hinh-2019-06-16-luc-221957-1560731810226556045989.png\" /></a></p>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/6/17/anh-chup-man-hinh-2019-06-16-luc-222002-156073181023118846468.png\"><img alt=\"Độc Quyền: Mỹ T&amp;#226;m nựng m&amp;#225; Mai T&amp;#224;i Phến tr&amp;#234;n m&amp;#225;y bay v&amp;#224; loạt bằng chứng hẹn h&amp;#242; ch&amp;#224;ng diễn vi&amp;#234;n k&amp;#233;m 10 tuổi! - Ảnh 12.\" src=\"https://kenh14cdn.com/thumb_w/660/2019/6/17/anh-chup-man-hinh-2019-06-16-luc-222002-156073181023118846468.png\" /></a></p>\r\n\r\n<p>Mai T&agrave;i Phến th&aacute;o gối đưa cho Mỹ T&acirc;m.</p>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/6/16/anh-chup-man-hinh-2019-06-16-luc-221134-15606980298441490990021.png\" target=\"_blank\"><img alt=\"Độc Quyền: Mỹ Tâm nựng má Mai Tài Phến trên máy bay và loạt bằng chứng hẹn hò chàng diễn viên kém 10 tuổi! - Ảnh 13.\" src=\"https://kenh14cdn.com/2019/6/16/anh-chup-man-hinh-2019-06-16-luc-221134-15606980298441490990021.png\" /></a></p>\r\n\r\n<p>Tay Mai T&agrave;i Phến đặt l&ecirc;n đ&ugrave;i Mỹ T&acirc;m.</p>\r\n\r\n<p>Đến&nbsp;<strong>16h30, Mỹ T&acirc;m v&agrave; Mai T&agrave;i Phến đ&aacute;p xuống s&acirc;n bay Nội B&agrave;i (H&agrave; Nội)</strong>. Cả hai nhanh ch&oacute;ng di chuyển ra xe để chuẩn bị cho một buổi biểu diễn của &quot;hoạ mi t&oacute;c n&acirc;u&quot; tại trường Đại học Kinh tế Quốc d&acirc;n. Xe đưa Mỹ T&acirc;m đến buổi biểu diễn, Mai T&agrave;i Phến một lần nữa kh&ocirc;ng lộ diện. Theo phỏng đo&aacute;n, c&oacute; lẽ thời điểm n&agrave;y nam diễn vi&ecirc;n ở ngay trong xe để đợi Mỹ T&acirc;m.</p>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/6/17/30-15607105870431309919210.jpg\" target=\"_blank\"><img alt=\"Độc Quyền: Mỹ Tâm nựng má Mai Tài Phến trên máy bay và loạt bằng chứng hẹn hò chàng diễn viên kém 10 tuổi! - Ảnh 14.\" src=\"https://kenh14cdn.com/2019/6/17/30-15607105870431309919210.jpg\" /></a></p>\r\n\r\n<p>Cặp đ&ocirc;i s&aacute;t c&aacute;nh kh&ocirc;ng rời trong suốt h&agrave;nh tr&igrave;nh từ Nam ch&iacute; Bắc.</p>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/6/16/626446873545278752472276195981127572783104n-1560698286400101174667.jpg\" target=\"_blank\"><img alt=\"Độc Quyền: Mỹ Tâm nựng má Mai Tài Phến trên máy bay và loạt bằng chứng hẹn hò chàng diễn viên kém 10 tuổi! - Ảnh 15.\" src=\"https://kenh14cdn.com/2019/6/16/626446873545278752472276195981127572783104n-1560698286400101174667.jpg\" /></a></p>\r\n\r\n<p>Mỹ T&acirc;m một m&igrave;nh v&agrave;o h&aacute;t tại trường ĐH. Kinh Tế Quốc D&acirc;n. Mai T&agrave;i Phến đợi ở ngo&agrave;i xe.</p>\r\n\r\n<p>19h30 tại trường ĐH. Kinh Tế Quốc D&acirc;n, Mỹ T&acirc;m h&aacute;t mở m&agrave;n cho chương tr&igrave;nh. C&ocirc; biểu diễn một b&agrave;i h&aacute;t duy nhất trong sự ủng hộ nồng nhiệt của kh&aacute;n giả. Ngay sau đ&oacute; giọng ca &quot;Đừng hỏi em&quot; di chuyển ra xe. Đ&uacute;ng như dự đo&aacute;n, nam diễn vi&ecirc;n Mai T&agrave;i Phến đợi ở trong xe để c&ugrave;ng Mỹ t&acirc;m l&ecirc;n đường đến s&acirc;n bay trở về TP. HCM. Tại s&acirc;n bay, ch&uacute;ng t&ocirc;i ghi lại được khoảnh khắc đắt gi&aacute; Mỹ T&acirc;m lau mồ h&ocirc;i (hoặc phủi bụi) tr&ecirc;n gương mặt của Mai T&agrave;i Phến.&nbsp;<strong>21h c&ugrave;ng ng&agrave;y, Mỹ T&acirc;m v&agrave; Mai T&agrave;i phến l&ecirc;n m&aacute;y bay v&agrave; đ&aacute;p xuống s&acirc;n bay T&acirc;n Sơn Nhất l&uacute;c 23h</strong>. Sau đ&oacute;, nam diễn vi&ecirc;n c&ugrave;ng Mỹ T&acirc;m về nh&agrave; ri&ecirc;ng.</p>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/6/17/36-1560710514816202858677.jpg\" target=\"_blank\"><img alt=\"Độc Quyền: Mỹ Tâm nựng má Mai Tài Phến trên máy bay và loạt bằng chứng hẹn hò chàng diễn viên kém 10 tuổi! - Ảnh 16.\" src=\"https://kenh14cdn.com/2019/6/17/36-1560710514816202858677.jpg\" /></a></p>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/6/17/37-1560710514821727235812.jpg\" target=\"_blank\"><img alt=\"Độc Quyền: Mỹ Tâm nựng má Mai Tài Phến trên máy bay và loạt bằng chứng hẹn hò chàng diễn viên kém 10 tuổi! - Ảnh 17.\" src=\"https://kenh14cdn.com/2019/6/17/37-1560710514821727235812.jpg\" /></a></p>\r\n\r\n<p>Khoảnh khắc Mỹ T&acirc;m chăm s&oacute;c người t&igrave;nh k&eacute;m tuổi.</p>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/6/17/40-15607106685061124335044.jpg\" target=\"_blank\"><img alt=\"Độc Quyền: Mỹ Tâm nựng má Mai Tài Phến trên máy bay và loạt bằng chứng hẹn hò chàng diễn viên kém 10 tuổi! - Ảnh 18.\" src=\"https://kenh14cdn.com/2019/6/17/40-15607106685061124335044.jpg\" /></a></p>\r\n\r\n<p>Cặp đ&ocirc;i vẫn kịp trao nhau c&aacute;i nh&igrave;n tr&igrave;u mến trước khi l&ecirc;n đường về TP.HCM.</p>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2019/6/17/52-15607375582421927700557.jpg\" target=\"_blank\"><img alt=\"Độc Quyền: Mỹ Tâm nựng má Mai Tài Phến trên máy bay và loạt bằng chứng hẹn hò chàng diễn viên kém 10 tuổi! - Ảnh 19.\" src=\"https://kenh14cdn.com/2019/6/17/52-15607375582421927700557.jpg\" /></a></p>\r\n\r\n<p>Hơn 23h khuya, Mai T&agrave;i Phến th&aacute;p t&ugrave;ng Mỹ T&acirc;m về đến tận nh&agrave;.</p>\r\n\r\n<p>Tại sao Mai T&agrave;i Phến đồng h&agrave;nh c&ugrave;ng Mỹ T&acirc;m trong suốt h&agrave;nh tr&igrave;nh đi biểu diễn khắp mọi nơi từ Nam ch&iacute; Bắc d&ugrave; kh&ocirc;ng c&oacute; lịch tr&igrave;nh hay c&ocirc;ng việc g&igrave;? L&yacute; do g&igrave; sau khi hết qu&aacute; tr&igrave;nh hợp t&aacute;c, cặp đ&ocirc;i vẫn lu&ocirc;n kề kề cạnh nhau, lại c&ograve;n d&agrave;nh cho nhau những cử chỉ th&acirc;n mật? Tất cả những điều đ&oacute; chỉ c&oacute; thể được giải th&iacute;ch bằng một l&yacute; do đơn giản l&agrave; họ đang y&ecirc;u nhau m&agrave; th&ocirc;i. Ch&uacute;c mừng Mỹ T&acirc;m, như vậy l&agrave; sau rất nhiều năm hoạt động trong showbiz cực kỳ k&iacute;n tiếng, cuối c&ugrave;ng nữ ca sĩ cũng đang c&oacute; những ng&agrave;y th&aacute;ng hạnh ph&uacute;c b&ecirc;n một nửa của m&igrave;nh rồi!</p>\r\n\r\n<p>Giờ th&igrave; chỉ cần đợi một ng&agrave;y cặp đ&ocirc;i sẽ c&ugrave;ng dắt tay nhau c&ocirc;ng khai chuyện hẹn h&ograve; c&ugrave;ng với fan nữa m&agrave; th&ocirc;i.</p>\r\n', 'Mỹ Tâm vốn kín tiếng và quyền lực bậc nhất showbiz nhưng ở bên Mai Tài Phến, cô đã để lộ những hành động yêu thương. Phải cất công theo dấu lịch trình của cặp đôi qua nhiều thành phố, chúng tôi mới ghi lại được những khoảnh khắc hiếm hoi này.', NULL, NULL, 6);

